import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';
const getHeaders = () => ({ Authorization: `Bearer ${localStorage.getItem('token')}` });

export const fetchCustomers = async () => {
  const response = await axios.get(`${API_BASE_URL}/customers`, { headers: getHeaders() });
  return response.data;
};

export const fetchRoutes = async () => {
  const response = await axios.get(`${API_BASE_URL}/routes`, { headers: getHeaders() });
  return response.data;
};

export const fetchJobs = async () => {
  const response = await axios.get(`${API_BASE_URL}/jobs`, { headers: getHeaders() });
  return response.data;
};

export const saveRoute = async (route) => {
  const response = await axios.post(`${API_BASE_URL}/routes`, route, { headers: getHeaders() });
  return response.data;
};

export const deleteRoute = async (routeId) => {
  try {
    await axios.delete(`${API_BASE_URL}/routes/${routeId}`, { headers: getHeaders() });
  } catch (error) {
    console.error('Error deleting route:', error);
    throw error;
  }
};

export const fetchCalendarEvents = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/calendar-events`, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error fetching calendar events:', error);
    throw error;
  }
};

export const createCalendarEvent = async (eventData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/calendar-events`, eventData, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error creating calendar event:', error);
    throw error;
  }
};

export const updateCalendarEvent = async (eventId, eventData) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/calendar-events/${eventId}`, eventData, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error updating calendar event:', error);
    throw error;
  }
};


export const saveSchedule = async (routeId, schedule) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/routes/${routeId}/schedule`, schedule, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error saving schedule:', error);
    throw error;
  }
};

export const updateRoute = async (routeId, updatedData) => {
  try {
    console.log('Updating route:', routeId, 'with data:', updatedData);
    console.log('Full URL:', `${API_BASE_URL}/routes/${routeId}`);
    console.log('Headers:', getHeaders());
    const response = await axios.put(`${API_BASE_URL}/routes/${routeId}`, updatedData, { headers: getHeaders() });
    console.log('Route update response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error updating route:', error.response?.data || error.message);
    console.error('Full error object:', error);
    throw error;
  }
};

export const updateJob = async (jobId, updatedData) => {
  try {
    console.log('Updating job:', jobId, 'with data:', updatedData);
    const response = await axios.put(`${API_BASE_URL}/jobs/${jobId}`, updatedData, { headers: getHeaders() });
    console.log('Job update response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error updating job:', error.response?.data || error.message);
    throw error;
  }
};

export const scheduleRoute = async (routeId, scheduleData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/routes/${routeId}/schedule`, scheduleData, { headers: getHeaders() });
    console.log('Route schedule response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error scheduling route:', error.response?.data || error.message);
    throw error;
  }
};

export const calculateRouteTotal = async (routeId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/routes/${routeId}/total`, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error calculating route total:', error);
    throw error;
  }
};
