// src/pages/Profile.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header'; // Import Header for authenticated pages
import ProfileTab from '../components/ProfileTabs/ProfileTab';
import SecurityTab from '../components/ProfileTabs/SecurityTab';
import BadgesTab from '../components/ProfileTabs/BadgesTab';
import ActivityTab from '../components/ProfileTabs/ActivityTab';
import SettingsTab from '../components/ProfileTabs/SettingsTab';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const [profileData, setProfileData] = useState(null);
  const { user, loading } = useAuth();
  const navigate = useNavigate();

  // Function to fetch profile data
  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/profile`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setProfileData(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
      // Handle unauthorized access
      if (error.response && error.response.status === 401) {
        // Redirect to login or show a message
        navigate('/signin');
      }
    }
  };

  useEffect(() => {
    if (!loading) {
      if (!user) {
        navigate('/signin');
      } else {
        fetchProfile();
      }
    }
  }, [loading, user, navigate]);

  const renderTabContent = () => {
    switch (activeTab) {
      case 'profile':
        return <ProfileTab profileData={profileData} refreshProfile={fetchProfile} />;
      case 'security':
        return <SecurityTab />;
      case 'badges':
        return <BadgesTab badges={profileData?.badges} />;
      case 'activity':
        return <ActivityTab />;
      case 'settings':
        return <SettingsTab />;
      default:
        return <ProfileTab profileData={profileData} refreshProfile={fetchProfile} />;
    }
  };

  return (
    <div className="bg-background text-text min-h-screen">
      <Header /> {/* Use Header since this is inside the app */}
      <main className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <h1 className="text-4xl font-bold text-center mb-8">Your Profile</h1>
        <div className="bg-surface shadow rounded-lg">
          <div className="border-b border-gray-700">
            <nav className="-mb-px flex">
              {['profile', 'security', 'badges', 'activity', 'settings'].map((tab) => (
                <button
                  key={tab}
                  onClick={() => setActiveTab(tab)}
                  className={`w-1/5 py-4 px-1 text-center border-b-2 font-medium text-sm ${
                    activeTab === tab
                      ? 'border-primary text-primary'
                      : 'border-transparent text-gray-400 hover:text-text hover:border-gray-300'
                  }`}
                >
                  {tab.charAt(0).toUpperCase() + tab.slice(1)}
                </button>
              ))}
            </nav>
          </div>
          <div className="p-6">{renderTabContent()}</div>
        </div>
      </main>
    </div>
  );
};

export default Profile;
