import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useNavigate } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import CustomerMap from '../../components/CustomerMap';
import { fetchJobs, fetchRoutes, saveRoute, deleteRoute, optimizeRoute, updateCalendarEvent, updateJob } from './api';

const BuildRoutes = () => {
  const [jobs, setJobs] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [events, setEvents] = useState([]);
  const [activeTab, setActiveTab] = useState('routes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [jobsData, routesData] = await Promise.all([fetchJobs(), fetchRoutes()]);
      console.log('Fetched jobs:', jobsData);
      console.log('Fetched routes:', routesData);
      setJobs(jobsData || []);
      setRoutes(routesData || []);
      generateEvents(routesData || []);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const generateEvents = (routesData) => {
    if (!Array.isArray(routesData)) {
      console.error('routesData is not an array:', routesData);
      return;
    }
    
    const generatedEvents = routesData.flatMap(route => {
      if (!route || !Array.isArray(route.customers)) {
        console.error('Invalid route or customers array:', route);
        return [];
      }
      
      return route.customers.map(customer => {
        if (!customer) {
          console.error('Invalid customer:', customer);
          return null;
        }
        
        return {
          id: customer._id,
          title: `${route.name}: ${customer.name}`,
          start: customer.scheduledDate || new Date(),
          end: customer.scheduledEndDate || new Date(new Date().getTime() + 60*60*1000),
          color: '#4CAF50',
          extendedProps: { type: 'customer', routeId: route._id, customerId: customer._id }
        };
      }).filter(Boolean);  // Remove any null events
    });
    
    setEvents(generatedEvents);
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if (!destination) return;

    if (source.droppableId === 'jobPool' && destination.droppableId.startsWith('route-')) {
      const jobId = result.draggableId;
      const routeId = destination.droppableId.split('-')[1];
      moveJobToRoute(jobId, routeId);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'jobPool') {
      const jobId = result.draggableId;
      const routeId = source.droppableId.split('-')[1];
      moveJobToPool(jobId, routeId);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
      const jobId = result.draggableId;
      const sourceRouteId = source.droppableId.split('-')[1];
      const destRouteId = destination.droppableId.split('-')[1];
      moveJobBetweenRoutes(jobId, sourceRouteId, destRouteId);
    }
  };

  const moveJobToRoute = (jobId, routeId) => {
    const job = jobs.find(j => j._id === jobId);
    setJobs(prevJobs => prevJobs.filter(j => j._id !== jobId));
    setRoutes(prevRoutes => prevRoutes.map(route => 
      route._id === routeId 
        ? { ...route, customers: [...route.customers, job] }
        : route
    ));
    updateJobRoute(jobId, routeId);
  };

  const moveJobToPool = (jobId, routeId) => {
    const route = routes.find(r => r._id === routeId);
    const job = route.customers.find(j => j._id === jobId);
    setRoutes(prevRoutes => prevRoutes.map(r => 
      r._id === routeId 
        ? { ...r, customers: r.customers.filter(j => j._id !== jobId) }
        : r
    ));
    setJobs(prevJobs => [...prevJobs, job]);
    updateJobRoute(jobId, null);
  };

  const moveJobBetweenRoutes = (jobId, sourceRouteId, destRouteId) => {
    const sourceRoute = routes.find(r => r._id === sourceRouteId);
    const job = sourceRoute.customers.find(j => j._id === jobId);
    setRoutes(prevRoutes => prevRoutes.map(route => {
      if (route._id === sourceRouteId) {
        return { ...route, customers: route.customers.filter(j => j._id !== jobId) };
      }
      if (route._id === destRouteId) {
        return { ...route, customers: [...route.customers, job] };
      }
      return route;
    }));
    updateJobRoute(jobId, destRouteId);
  };

  const updateJobRoute = async (jobId, routeId) => {
    try {
      await updateJob(jobId, { route: routeId });
      setSuccessMessage('Job updated successfully!');
    } catch (error) {
      console.error('Error updating job route:', error);
      setError('Failed to update job route. Please try again.');
    }
  };

  const handleOptimizeRoute = async (routeId) => {
    try {
      const optimizedRoute = await optimizeRoute(routeId);
      setRoutes(routes.map(route => route._id === routeId ? optimizedRoute : route));
      setSuccessMessage('Route optimized successfully!');
    } catch (error) {
      console.error('Error optimizing route:', error);
      setError('Failed to optimize route. Please try again.');
    }
  };

  const handleEventDrop = async (info) => {
    const { event } = info;
    try {
      const updatedEvent = await updateCalendarEvent({
        id: event.id,
        start: event.start,
        end: event.end,
      });
      setEvents(prevEvents => 
        prevEvents.map(e => e.id === updatedEvent.id ? updatedEvent : e)
      );
      setSuccessMessage('Event updated successfully!');
    } catch (error) {
      console.error('Error updating event:', error);
      setError('Failed to update event. Please try again.');
      info.revert();
    }
  };

  const handleAddRoute = async () => {
    try {
      const newRoute = await saveRoute({ name: `New Route ${routes.length + 1}`, customers: [] });
      setRoutes([...routes, newRoute]);
      setSuccessMessage('New route added successfully!');
    } catch (error) {
      console.error('Error adding new route:', error);
      setError('Failed to add new route. Please try again.');
    }
  };

  const handleDeleteRoute = async (routeId) => {
    try {
      await deleteRoute(routeId);
      setRoutes(routes.filter(route => route._id !== routeId));
      setSuccessMessage('Route deleted successfully!');
    } catch (error) {
      console.error('Error deleting route:', error);
      setError('Failed to delete route. Please try again.');
    }
  };

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <div className="build-routes min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-6">Route Management</h1>
          
          {successMessage && (
            <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4" role="alert">
              <p>{successMessage}</p>
            </div>
          )}

          <div className="mb-6">
            <div className="sm:hidden">
              <select
                onChange={(e) => setActiveTab(e.target.value)}
                className="block w-full bg-background text-text border-gray-300 focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 rounded-md shadow-sm"
              >
                <option value="routes">Routes</option>
                <option value="jobs">Jobs</option>
                <option value="calendar">Calendar</option>
                <option value="map">Map</option>
              </select>
            </div>
            <div className="hidden sm:block">
              <nav className="flex space-x-4" aria-label="Tabs">
                {['routes', 'jobs', 'calendar', 'map'].map((tab) => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`${
                      activeTab === tab
                        ? 'bg-primary text-white'
                        : 'text-text hover:text-white hover:bg-primary'
                    } px-3 py-2 font-medium text-sm rounded-md`}
                  >
                    {tab.charAt(0).toUpperCase() + tab.slice(1)}
                  </button>
                ))}
              </nav>
            </div>
          </div>

          <div className="bg-surface p-6 rounded-lg shadow-lg">
            {activeTab === 'routes' && (
              <DragDropContext onDragEnd={onDragEnd}>
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-2xl font-semibold">Routes</h2>
                  <button
                    onClick={handleAddRoute}
                    className="bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90"
                  >
                    Add New Route
                  </button>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {routes.map(route => (
                    <Droppable key={route._id} droppableId={`route-${route._id}`}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.droppableProps}
                          className="bg-gray-100 p-4 rounded-lg text-black"
                        >
                          <div className="flex justify-between items-center mb-2">
                            <h3 className="font-semibold">{route.name}</h3>
                            <div>
                              <button
                                onClick={() => handleOptimizeRoute(route._id)}
                                className="text-primary hover:text-primary-dark mr-2"
                              >
                                Optimize
                              </button>
                              <button
                                onClick={() => handleDeleteRoute(route._id)}
                                className="text-red-600 hover:text-red-800"
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                          {route.customers && route.customers.map((customer, index) => (
                            <Draggable key={customer._id} draggableId={customer._id} index={index}>
                              {(provided) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                  className="bg-white p-2 mb-2 rounded shadow"
                                >
                                  {customer.name}
                                </div>
                              )}
                            </Draggable>
                          ))}
                          {provided.placeholder}
                        </div>
                      )}
                    </Droppable>
                  ))}
                </div>
                <Droppable droppableId="jobPool">
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className="mt-6 bg-gray-200 p-4 rounded-lg text-black"
                    >
                      <h3 className="font-semibold mb-2">Unassigned Jobs</h3>
                      {jobs.map((job, index) => (
                        <Draggable key={job._id} draggableId={job._id} index={index}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className="bg-white p-2 mb-2 rounded shadow"
                            >
                              {job.title}
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </DragDropContext>
            )}

            {activeTab === 'jobs' && (
              <div className="jobs-container">
                <h2 className="text-2xl font-semibold mb-4">Jobs</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {jobs.map(job => (
                    <div key={job._id} className="bg-gray-100 p-4 rounded-lg text-black">
                      <h3 className="font-semibold">{job.title}</h3>
                      <p>{job.description}</p>
                      <p>Status: {job.status}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'calendar' && (
              <div className="calendar-container">
                <h2 className="text-2xl font-semibold mb-4">Schedule</h2>
                <FullCalendar
                  plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                  initialView="dayGridMonth"
                  headerToolbar={{
                    left: 'prev,next today',
                    center: 'title',
                    right: 'dayGridMonth,timeGridWeek,timeGridDay'
                  }}
                  editable={true}
                  droppable={true}
                  events={events}
                  eventDrop={handleEventDrop}
                />
              </div>
            )}

            {activeTab === 'map' && (
              <div className="map-container">
                <h2 className="text-2xl font-semibold mb-4">Route Map</h2>
                <CustomerMap 
                  routes={routes}
                  onOptimizeRoute={handleOptimizeRoute}
                />
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default BuildRoutes;
