// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import { PlusIcon, CalendarIcon, MapIcon, DocumentIcon } from '@heroicons/react/24/outline';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [recentJob, setRecentJob] = useState(null);
  const [recentCustomer, setRecentCustomer] = useState(null);

  useEffect(() => {
    fetchCounts();
    fetchRecentActivity();
  }, []);

  const fetchCounts = async () => {
    try {
      const jobsResponse = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobCount(jobsResponse.data.length);

      const customersResponse = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomerCount(customersResponse.data.length);
    } catch (error) {
      console.error('Error fetching counts:', error);
    }
  };

  const fetchRecentActivity = async () => {
    try {
      const recentJobsResponse = await axios.get('http://localhost:5000/api/jobs/recent', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRecentJob(recentJobsResponse.data[0]);

      const recentCustomersResponse = await axios.get('http://localhost:5000/api/customers/recent', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRecentCustomer(recentCustomersResponse.data[0]);
    } catch (error) {
      console.error('Error fetching recent activity:', error);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <nav className="bg-surface">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <span className="font-bold text-xl">AUTOLAWN Dashboard</span>
            </div>
            <div className="flex items-center">
              <button
                onClick={handleLogout}
                className="bg-rose-700 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              >
                Logout
              </button>
              <Link to="/profile" className="ml-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90">
                Profile
              </Link>
            </div>
          </div>
        </div>
      </nav>
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-semibold">Welcome, {user?.name}</h1>
          
          <div className="mt-6 grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3">
            {/* Jobs Overview */}
            <div className="bg-surface overflow-hidden shadow rounded-lg h-41 w-full">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium">Jobs</h3>
                <div className="mt-2 text-3xl font-semibold">{jobCount}</div>
                <div className="mt-4">
                  <Link to="/manage-jobs" className="text-primary hover:text-primary-dark">
                    View all jobs
                  </Link>
                </div>
              </div>
            </div>

            {/* Customers Overview */}
            <div className="bg-surface overflow-hidden shadow rounded-lg h-41 w-full">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium">Customers</h3>
                <div className="mt-2 text-3xl font-semibold">{customerCount}</div>
                <div className="mt-4">
                  <Link to="/manage-customers" className="text-primary hover:text-primary-dark">
                    View all customers
                  </Link>
                </div>
              </div>
            </div>

            {/* Recent Activity */}
            <div className="bg-surface overflow-hidden shadow rounded-lg h-41 w-full">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium">Recent Activity</h3>
                <ul className="mt-2 space-y-2">
                  {recentJob && (
                    <>
                      <h4 className="font-semibold">Recent Job</h4>
                      <li key={recentJob._id} className="text-xs">
                        {recentJob.title} - {recentJob.customer.name}
                      </li>
                    </>
                  )}
                  {recentCustomer && (
                    <>
                      <h4 className="font-semibold">Recent Customer</h4>
                      <li key={recentCustomer._id} className="text-xs">
                        {recentCustomer.name}
                      </li>
                    </>
                  )}
                  {!recentJob && !recentCustomer && (
                    <li>No recent activity</li>
                  )}
                </ul>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-4">Quick Actions</h2>
            <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
              <Link to="/jobs" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <PlusIcon className="h-5 w-5 mr-2" />
                Create New Job
              </Link>
              <Link to="/customers" className="bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <PlusIcon className="h-5 w-5 mr-2" />
                Add New Customer
              </Link>
              <Link to="/schedule" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <CalendarIcon className="h-5 w-5 mr-2" />
                Scheduling 
              </Link>
              <Link to="/generate-report" className="bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <DocumentIcon className="h-5 w-5 mr-2" />
                Generate Report
              </Link>
              <Link to="/build-routes" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <MapIcon className="h-5 w-5 mr-2" />
                Build Routes
              </Link>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
