// src/pages/Jobs.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { PlusIcon, CalendarIcon, MapIcon, UserGroupIcon, PencilIcon, TrashIcon } from '@heroicons/react/24/outline';

const Jobs = () => {
  const [job, setJob] = useState({
    title: '',
    description: '',
    scheduledDate: '',
    customer: '',
    price: '',
    status: 'Scheduled',
    recurrence: 'One-time'
  });
  const [jobs, setJobs] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [editingJob, setEditingJob] = useState(null);

  useEffect(() => {
    fetchCustomers();
    fetchJobs();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to fetch jobs. Please try again.');
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (editingJob) {
      setEditingJob({ ...editingJob, [name]: value });
    } else {
      setJob({ ...job, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    const jobData = editingJob || job;

    try {
      if (editingJob) {
        await axios.put(`http://localhost:5000/api/jobs/${editingJob._id}`, jobData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setSuccess('Job updated successfully!');
        setEditingJob(null);
      } else {
        await axios.post('http://localhost:5000/api/jobs', jobData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setSuccess('Job created successfully!');
      }
      setJob({
        title: '',
        description: '',
        scheduledDate: '',
        customer: '',
        price: '',
        status: 'Scheduled',
        recurrence: 'One-time'
      });
      fetchJobs();
      setShowForm(false);
    } catch (error) {
      console.error('Error saving job:', error.response?.data || error.message);
      setError(error.response?.data?.message || 'Failed to save job. Please try again.');
    }
    setLoading(false);
  };

  const handleEdit = (job) => {
    setEditingJob(job);
    setShowForm(true);
  };

  const handleDelete = async (jobId) => {
    if (window.confirm('Are you sure you want to delete this job?')) {
      try {
        await axios.delete(`http://localhost:5000/api/jobs/${jobId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setSuccess('Job deleted successfully!');
        fetchJobs();
      } catch (error) {
        console.error('Error deleting job:', error);
        setError('Failed to delete job. Please try again.');
      }
    }
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-semibold">Jobs</h1>
            <div className="space-x-2">
              <button
                onClick={() => {setShowForm(!showForm); setEditingJob(null);}}
                className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              >
                <PlusIcon className="h-5 w-5 inline-block mr-1" />
                {showForm ? 'Hide Form' : 'New Job'}
              </button>
              <Link
                to="/job-calendar"
                className="bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              >
                <CalendarIcon className="h-5 w-5 inline-block mr-1" />
                Calendar View
              </Link>
              <Link
                to="/job-map"
                className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              >
                <MapIcon className="h-5 w-5 inline-block mr-1" />
                Map View
              </Link>
              <Link
                to="/team-assignment"
                className="bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              >
                <UserGroupIcon className="h-5 w-5 inline-block mr-1" />
                Team Assignment
              </Link>
            </div>
          </div>
          
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>}
          
          {/* Create/Edit Job Form */}
          {showForm && (
            <div className="bg-surface overflow-hidden shadow rounded-lg p-6 mb-6">
              <h2 className="text-xl font-semibold mb-4">{editingJob ? 'Edit Job' : 'Create New Job'}</h2>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label htmlFor="title" className="block text-sm font-medium">Job Title</label>
                    <input
                      type="text"
                      id="title"
                      name="title"
                      value={editingJob ? editingJob.title : job.title}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      required
                    />
                  </div>
                  <div>
                    <label htmlFor="customer" className="block text-sm font-medium">Customer</label>
                    <select
                      id="customer"
                      name="customer"
                      value={editingJob ? editingJob.customer : job.customer}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      required
                    >
                      <option value="">Select a customer</option>
                      {customers.map((customer) => (
                        <option key={customer._id} value={customer._id}>
                          {customer.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label htmlFor="scheduledDate" className="block text-sm font-medium">Scheduled Date</label>
                    <input
                      type="date"
                      id="scheduledDate"
                      name="scheduledDate"
                      value={editingJob ? editingJob.scheduledDate.split('T')[0] : job.scheduledDate}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      required
                    />
                  </div>
                  <div>
                    <label htmlFor="price" className="block text-sm font-medium">Price</label>
                    <input
                      type="number"
                      id="price"
                      name="price"
                      value={editingJob ? editingJob.price : job.price}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      required
                    />
                  </div>
                  <div>
                    <label htmlFor="status" className="block text-sm font-medium">Status</label>
                    <select
                      id="status"
                      name="status"
                      value={editingJob ? editingJob.status : job.status}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      required
                    >
                      <option value="Scheduled">Scheduled</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Cancelled">Cancelled</option>
                    </select>
                  </div>
                  <div>
                    <label htmlFor="recurrence" className="block text-sm font-medium">Recurrence</label>
                    <select
                      id="recurrence"
                      name="recurrence"
                      value={editingJob ? editingJob.recurrence : job.recurrence}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      required
                    >
                      <option value="One-time">One-time</option>
                      <option value="Weekly">Weekly</option>
                      <option value="Bi-weekly">Bi-weekly</option>
                      <option value="Monthly">Monthly</option>
                    </select>
                  </div>
                </div>
                <div>
                  <label htmlFor="description" className="block text-sm font-medium">Description</label>
                  <textarea
                    id="description"
                    name="description"
                    value={editingJob ? editingJob.description : job.description}
                    onChange={handleChange}
                    rows="4"
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                  ></textarea>
                </div>
                <button
                  type="submit"
                  className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : (editingJob ? 'Update Job' : 'Create Job')}
                </button>
              </form>
            </div>
          )}

          {/* Job List */}
          <div className="bg-surface overflow-hidden shadow rounded-lg">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Job</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Recurrence</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {jobs.map((job) => (
                  <tr key={job._id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm font-medium text-gray-900">{job.title}</div>
                      <div className="text-sm text-gray-500">{job.description}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{job.customer.name}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{new Date(job.scheduledDate).toLocaleDateString()}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">${job.price}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                        ${job.status === 'Completed' ? 'bg-green-100 text-green-800' : 
                          job.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' : 
                          job.status === 'Cancelled' ? 'bg-red-100 text-red-800' : 
                          'bg-blue-100 text-blue-800'}`}>
                        {job.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{job.recurrence}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <button onClick={() => handleEdit(job)} className="text-indigo-600 hover:text-indigo-900 mr-2">
                        <PencilIcon className="h-5 w-5 inline-block" />
                      </button>
                      <button onClick={() => handleDelete(job._id)} className="text-red-600 hover:text-red-900">
                        <TrashIcon className="h-5 w-5 inline-block" />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="mt-4">
            <Link to="/dashboard" className="text-primary hover:text-primary-dark">
              Back to Dashboard
            </Link>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Jobs;
