// src/components/ProfileTabs/CurrentSubscription.js
import React, { useEffect, useState } from 'react';
import axiosInstance from '../../utils/axios';

const CurrentSubscription = () => {
  const [subscription, setSubscription] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSubscription = async () => {
      try {
        const response = await axiosInstance.get('/api/payment/current-subscription');
        setSubscription(response.data);
      } catch (err) {
        console.error('Error fetching subscription:', err);
        setError(err.response?.data?.error || 'Failed to load subscription details.');
      } finally {
        setLoading(false);
      }
    };
    fetchSubscription();
  }, []);

  if (loading) {
    return <p className="text-gray-500">Loading subscription details...</p>;
  }

  if (error) {
    return <p className="text-red-500">{error}</p>;
  }

  if (!subscription) {
    return <p className="text-gray-500">You do not have an active subscription.</p>;
  }

  return (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold">Current Subscription</h3>
      <p>
        <strong>Plan:</strong> {subscription.items.data[0].price.nickname}
      </p>
      <p>
        <strong>Status:</strong> {subscription.status}
      </p>
      <p>
        <strong>Next Billing Date:</strong>{' '}
        {new Date(subscription.current_period_end * 1000).toLocaleDateString()}
      </p>
      {/* Add more details as needed */}
    </div>
  );
};

export default CurrentSubscription;
