import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Header from '../components/Header';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/24/solid';

const API_BASE_URL = 'http://localhost:5000/api';
const DAYS_OF_WEEK = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

const BuildRoutes = () => {
  const [routes, setRoutes] = useState({});
  const [jobPool, setJobPool] = useState([]);
  const [selectedDay, setSelectedDay] = useState('Monday');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchRouteAndJobs();
  }, [selectedDay]);

  const fetchRouteAndJobs = async () => {
    setLoading(true);
    setError(null);
    try {
      const [routeResponse, jobsResponse] = await Promise.all([
        axios.get(`${API_BASE_URL}/routes/${selectedDay}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get(`${API_BASE_URL}/jobs/available`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      setRoutes(prevRoutes => ({
        ...prevRoutes,
        [selectedDay]: routeResponse.data.jobs || { route1: [], route2: [], route3: [] }
      }));
      setJobPool(jobsResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load routes and jobs. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceList = source.droppableId;
    const destList = destination.droppableId;

    let updatedRoutes = { ...routes };
    let updatedJobPool = [...jobPool];

    if (sourceList === 'jobPool' && destList.startsWith('route')) {
      const [movedJob] = updatedJobPool.splice(source.index, 1);
      updatedRoutes[selectedDay][destList].push(movedJob);
    } else if (sourceList.startsWith('route') && destList === 'jobPool') {
      const [movedJob] = updatedRoutes[selectedDay][sourceList].splice(source.index, 1);
      updatedJobPool.push(movedJob);
    } else if (sourceList.startsWith('route') && destList.startsWith('route')) {
      const [movedJob] = updatedRoutes[selectedDay][sourceList].splice(source.index, 1);
      updatedRoutes[selectedDay][destList].splice(destination.index, 0, movedJob);
    }

    setRoutes(updatedRoutes);
    setJobPool(updatedJobPool);

    if (destList.startsWith('route')) {
      try {
        await axios.put(`${API_BASE_URL}/routes/${selectedDay}`, { jobs: updatedRoutes[selectedDay] }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      } catch (error) {
        console.error('Error updating route:', error);
        setError('Failed to update route. Please try again.');
        fetchRouteAndJobs();
      }
    }
  };

  const renderJobList = (listId, jobs, routeName) => (
    <Droppable droppableId={listId}>
      {(provided) => (
        <div
          {...provided.droppableProps}
          ref={provided.innerRef}
          className={`bg-gray-800 p-2 rounded ${routeName ? 'min-h-[600px]' : 'min-h-[200px]'}`}
        >
          <h2 className="text-xl font-bold mb-2 text-white">
            {routeName ? routeName : 'Job Pool'}
          </h2>
          {jobs.map((job, index) => (
            <Draggable key={job._id} draggableId={job._id} index={index}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  className={`bg-green-700 p-2 mb-2 rounded text-white ${job.recurrence === 'One-time' ? 'bg-opacity-50' : ''}`}
                >
                  <p className="font-bold">{job.title}</p>
                  <p className="text-sm">{job.customer?.name}</p>
                  <p className="text-xs">{job.recurrence}</p>
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );

  const handlePrevDay = () => {
    const currentIndex = DAYS_OF_WEEK.indexOf(selectedDay);
    const prevIndex = (currentIndex - 1 + DAYS_OF_WEEK.length) % DAYS_OF_WEEK.length;
    setSelectedDay(DAYS_OF_WEEK[prevIndex]);
  };

  const handleNextDay = () => {
    const currentIndex = DAYS_OF_WEEK.indexOf(selectedDay);
    const nextIndex = (currentIndex + 1) % DAYS_OF_WEEK.length;
    setSelectedDay(DAYS_OF_WEEK[nextIndex]);
  };

  if (loading) {
    return <div className="text-center mt-8 text-white">Loading...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-4">Build Routes</h1>
        <div className="flex items-center justify-between mb-4">
          <button onClick={handlePrevDay} className="bg-gray-800 p-2 rounded">
            <ChevronLeftIcon className="h-6 w-6" />
          </button>
          <h2 className="text-2xl font-bold">{selectedDay}</h2>
          <button onClick={handleNextDay} className="bg-gray-800 p-2 rounded">
            <ChevronRightIcon className="h-6 w-6" />
          </button>
        </div>
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-1">
              {renderJobList('route1', routes[selectedDay]?.route1 || [], 'Route 1')}
            </div>
            <div className="col-span-1">
              {renderJobList('route2', routes[selectedDay]?.route2 || [], 'Route 2')}
            </div>
            <div className="col-span-1">
              {renderJobList('route3', routes[selectedDay]?.route3 || [], 'Route 3')}
            </div>
          </div>
          <div className="mt-4">
            {renderJobList('jobPool', jobPool)}
          </div>
        </DragDropContext>
      </main>
    </div>
  );
};

export default BuildRoutes;
