// frontend/src/pages/Dashboard.js

import React, { useEffect, useState } from 'react';
import { fetchCounts } from '../api/dashboardApi';
// Import any UI components or libraries as needed
// Example: import { Card, Row, Col } from 'antd'; // If using Ant Design

const Dashboard = () => {
  const [counts, setCounts] = useState({
    customerCount: 0,
    jobCount: 0,
    employeeCount: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const getCounts = async () => {
      try {
        const data = await fetchCounts();
        setCounts(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load dashboard counts.');
        setLoading(false);
      }
    };

    getCounts();
  }, []);

  if (loading) {
    return <div>Loading dashboard...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>Dashboard</h1>
      <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '30px' }}>
        {/* Customer Count */}
        <div style={cardStyle}>
          <h3>Customers</h3>
          <p style={countStyle}>{counts.customerCount}</p>
        </div>

        {/* Job Count */}
        <div style={cardStyle}>
          <h3>Jobs</h3>
          <p style={countStyle}>{counts.jobCount}</p>
        </div>

        {/* Employee Count */}
        <div style={cardStyle}>
          <h3>Employees</h3>
          <p style={countStyle}>{counts.employeeCount}</p>
        </div>
      </div>

      {/* Future Insights and Quick Actions can be added below */}
      {/* Example: Quick Actions, Charts, etc. */}
    </div>
  );
};

// Inline styles for simplicity; consider using CSS or styled-components
const cardStyle = {
  border: '1px solid #ddd',
  borderRadius: '8px',
  padding: '20px',
  width: '200px',
  textAlign: 'center',
  boxShadow: '2px 2px 12px rgba(0,0,0,0.1)',
};

const countStyle = {
  fontSize: '2em',
  margin: '10px 0 0 0',
  color: '#1890ff',
};

export default Dashboard;
