import React, { useState } from 'react';
import axiosInstance from '../utils/axiosInstance';
import { useAuth } from '../contexts/AuthContext';
import AlertDialog from './AlertDialog'; // Import the improved AlertDialog

const AccountTab = ({ profile, onAccountUpdate }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const { logout } = useAuth();

  const handleCancelSubscription = async () => {
    setIsLoading(true);
    setError('');
    setSuccess('');
    try {
      const response = await axiosInstance.post('/api/subscription/cancel');
      setSuccess(`Your subscription has been cancelled. You will have access to premium features until ${new Date(response.data.cancelAt).toLocaleDateString()}.`);
      onAccountUpdate(); // Refresh the profile data
      setIsDialogOpen(false);
    } catch (err) {
      setError('Failed to cancel subscription. Please try again.');
      console.error('Error cancelling subscription:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6 text-white">
      <h2 className="text-2xl font-bold">Account Details</h2>
      <p><strong>Subscription Tier:</strong> {profile.subscriptionTier}</p>
      <p><strong>Subscription Status:</strong> {profile.subscriptionActive ? 'Active' : 'Inactive'}</p>
      
      {profile.subscriptionActive && !profile.cancellationRequested && (
        <button
          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 disabled:opacity-50"
          onClick={() => setIsDialogOpen(true)}
          disabled={isLoading}
        >
          {isLoading ? 'Processing...' : 'Cancel Subscription'}
        </button>
      )}

      <AlertDialog isOpen={isDialogOpen} onClose={() => setIsDialogOpen(false)}>
        {/* The content is now handled within the AlertDialog component */}
      </AlertDialog>

      {error && (
        <div className="bg-red-600 text-white p-4 rounded-md">
          <strong>Error:</strong> {error}
        </div>
      )}

      {success && (
        <div className="bg-green-600 text-white p-4 rounded-md">
          <strong>Success:</strong> {success}
        </div>
      )}
      
      <div className="mt-8">
        <h3 className="text-xl font-semibold mb-4">Danger Zone</h3>
        <button 
          onClick={() => {/* Implement account deletion logic */}}
          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700"
        >
          Delete Account
        </button>
      </div>
    </div>
  );
};

export default AccountTab;
