import React from 'react';
import Navbar from '../components/Navbar';
import { FaCheck, FaTimes } from 'react-icons/fa';

const PricingTier = ({ name, price, features, recommended }) => (
  <div className={`bg-surface p-6 rounded-lg shadow-md ${recommended ? 'border-2 border-primary' : ''}`}>
    {recommended && (
      <span className="bg-primary text-white px-2 py-1 rounded-full text-sm font-semibold mb-2 inline-block">
        Recommended
      </span>
    )}
    <h3 className="text-2xl font-bold mb-2">{name}</h3>
    <p className="text-3xl font-bold mb-4">${price}<span className="text-sm font-normal">/month</span></p>
    <ul className="space-y-2">
      {features.map((feature, index) => (
        <li key={index} className="flex items-center">
          {feature.included ? (
            <FaCheck className="text-green-500 mr-2" />
          ) : (
            <FaTimes className="text-red-500 mr-2" />
          )}
          <span className={feature.included ? '' : 'text-gray-400'}>{feature.text}</span>
        </li>
      ))}
    </ul>
    <button className={`mt-6 w-full py-2 px-4 rounded ${recommended ? 'bg-primary text-white' : 'bg-gray-200 text-gray-800'} font-semibold`}>
      Choose Plan
    </button>
  </div>
);

const Pricing = () => {
  const tiers = [
    {
      name: "Free",
      price: 0,
      features: [
        { text: "Up to 10 customers", included: true },
        { text: "Basic scheduling", included: true },
        { text: "Limited job tracking", included: true },
        { text: "Email support", included: true },
        { text: "Route optimization", included: false },
        { text: "Advanced analytics", included: false },
        { text: "Team management", included: false },
      ],
    },
    {
      name: "Basic",
      price: 29,
      features: [
        { text: "Up to 50 customers", included: true },
        { text: "Advanced scheduling", included: true },
        { text: "Full job tracking", included: true },
        { text: "Email and phone support", included: true },
        { text: "Basic route optimization", included: true },
        { text: "Basic analytics", included: true },
        { text: "Team management", included: false },
      ],
    },
    {
      name: "Pro",
      price: 79,
      recommended: true,
      features: [
        { text: "Unlimited customers", included: true },
        { text: "Advanced scheduling", included: true },
        { text: "Full job tracking", included: true },
        { text: "Priority support", included: true },
        { text: "Advanced route optimization", included: true },
        { text: "Advanced analytics", included: true },
        { text: "Team management", included: true },
      ],
    },
    {
      name: "Enterprise",
      price: 199,
      features: [
        { text: "Unlimited customers", included: true },
        { text: "Advanced scheduling", included: true },
        { text: "Full job tracking", included: true },
        { text: "24/7 dedicated support", included: true },
        { text: "Advanced route optimization", included: true },
        { text: "Custom analytics", included: true },
        { text: "Advanced team management", included: true },
      ],
    },
  ];

  return (
    <div className="bg-background text-text min-h-screen">
      <Navbar />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        <h1 className="text-4xl font-bold text-center mb-12">Choose Your Plan</h1>
        <p className="text-xl text-center mb-12">
          Select the perfect plan to grow your lawn care business with AUTOLAWN
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {tiers.map((tier, index) => (
            <PricingTier key={index} {...tier} />
          ))}
        </div>
        <div className="mt-16 text-center">
          <h2 className="text-2xl font-semibold mb-4">Not sure which plan is right for you?</h2>
          <a 
            href="/contact" 
            className="bg-primary text-white px-6 py-3 rounded-md text-lg font-medium hover:bg-opacity-90 transition duration-300"
          >
            Contact Us
          </a>
        </div>
      </main>
    </div>
  );
};

export default Pricing;
