import React, { useState } from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import { saveRoute } from './api';

const RouteBuilder = ({ jobs, routes, setJobs, setRoutes, setSuccessMessage }) => {
  const [filter, setFilter] = useState('All');

  const filteredJobs = filter === 'All' ? jobs : jobs.filter(job => job.status === filter);

  const handleSaveRoute = async (route) => {
    try {
      const savedRoute = await saveRoute(route);
      setRoutes(prevRoutes => prevRoutes.map(r => r._id === savedRoute._id ? savedRoute : r));
      setSuccessMessage('Route saved successfully!');
    } catch (error) {
      console.error('Error saving route:', error);
      setSuccessMessage('Failed to save route. Please try again.');
    }
  };

  return (
    <div className="route-builder grid grid-cols-3 gap-4">
      <div className="jobs-pool col-span-1">
        <h2 className="text-xl font-semibold mb-2">Available Jobs</h2>
        <select 
          value={filter} 
          onChange={(e) => setFilter(e.target.value)}
          className="mb-2 p-2 border rounded"
        >
          <option value="All">All</option>
          <option value="Scheduled">Scheduled</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
          <option value="Canceled">Canceled</option>
        </select>
        <Droppable droppableId="jobsPool">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef} className="min-h-[300px] bg-gray-100 p-2 rounded">
              {filteredJobs.map((job, index) => (
                <Draggable key={job._id} draggableId={job._id} index={index}>
                  {(provided) => (
                    <li
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="bg-white p-2 mb-2 rounded shadow"
                    >
                      {job.customer?.name || 'Unnamed Customer'} - {job.status}
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </div>
      <div className="routes-list col-span-2">
        <h2 className="text-xl font-semibold mb-2">Routes</h2>
        {routes.map((route, routeIndex) => (
          <Droppable key={route._id} droppableId={`route-${route._id}`}>
            {(provided) => (
              <div 
                {...provided.droppableProps} 
                ref={provided.innerRef}
                className="bg-gray-100 p-4 rounded mb-4 min-h-[150px]"
              >
                <div className="flex justify-between items-center mb-2">
                  <h3 className="font-semibold">{route.name}</h3>
                  <button
                    onClick={() => handleSaveRoute(route)}
                    className="bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-600"
                  >
                    Save Route
                  </button>
                </div>
                {route.jobs && route.jobs.map((job, index) => (
                  <Draggable key={job._id} draggableId={job._id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-white p-2 mb-2 rounded shadow"
                      >
                        {job.customer?.name || 'Unnamed Customer'} - {job.status}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </div>
    </div>
  );
};

export default RouteBuilder;
