import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const getHeaders = () => ({
  headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
});

const handleApiError = (error, fallbackMessage) => {
  if (error.response && error.response.status === 401) {
    localStorage.removeItem('token');
    window.location.href = '/login';
  } else {
    console.error(fallbackMessage, error.response?.data || error.message);
  }
  throw error;
};

export const fetchJobs = async () => {
  try {
    const response = await axios.get(`${API_URL}/jobs`, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching jobs:');
  }
};

export const fetchCustomers = async () => {
  try {
    const response = await axios.get(`${API_URL}/customers`, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching customers:');
  }
};

export const fetchEmployees = async () => {
  try {
    const response = await axios.get(`${API_URL}/employees`, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching employees:');
  }
};

export const fetchAnalytics = async (timeRange, customRange = null) => {
  let startDate, endDate;

  if (timeRange === 'custom' && customRange) {
    startDate = customRange.start;
    endDate = customRange.end;
  } else {
    const end = new Date();
    const start = new Date();
    start.setDate(end.getDate() - (timeRange === 'all' ? 365 : parseInt(timeRange)));
    startDate = start.toISOString().split('T')[0];
    endDate = end.toISOString().split('T')[0];
  }

  const url = `${API_URL}/analytics?startDate=${startDate}&endDate=${endDate}`;
  try {
    const response = await axios.get(url, getHeaders());

    // Ensure all expected properties are present with default values
    const analyticsData = {
      revenue: { labels: [], data: [] },
      customerGrowth: { labels: [], data: [] },
      jobStatusDistribution: { labels: [], data: [] },
      topPerformingEmployees: { labels: [], data: [] },
      customerRetention: 0,
      jobCancellationRate: 0,
      jobFrequencyByCustomer: { labels: [], data: [] },
      customerSatisfaction: { labels: [], data: [], averageRating: 0 },
      topServices: { labels: [], data: [] },
      employeeDistribution: { labels: [], data: [] },
      customerLifetimeValue: { labels: [], data: [] },
      averageLifetimeValue: 0,
      jobCompletionRate: 0,
      ...response.data
    };

    return analyticsData;
  } catch (error) {
    console.error('Error fetching analytics:', error.response?.data || error.message);
    // Return a default structure instead of throwing an error
    return {
      revenue: { labels: [], data: [] },
      customerGrowth: { labels: [], data: [] },
      jobStatusDistribution: { labels: [], data: [] },
      topPerformingEmployees: { labels: [], data: [] },
      customerRetention: 0,
      jobCancellationRate: 0,
      jobFrequencyByCustomer: { labels: [], data: [] },
      customerSatisfaction: { labels: [], data: [], averageRating: 0 },
      topServices: { labels: [], data: [] },
      employeeDistribution: { labels: [], data: [] },
      customerLifetimeValue: { labels: [], data: [] },
      averageLifetimeValue: 0,
      jobCompletionRate: 0
    };
  }
};

export const fetchRoutes = async () => {
  try {
    const response = await axios.get(`${API_URL}/routes`, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching routes:');
  }
};

export const fetchServices = async () => {
  try {
    const response = await axios.get(`${API_URL}/services`, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching services:');
  }
};

export const fetchBusinessInfo = async () => {
  try {
    const response = await axios.get(`${API_URL}/business-info`, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching business info:');
  }
};

export const fetchRevenue = async (startDate, endDate) => {
  const url = `${API_URL}/analytics/revenue?startDate=${startDate}&endDate=${endDate}`;
  try {
    const response = await axios.get(url, getHeaders());

    // Calculate total revenue considering recurring jobs
    const revenueData = response.data;
    revenueData.totalRevenue = revenueData.jobs.reduce((total, job) => {
      if (job.isRecurring) {
        return total + (job.completionCount * job.price);
      }
      return total + job.price;
    }, 0);

    return revenueData;
  } catch (error) {
    return handleApiError(error, 'Error fetching revenue:');
  }
};

export const fetchJobStatistics = async (startDate, endDate) => {
  const url = `${API_URL}/analytics/job-statistics?startDate=${startDate}&endDate=${endDate}`;
  try {
    const response = await axios.get(url, getHeaders());

    // Adjust job statistics to account for recurring jobs
    const jobStats = response.data;
    jobStats.totalRevenue = jobStats.jobs.reduce((total, job) => {
      if (job.isRecurring) {
        return total + (job.completionCount * job.price);
      }
      return total + job.price;
    }, 0);

    jobStats.jobStatusDistribution = jobStats.jobs.reduce((acc, job) => {
      if (job.isRecurring) {
        acc[job.recurringStatus] = (acc[job.recurringStatus] || 0) + 1;
      } else {
        acc[job.status] = (acc[job.status] || 0) + 1;
      }
      return acc;
    }, {});

    return jobStats;
  } catch (error) {
    return handleApiError(error, 'Error fetching job statistics:');
  }
};

export const fetchEmployeePerformance = async (startDate, endDate) => {
  const url = `${API_URL}/analytics/employee-performance?startDate=${startDate}&endDate=${endDate}`;
  try {
    const response = await axios.get(url, getHeaders());
    return response.data;
  } catch (error) {
    return handleApiError(error, 'Error fetching employee performance:');
  }
};

export default {
  fetchJobs,
  fetchCustomers,
  fetchEmployees,
  fetchAnalytics,
  fetchRoutes,
  fetchServices,
  fetchBusinessInfo,
  fetchRevenue,
  fetchJobStatistics,
  fetchEmployeePerformance,
};
