import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PlusIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';

const Jobs = () => {
  const [job, setJob] = useState({
    title: '',
    description: '',
    customer: '',
    price: '',
    status: 'Pending',
    recurrence: 'One-time',
    scheduledDate: '',
    estimatedDuration: '',
    location: {
      address: '',
      coordinates: []
    },
    cost: '',
    assignedTo: '',
    notes: ''
  });
  const [jobs, setJobs] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [services, setServices] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchCustomers();
    fetchJobs();
    fetchEmployees();
    fetchRoutes();
    fetchServices();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/jobs/recent', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to fetch jobs. Please try again.');
    }
    setLoading(false);
  };

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/employees', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchRoutes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/routes', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRoutes(response.data);
    } catch (error) {
      console.error('Error fetching routes:', error);
    }
  };

  const fetchServices = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/services', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setServices(response.data);
    } catch (error) {
      console.error('Error fetching services:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setJob(prevJob => ({
        ...prevJob,
        [parent]: {
          ...prevJob[parent],
          [child]: value
        }
      }));
    } else {
      setJob({ ...job, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    const jobData = { ...job };
    if (!job.scheduledDate) {
      delete jobData.scheduledDate;  // Remove scheduledDate if not provided
    }

    try {
      const response = await axios.post('http://localhost:5000/api/jobs', jobData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Job created successfully!');
      setJob({
        title: '',
        description: '',
        customer: '',
        price: '',
        status: 'Pending',
        recurrence: 'One-time',
        scheduledDate: '',
        estimatedDuration: '',
        location: {
          address: '',
          coordinates: []
        },
        cost: '',
        assignedTo: '',
        notes: ''
      });
      fetchJobs();
      setShowForm(false);
    } catch (error) {
      console.error('Error creating job:', error.response?.data || error.message);
      setError(error.response?.data?.message || 'Failed to create job. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl font-semibold">Jobs</h1>
            <div className="space-x-2">
              <button
                onClick={() => setShowForm(!showForm)}
                className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <PlusIcon className="h-5 w-5 inline-block mr-1" />
                {showForm ? 'Hide Form' : 'New Job'}
              </button>
            </div>
          </div>
          
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>}
          
          {/* Create New Job Form */}
          {showForm && (
            <div className="bg-surface shadow rounded-lg p-6 mb-6">
              <h2 className="text-xl font-semibold mb-4">Create New Job</h2>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label htmlFor="title" className="block text-sm font-medium">Service</label>
                    <select
                      id="title"
                      name="title"
                      value={job.title}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                      required
                    >
                      <option value="">Select a service</option>
                      {services.map((service) => (
                        <option key={service._id} value={service.name}>
                          {service.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label htmlFor="customer" className="block text-sm font-medium">Customer</label>
                    <select
                      id="customer"
                      name="customer"
                      value={job.customer}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                      required
                    >
                      <option value="">Select a customer</option>
                      {customers.map((customer) => (
                        <option key={customer._id} value={customer._id}>
                          {customer.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label htmlFor="price" className="block text-sm font-medium">Price</label>
                    <input
                      type="number"
                      id="price"
                      name="price"
                      value={job.price}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                      required
                    />
                  </div>
                  <div>
                    <label htmlFor="status" className="block text-sm font-medium">Status</label>
                    <select
                      id="status"
                      name="status"
                      value={job.status}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                      required
                    >
                      <option value="Pending">Pending</option>
                      <option value="Scheduled">Scheduled</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                      <option value="Cancelled">Cancelled</option>
                    </select>
                  </div>
                  <div>
                    <label htmlFor="recurrence" className="block text-sm font-medium">Recurrence</label>
                    <select
                      id="recurrence"
                      name="recurrence"
                      value={job.recurrence}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                      required
                    >
                      <option value="One-time">One-time</option>
                      <option value="Weekly">Weekly</option>
                      <option value="Bi-weekly">Bi-weekly</option>
                      <option value="Monthly">Monthly</option>
                    </select>
                  </div>
                  <div>
                    <label htmlFor="scheduledDate" className="block text-sm font-medium">Scheduled Date</label>
                    <input
                      type="date"
                      id="scheduledDate"
                      name="scheduledDate"
                      value={job.scheduledDate}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                    />
                  </div>
                  <div>
                    <label htmlFor="estimatedDuration" className="block text-sm font-medium">Estimated Duration (minutes)</label>
                    <input
                      type="number"
                      id="estimatedDuration"
                      name="estimatedDuration"
                      value={job.estimatedDuration}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                    />
                  </div>
                  <div>
                    <label htmlFor="cost" className="block text-sm font-medium">Cost</label>
                    <input
                      type="number"
                      id="cost"
                      name="cost"
                      value={job.cost}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                    />
                  </div>
                  <div>
                    <label htmlFor="assignedTo" className="block text-sm font-medium">Assigned To</label>
                    <select
                      id="assignedTo"
                      name="assignedTo"
                      value={job.assignedTo}
                      onChange={handleChange}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                    >
                      <option value="">Select an employee</option>
                      {employees.map((employee) => (
                        <option key={employee._id} value={employee._id}>
                          {employee.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                <div>
                  <label htmlFor="location.address" className="block text-sm font-medium">Location Address</label>
                  <input
                    type="text"
                    id="location.address"
                    name="location.address"
                    value={job.location.address}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  />
                </div>
                <div>
                  <label htmlFor="notes" className="block text-sm font-medium">Notes</label>
                  <textarea
                    id="notes"
                    name="notes"
                    value={job.notes}
                    onChange={handleChange}
                    rows="4"
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  ></textarea>
                </div>
                <button
                  type="submit"
                  className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-primary"
                  disabled={loading}
                >
                  {loading ? 'Creating...' : 'Create Job'}
                </button>
              </form>
            </div>
          )}

          {/* Job List */}
          <div className="bg-surface shadow rounded-lg overflow-hidden">
            <table className="min-w-full divide-y divide-gray-700">
              <thead className="bg-gray-800">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Job</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Customer</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Price</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Recurrence</th>
                </tr>
              </thead>
              <tbody className="bg-gray-900 divide-y divide-gray-700">
                {jobs.map((job) => (
                  <tr key={job._id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm font-medium text-white">{job.title}</div>
                      <div className="text-sm text-gray-400">{job.description}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-white">{job.customer?.name || 'Unknown'}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-white">${job.price}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                        ${job.status === 'Completed' ? 'bg-green-100 text-green-800' : 
                          job.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' : 
                          job.status === 'Cancelled' ? 'bg-red-100 text-red-800' : 
                          job.status === 'Scheduled' ? 'bg-blue-100 text-blue-800' :
                          'bg-gray-100 text-gray-800'}`}>
                        {job.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-white">{job.recurrence}</div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Jobs;
