import React, { useState } from 'react';
import axios from 'axios';
import { QuoteDownloadLink } from './QuotePDF';
import Header from './Header';

const QuoteTool = () => {
  const [type, setType] = useState('lawncare');
  const [area, setArea] = useState('');
  const [options, setOptions] = useState({
    lawncare: {
      frequency: 'bi-weekly',
      hillside: false,
      pushMowing: false,
      weedWhacking: false,
      edging: false,
      fertilizing: false,
      debrisRemoval: false,
      seasonalCleanup: false,
    },
    hedge_trimming: {
      heightOver6ft: false,
      shapeComplexity: 'simple',
      debris: false,
    },
    landscaping: {
      soilPreparation: false,
      plantSelection: 'basic',
      irrigation: false,
      hardscaping: false,
    },
  });
  const [quote, setQuote] = useState(null);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    address: '',
    phone: '',
    email: '',
  });

  const handleOptionChange = (serviceType, option, value) => {
    setOptions(prevOptions => ({
      ...prevOptions,
      [serviceType]: {
        ...prevOptions[serviceType],
        [option]: value,
      },
    }));
  };

  const handleCalculateQuote = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/quotes/calculate', {
        type,
        area: parseFloat(area),
        options: options[type],
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setQuote(response.data);
    } catch (error) {
      console.error('Error calculating quote:', error);
    }
  };

  const renderOptions = () => {
    switch (type) {
      case 'lawncare':
        return (
          <>
            <label className="block mb-2">
              <span className="text-white">Frequency:</span>
              <select
                value={options.lawncare.frequency}
                onChange={e => handleOptionChange('lawncare', 'frequency', e.target.value)}
                className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
              >
                <option value="weekly">Weekly</option>
                <option value="bi-weekly">Bi-Weekly</option>
              </select>
            </label>
            {['hillside', 'pushMowing', 'weedWhacking', 'edging', 'fertilizing', 'debrisRemoval', 'seasonalCleanup'].map(option => (
              <label key={option} className="flex items-center mb-2">
                <input
                  type="checkbox"
                  checked={options.lawncare[option]}
                  onChange={e => handleOptionChange('lawncare', option, e.target.checked)}
                  className="form-checkbox h-5 w-5 text-blue-600"
                />
                <span className="ml-2 text-white">{option.split(/(?=[A-Z])/).join(" ")}</span>
              </label>
            ))}
          </>
        );
      case 'hedge_trimming':
        return (
          <>
            <label className="flex items-center mb-2">
              <input
                type="checkbox"
                checked={options.hedge_trimming.heightOver6ft}
                onChange={e => handleOptionChange('hedge_trimming', 'heightOver6ft', e.target.checked)}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-white">Height over 6ft</span>
            </label>
            <label className="block mb-2">
              <span className="text-white">Shape Complexity:</span>
              <select
                value={options.hedge_trimming.shapeComplexity}
                onChange={e => handleOptionChange('hedge_trimming', 'shapeComplexity', e.target.value)}
                className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
              >
                <option value="simple">Simple</option>
                <option value="moderate">Moderate</option>
                <option value="complex">Complex</option>
              </select>
            </label>
            <label className="flex items-center mb-2">
              <input
                type="checkbox"
                checked={options.hedge_trimming.debris}
                onChange={e => handleOptionChange('hedge_trimming', 'debris', e.target.checked)}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-white">Debris Removal</span>
            </label>
          </>
        );
      case 'landscaping':
        return (
          <>
            <label className="flex items-center mb-2">
              <input
                type="checkbox"
                checked={options.landscaping.soilPreparation}
                onChange={e => handleOptionChange('landscaping', 'soilPreparation', e.target.checked)}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-white">Soil Preparation</span>
            </label>
            <label className="block mb-2">
              <span className="text-white">Plant Selection:</span>
              <select
                value={options.landscaping.plantSelection}
                onChange={e => handleOptionChange('landscaping', 'plantSelection', e.target.value)}
                className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
              >
                <option value="basic">Basic</option>
                <option value="premium">Premium</option>
                <option value="exotic">Exotic</option>
              </select>
            </label>
            <label className="flex items-center mb-2">
              <input
                type="checkbox"
                checked={options.landscaping.irrigation}
                onChange={e => handleOptionChange('landscaping', 'irrigation', e.target.checked)}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-white">Irrigation System</span>
            </label>
            <label className="flex items-center mb-2">
              <input
                type="checkbox"
                checked={options.landscaping.hardscaping}
                onChange={e => handleOptionChange('landscaping', 'hardscaping', e.target.checked)}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-white">Hardscaping</span>
            </label>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <Header />
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-surface rounded-lg shadow-xl p-6">
            <h2 className="text-2xl font-semibold text-white mb-4">Quoting Tool</h2>
            <div className="mb-4">
              {['lawncare', 'hedge_trimming', 'landscaping'].map(t => (
                <button
                  key={t}
                  onClick={() => setType(t)}
                  className={`mr-2 px-4 py-2 rounded ${type === t ? 'bg-blue-600 text-white' : 'bg-gray-700 text-gray-300'}`}
                >
                  {t.replace('_', ' ')}
                </button>
              ))}
            </div>
            <div className="mb-4">
              <label className="block mb-2 text-white">
                Area (sq ft):
                <input
                  type="number"
                  value={area}
                  onChange={e => setArea(e.target.value)}
                  className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                />
              </label>
              {renderOptions()}
              <button
                onClick={handleCalculateQuote}
                className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-500"
              >
                Calculate Quote
              </button>
            </div>
            {quote && (
              <div className="mt-6">
                <h3 className="text-xl font-semibold text-white mb-4">Quote Result</h3>
                <p className="text-white">Base Price: ${quote.basePrice.toFixed(2)}</p>
                <p className="text-white">Additional Price: ${quote.additionalPrice.toFixed(2)}</p>
                <p className="text-white">Total Price: ${quote.totalPrice.toFixed(2)}</p>
                <h4 className="text-lg font-semibold text-white mt-4 mb-2">Customer Information</h4>
                {Object.entries(customerInfo).map(([key, value]) => (
                  <label key={key} className="block mb-2">
                    <span className="text-white">{key.charAt(0).toUpperCase() + key.slice(1)}:</span>
                    <input
                      type="text"
                      value={value}
                      onChange={e => setCustomerInfo({ ...customerInfo, [key]: e.target.value })}
                      className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                    />
                  </label>
                ))}
                <QuoteDownloadLink quote={quote} customerInfo={customerInfo} type={type} options={options[type]} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuoteTool;
