import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import {
  PlusIcon,
  UsersIcon,
  MapIcon,
  DocumentIcon,
  ArrowPathIcon,
} from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import { fetchJobsAnalytics } from '../api/dashboardApi';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const Dashboard = () => {
  const { user } = useAuth();
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [employeeCount, setEmployeeCount] = useState(0);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState('30'); // Default to 30 days

  useEffect(() => {
    fetchDashboardData();
  }, [timeRange]);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      const data = await fetchJobsAnalytics(timeRange);
      setAnalyticsData(data);
      setJobCount(data.totalJobs);
      setCustomerCount(data.totalCustomers);
      setEmployeeCount(data.totalEmployees);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const renderRevenueOverTime = () => {
    if (!analyticsData || !analyticsData.revenueOverTime) return null;

    const chartData = {
      labels: analyticsData.revenueOverTime.map(item => new Date(item.date)),
      datasets: [{
        label: 'Revenue',
        data: analyticsData.revenueOverTime.map(item => item.revenue),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }]
    };

    const options = {
      responsive: true,
      scales: {
        x: {
          type: 'time',
          time: {
            unit: 'day'
          }
        },
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Revenue ($)'
          }
        }
      }
    };

    return (
      <div className="bg-gray-800 p-4 rounded-lg shadow mb-4">
        <h2 className="text-xl font-bold mb-2 text-white">Revenue Over Time</h2>
        <Line data={chartData} options={options} />
      </div>
    );
  };

  const renderRecurringVsOneTimeRevenue = () => {
    if (!analyticsData || !analyticsData.recurringVsOneTimeRevenue) return null;

    const chartData = {
      labels: ['Recurring', 'One-Time'],
      datasets: [{
        label: 'Revenue',
        data: [
          analyticsData.recurringVsOneTimeRevenue.recurring,
          analyticsData.recurringVsOneTimeRevenue.oneTime
        ],
        backgroundColor: ['rgba(54, 162, 235, 0.6)', 'rgba(255, 99, 132, 0.6)']
      }]
    };

    const options = {
      responsive: true,
      scales: {
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Revenue ($)'
          }
        }
      }
    };

    return (
      <div className="bg-gray-800 p-4 rounded-lg shadow mb-4">
        <h2 className="text-xl font-bold mb-2 text-white">Recurring vs One-Time Revenue</h2>
        <Bar data={chartData} options={options} />
      </div>
    );
  };

  const renderAverageJobMetrics = () => {
    if (!analyticsData) return null;

    return (
      <div className="bg-gray-800 p-4 rounded-lg shadow mb-4">
        <h2 className="text-xl font-bold mb-2 text-white">Average Job Metrics</h2>
        <div className="grid grid-cols-2 gap-4">
          <div>
            <p className="text-gray-400">Average Duration</p>
            <p className="text-2xl font-bold text-white">{analyticsData.averageJobDuration.toFixed(2)} minutes</p>
          </div>
          <div>
            <p className="text-gray-400">Average Hourly Rate</p>
            <p className="text-2xl font-bold text-white">${analyticsData.averageHourlyRate.toFixed(2)}</p>
          </div>
        </div>
      </div>
    );
  };

  const renderPastDueJobs = () => {
    if (!analyticsData || !analyticsData.pastDueJobs) return null;

    return (
      <div className="bg-gray-800 p-4 rounded-lg shadow mb-4">
        <h2 className="text-xl font-bold mb-2 text-white">Past Due Recurring Jobs</h2>
        <p className="text-2xl font-bold text-white">{analyticsData.pastDueJobs}</p>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user?.name || 'User'}</h1>
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            className="bg-gray-800 text-white px-4 py-2 rounded-md"
          >
            <option value="7">Past 7 days</option>
            <option value="30">Past 30 days</option>
            <option value="90">Past 90 days</option>
            <option value="365">Past Year</option>
          </select>
        </div>

        {error && (
          <div className="bg-red-600 text-white p-4 rounded-md mb-6">
            {error}
          </div>
        )}

        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 mb-8">
          <OverviewCard title="Jobs" count={jobCount} link="/manage-jobs" />
          <OverviewCard title="Customers" count={customerCount} link="/manage-customers" />
          <OverviewCard title="Employees" count={employeeCount} link="/manage-employees" />
        </div>

        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" />
          </div>
        </div>

        {loading ? (
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-white"></div>
            <p className="mt-4">Loading analytics...</p>
          </div>
        ) : (
          <>
            {renderRevenueOverTime()}
            {renderRecurringVsOneTimeRevenue()}
            {renderAverageJobMetrics()}
            {renderPastDueJobs()}
          </>
        )}
      </main>
    </div>
  );
};

const OverviewCard = ({ title, count, link }) => (
  <div className="bg-gray-800 overflow-hidden shadow rounded-lg">
    <div className="px-4 py-5 sm:p-6">
      <h3 className="text-lg leading-6 font-medium text-white">{title}</h3>
      <div className="mt-2 text-3xl font-semibold text-white">{count}</div>
      <div className="mt-4">
        <Link to={link} className="text-blue-400 hover:text-blue-300">
          View all {title.toLowerCase()}
        </Link>
      </div>
    </div>
  </div>
);

const QuickActionButton = ({ to, icon: Icon, text }) => (
  <Link
    to={to}
    className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600 flex items-center justify-center"
  >
    <Icon className="h-5 w-5 mr-2" />
    {text}
  </Link>
);

export default Dashboard;
