// src/pages/Profile.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import ProfileTab from '../components/ProfileTabs/ProfileTab';
import SecurityTab from '../components/ProfileTabs/SecurityTab';
import BadgesTab from '../components/ProfileTabs/BadgesTab';
import ActivityTab from '../components/ProfileTabs/ActivityTab';
import SettingsTab from '../components/ProfileTabs/SettingsTab';
import SubscriptionTab from '../components/ProfileTabs/SubscriptionTab';
import AchievementProgress from '../components/ProfileTabs/AchievementProgress';
import ErrorBoundary from '../components/ErrorBoundary'; // Import the ErrorBoundary
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { LinearProgress } from '@mui/material';
import { FaUser, FaShieldAlt, FaMedal, FaChartLine, FaCog, FaDollarSign } from 'react-icons/fa';
import { useLoadScript } from '@react-google-maps/api';

const libraries = ['places']; // Required for Autocomplete

const Profile = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const [profileData, setProfileData] = useState(null);
  const { user, loading, refreshUser } = useAuth();
  const navigate = useNavigate();
  
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  // Function to fetch profile data
  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/profile`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setProfileData(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
      if (error.response && error.response.status === 401) {
        navigate('/signin');
      }
    }
  };

  useEffect(() => {
    if (!loading) {
      if (!user) {
        navigate('/signin');
      } else {
        fetchProfile();
      }
    }
  }, [loading, user, navigate]);

  const renderTabContent = () => {
    switch (activeTab) {
      case 'profile':
        return <ProfileTab profileData={profileData} refreshProfile={fetchProfile} isLoaded={isLoaded} />;
      case 'security':
        return <SecurityTab />;
      case 'badges':
        return <BadgesTab badges={profileData?.badges} />;
      case 'activity':
        return <ActivityTab />;
      case 'settings':
        return <SettingsTab />;
      case 'subscription':
        return <SubscriptionTab />;
      default:
        return <ProfileTab profileData={profileData} refreshProfile={fetchProfile} isLoaded={isLoaded} />;
    }
  };

  // Calculate profile completion percentage
  const calculateCompletion = () => {
    if (!profileData) return 0;
    let fieldsCompleted = 0;
    const fieldsTotal = 5; // Adjust based on the number of profile fields
    if (profileData.userId?.name) fieldsCompleted += 1;
    if (profileData.userId?.email) fieldsCompleted += 1;
    if (profileData.bio) fieldsCompleted += 1;
    if (profileData.address?.street) fieldsCompleted += 1;
    if (profileData.address?.city && profileData.address?.state && profileData.address?.zipCode)
      fieldsCompleted += 1;
    // Add more fields as necessary
    return (fieldsCompleted / fieldsTotal) * 100;
  };

  // Debugging Logs
  useEffect(() => {
    console.log('Google Maps Loaded:', isLoaded);
    console.log('Load Error:', loadError);
  }, [isLoaded, loadError]);

  if (loadError) {
    return (
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <p className="text-red-500">Failed to load Google Maps. Please try again later.</p>
      </div>
    );
  }

  if (!isLoaded) {
    return (
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <p className="text-gray-500">Loading...</p>
      </div>
    );
  }

  return (
    <div className="bg-background text-text min-h-screen">
      <Header />
      <main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* User Summary Section */}
        <div className="flex flex-col md:flex-row items-center md:items-start md:space-x-6 mb-8">
          <div className="flex-shrink-0 mb-4 md:mb-0">
            <img
              className="w-24 h-24 rounded-full object-cover"
              src={profileData?.userId?.avatar || '/default-avatar.png'}
              alt="User Avatar"
            />
          </div>
          <div className="text-center md:text-left">
            <h1 className="text-3xl font-bold">{profileData?.userId?.name || 'Your Name'}</h1>
            <p className="text-gray-500">{profileData?.bio || 'Add a bio to your profile'}</p>
          </div>
        </div>

        {/* Profile Completion Progress Bar */}
        <div className="mb-6">
          <p className="text-sm text-gray-600 mb-1">
            Profile Completion: {Math.round(calculateCompletion())}%
          </p>
          <LinearProgress
            variant="determinate"
            value={calculateCompletion()}
            style={{ height: '10px', borderRadius: '5px' }}
          />
        </div>

        {/* Achievement Progress */}
        <AchievementProgress progress={Math.round(calculateCompletion())} />

        {/* Tabs */}
        <div className="bg-surface shadow rounded-lg mt-6">
          <div className="border-b border-gray-300">
            <nav className="flex flex-wrap">
              {[
                { name: 'Profile', icon: <FaUser />, key: 'profile' },
                { name: 'Security', icon: <FaShieldAlt />, key: 'security' },
                { name: 'Badges', icon: <FaMedal />, key: 'badges' },
                { name: 'Activity', icon: <FaChartLine />, key: 'activity' },
                { name: 'Settings', icon: <FaCog />, key: 'settings' },
                { name: 'Subscription', icon: <FaDollarSign />, key: 'subscription' },
              ].map((tab) => (
                <button
                  key={tab.key}
                  onClick={() => setActiveTab(tab.key)}
                  className={`flex-1 py-4 px-1 text-center border-b-2 font-medium text-sm flex items-center justify-center space-x-2 ${
                    activeTab === tab.key
                      ? 'border-primary text-primary'
                      : 'border-transparent text-gray-500 hover:text-primary hover:border-gray-300'
                  }`}
                >
                  <span>{tab.icon}</span>
                  <span>{tab.name}</span>
                </button>
              ))}
            </nav>
          </div>
          <ErrorBoundary>
            <div className="p-6">{renderTabContent()}</div>
          </ErrorBoundary>
        </div>
      </main>
    </div>
  );
};

export default Profile;
