import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie, Bar } from 'react-chartjs-2';
import Header from '../components/Header';

const JobStatisticsAnalytics = () => {
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchJobStatisticsData();
  }, []);

  const fetchJobStatisticsData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/analytics/job-statistics', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setAnalyticsData(response.data);
    } catch (error) {
      console.error('Error fetching job statistics data:', error);
      setError('Failed to fetch job statistics data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const jobStatusChartData = {
    labels: Object.keys(analyticsData?.jobStatus || {}),
    datasets: [
      {
        data: Object.values(analyticsData?.jobStatus || {}),
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
          'rgba(153, 102, 255, 0.6)',
        ],
      },
    ],
  };

  const averageJobCostChartData = {
    labels: analyticsData?.averageJobCost.labels || [],
    datasets: [
      {
        label: 'Average Job Cost',
        data: analyticsData?.averageJobCost.data || [],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6">Job Statistics Analytics</h1>
        {loading ? (
          <p>Loading data...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : analyticsData ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-surface p-6 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Job Status Distribution</h2>
              <Pie data={jobStatusChartData} options={chartOptions} />
            </div>
            <div className="bg-surface p-6 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Average Job Cost</h2>
              <Bar data={averageJobCostChartData} options={chartOptions} />
            </div>
          </div>
        ) : (
          <p>No data available.</p>
        )}
      </main>
    </div>
  );
};

export default JobStatisticsAnalytics;
