import axios from 'axios';

const API_URL = 'http://localhost:5000/api';
const getHeaders = () => ({ Authorization: `Bearer ${localStorage.getItem('token')}` });

export const fetchCustomers = async () => {
  const response = await axios.get(`${API_URL}/customers`, { headers: getHeaders() });
  return response.data;
};

export const fetchRoutes = async () => {
  const response = await axios.get(`${API_URL}/routes`, { headers: getHeaders() });
  return response.data;
};

export const saveRoute = async (route) => {
  const response = await axios.post(`${API_URL}/routes`, route, { headers: getHeaders() });
  return response.data;
};

export const deleteRoute = async (routeId) => {
  await axios.delete(`${API_URL}/routes/${routeId}`, { headers: getHeaders() });
};

export const optimizeRoute = async (routeId) => {
  const response = await axios.post(`${API_URL}/routes/${routeId}/optimize`, {}, { headers: getHeaders() });
  return response.data;
};

export const fetchJobs = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/jobs`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      return response.data; // This should be an array of job objects
    } catch (error) {
      console.error('Error fetching jobs:', error);
      throw error;
    }
  };
  
  export const fetchCalendarEvents = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/calendar-events`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      return response.data; // This should be an array of calendar event objects
    } catch (error) {
      console.error('Error fetching calendar events:', error);
      throw error;
    }
  };
  
  export const updateCalendarEvent = async (eventData) => {
    try {
      const response = await axios.put(`${API_BASE_URL}/calendar-events/${eventData.id}`, eventData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      return response.data; // This should be the updated event object
    } catch (error) {
      console.error('Error updating calendar event:', error);
      throw error;
    }
  };

export const saveSchedule = async (routeId, schedule) => {
  const response = await axios.post(`${API_URL}/routes/${routeId}/schedule`, schedule, { headers: getHeaders() });
  return response.data;
};
