// src/pages/BuildRoutes.js
import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import CustomerMap from '../components/CustomerMap';

const BuildRoutes = () => {
  const [customers, setCustomers] = useState([]);
  const [routes, setRoutes] = useState([]);

  useEffect(() => {
    fetchCustomers();
    fetchRoutes();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const fetchRoutes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/routes', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRoutes(response.data);
    } catch (error) {
      console.error('Error fetching routes:', error);
    }
  };

  const onDragEnd = async (result) => {
    const { source, destination } = result;

    if (!destination) return;

    if (source.droppableId === 'customersList' && destination.droppableId.startsWith('route-')) {
      // Moving customer from pool to route
      const routeIndex = parseInt(destination.droppableId.split('-')[1]);
      const customerIndex = source.index;
      const customer = customers[customerIndex];

      setCustomers(prevCustomers => prevCustomers.filter((_, index) => index !== customerIndex));
      setRoutes(prevRoutes => {
        const newRoutes = [...prevRoutes];
        newRoutes[routeIndex].customers.push(customer);
        return newRoutes;
      });

      try {
        await axios.put(`http://localhost:5000/api/routes/${routes[routeIndex]._id}`, {
          customers: [...routes[routeIndex].customers, customer._id]
        }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      } catch (error) {
        console.error('Error updating route:', error);
      }
    } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'customersList') {
      // Moving customer from route back to pool
      const routeIndex = parseInt(source.droppableId.split('-')[1]);
      const customerIndex = source.index;
      const customer = routes[routeIndex].customers[customerIndex];

      setRoutes(prevRoutes => {
        const newRoutes = [...prevRoutes];
        newRoutes[routeIndex].customers.splice(customerIndex, 1);
        return newRoutes;
      });
      setCustomers(prevCustomers => [...prevCustomers, customer]);

      try {
        await axios.put(`http://localhost:5000/api/routes/${routes[routeIndex]._id}`, {
          customers: routes[routeIndex].customers.filter((_, index) => index !== customerIndex).map(c => c._id)
        }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      } catch (error) {
        console.error('Error updating route:', error);
      }
    } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
      // Moving customer between routes
      const sourceRouteIndex = parseInt(source.droppableId.split('-')[1]);
      const destRouteIndex = parseInt(destination.droppableId.split('-')[1]);
      const customerIndex = source.index;
      const customer = routes[sourceRouteIndex].customers[customerIndex];

      setRoutes(prevRoutes => {
        const newRoutes = [...prevRoutes];
        newRoutes[sourceRouteIndex].customers.splice(customerIndex, 1);
        newRoutes[destRouteIndex].customers.splice(destination.index, 0, customer);
        return newRoutes;
      });

      try {
        await Promise.all([
          axios.put(`http://localhost:5000/api/routes/${routes[sourceRouteIndex]._id}`, {
            customers: routes[sourceRouteIndex].customers.filter((_, index) => index !== customerIndex).map(c => c._id)
          }, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
          axios.put(`http://localhost:5000/api/routes/${routes[destRouteIndex]._id}`, {
            customers: routes[destRouteIndex].customers.map(c => c._id)
          }, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          })
        ]);
      } catch (error) {
        console.error('Error updating routes:', error);
      }
    }
  };

  const addNewRoute = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/routes', {
        name: `Route ${routes.length + 1}`,
        customers: []
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRoutes(prevRoutes => [...prevRoutes, response.data]);
    } catch (error) {
      console.error('Error creating new route:', error);
    }
  };

  return (
    <div className="build-routes p-6">
      <h1 className="text-2xl font-bold mb-4">Build Routes</h1>
      <button onClick={addNewRoute} className="bg-blue-500 text-white px-4 py-2 rounded mb-4">
        Add New Route
      </button>
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="flex space-x-4">
          <div className="customers-list w-1/4">
            <h2 className="text-xl font-semibold mb-2">Customers</h2>
            <Droppable droppableId="customersList">
              {(provided) => (
                <ul {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-2 rounded min-h-[200px]">
                  {customers.map((customer, index) => (
                    <Draggable key={customer._id} draggableId={customer._id} index={index}>
                      {(provided) => (
                        <li
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white p-2 mb-2 rounded shadow"
                        >
                          {customer.name} - {customer.address.street}
                        </li>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </div>
          <div className="routes-list flex-1">
            <h2 className="text-xl font-semibold mb-2">Routes</h2>
            {routes.map((route, routeIndex) => (
              <Droppable key={routeIndex} droppableId={`route-${routeIndex}`}>
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-2 rounded mb-4 min-h-[100px]">
                    <h3 className="font-semibold">{route.name}</h3>
                    {route.customers.map((customer, index) => (
                      <Draggable key={customer._id} draggableId={`route-${routeIndex}-customer-${customer._id}`} index={index}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="bg-white p-2 mb-2 rounded shadow"
                          >
                            {customer.name} - {customer.address.street}
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </DragDropContext>
      <div className="map-container mt-6">
        <h2 className="text-xl font-semibold mb-2">Customer Map</h2>
        <CustomerMap customers={customers} />
      </div>
    </div>
  );
};

export default BuildRoutes;
