import React, { useState } from 'react';

const Scheduling = ({ routes, handleRouteSelect, selectedRoute, handleRecurringOptionChange, recurringOptions, saveSchedule }) => (
  <div className="scheduling-container">
    <h2 className="text-xl font-semibold mb-4">Scheduling</h2>
    <div className="grid grid-cols-2 gap-4">
      <div>
        <h3 className="text-lg font-semibold mb-2">Routes</h3>
        <ul>
          {routes.map(route => (
            <li 
              key={route._id} 
              onClick={() => handleRouteSelect(route._id)}
              className={`cursor-pointer p-2 ${selectedRoute?._id === route._id ? 'bg-blue-100' : ''}`}
            >
              {route.name}
            </li>
          ))}
        </ul>
      </div>
      <div>
        <h3 className="text-lg font-semibold mb-2">Recurring Schedule</h3>
        {selectedRoute && (
          <div>
            <select 
              value={recurringOptions.frequency} 
              onChange={(e) => handleRecurringOptionChange('frequency', e.target.value)}
            >
              <option value="weekly">Weekly</option>
              <option value="biweekly">Bi-weekly</option>
              <option value="monthly">Monthly</option>
            </select>
            {/* Add more options for recurring schedule */}
            <button onClick={saveSchedule} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
              Save Schedule
            </button>
          </div>
        )}
      </div>
    </div>
  </div>
);

export default Scheduling;
