import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import { PlusIcon, UsersIcon, MapIcon, DocumentIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register the required components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [employeeCount, setEmployeeCount] = useState(0);

  useEffect(() => {
    fetchCounts();
  }, []);

  const fetchCounts = async () => {
    try {
      const jobsResponse = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobCount(jobsResponse.data.length);

      const customersResponse = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomerCount(customersResponse.data.length);

      const employeesResponse = await axios.get('http://localhost:5000/api/employees', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setEmployeeCount(employeesResponse.data.length);
    } catch (error) {
      console.error('Error fetching counts:', error);
    }
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
  };

  const customerGrowthData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Customer Growth',
        data: [10, 20, 30, 40, 50, 60],
        borderColor: '#00bcd4',
        backgroundColor: '#00bcd4',
        fill: false,
      },
    ],
  };

  const revenueData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Revenue',
        data: [100, 200, 300, 400, 500, 600],
        borderColor: '#ab47bc',
        backgroundColor: '#ab47bc',
        fill: false,
      },
    ],
  };

  const averageJobCostData = {
    labels: ['Service A', 'Service B', 'Service C'],
    datasets: [
      {
        label: 'Average Job Cost',
        data: [150, 100, 250],
        backgroundColor: ['#00bcd4', '#ab47bc', '#ff7043'],
      },
    ],
  };

  const jobStatusData = {
    labels: ['Scheduled', 'In Progress', 'Completed', 'Cancelled'],
    datasets: [
      {
        data: [25, 15, 20, 10],
        backgroundColor: ['#00bcd4', '#ab47bc', '#ff7043', '#ffca28'],
      },
    ],
  };

  const employeeDistributionData = {
    labels: ['Crew 1', 'Crew 2', 'Crew 3'],
    datasets: [
      {
        data: [10, 20, 15],
        backgroundColor: ['#00bcd4', '#ab47bc', '#ff7043'],
      },
    ],
  };

  const topServicesData = {
    labels: ['Lawn Mowing', 'Fertilization', 'Weed Control'],
    datasets: [
      {
        label: 'Top Services by Revenue',
        data: [900, 600, 400],
        backgroundColor: ['#00bcd4', '#ab47bc', '#ff7043'],
      },
    ],
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          {user ? (
            <h1 className="text-2xl font-semibold">Welcome, {user.name}</h1>
          ) : (
            <h1 className="text-2xl font-semibold">Welcome</h1>
          )}

          <div className="mt-6 grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3">
            {/* Jobs Overview */}
            <div className="bg-surface overflow-hidden shadow rounded-lg h-41 w-full">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium">Jobs</h3>
                <div className="mt-2 text-3xl font-semibold">{jobCount}</div>
                <div className="mt-4">
                  <Link to="/manage-jobs" className="text-primary hover:text-primary-dark">
                    View all jobs
                  </Link>
                </div>
              </div>
            </div>

            {/* Customers Overview */}
            <div className="bg-surface overflow-hidden shadow rounded-lg h-41 w-full">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium">Customers</h3>
                <div className="mt-2 text-3xl font-semibold">{customerCount}</div>
                <div className="mt-4">
                  <Link to="/manage-customers" className="text-primary hover:text-primary-dark">
                    View all customers
                  </Link>
                </div>
              </div>
            </div>

            {/* Employees Overview */}
            <div className="bg-surface overflow-hidden shadow rounded-lg h-41 w-full">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium">Employees</h3>
                <div className="mt-2 text-3xl font-semibold">{employeeCount}</div>
                <div className="mt-4">
                  <Link to="/manage-employees" className="text-primary hover:text-primary-dark">
                    Manage employees
                  </Link>
                </div>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-4">Quick Actions</h2>
            <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
              <Link to="/jobs" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <PlusIcon className="h-5 w-5 mr-2" />
                Create New Job
              </Link>
              <Link to="/customers" className="bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <PlusIcon className="h-5 w-5 mr-2" />
                Add New Customer
              </Link>
              <Link to="/add-employee" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <UsersIcon className="h-5 w-5 mr-2" />
                Add New Employee
              </Link>
              <Link to="/generate-report" className="bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <DocumentIcon className="h-5 w-5 mr-2" />
                Generate Report
              </Link>
              <Link to="/build-routes" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
                <MapIcon className="h-5 w-5 mr-2" />
                Build Routes
              </Link>
            </div>
          </div>

          {/* Dashboard Insights */}
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-4">Dashboard Insights</h2>
            <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3">
              <div className="bg-surface p-4 rounded-lg shadow h-72">
                <h3 className="text-lg leading-6 font-medium">Customer Growth Over Time</h3>
                <Line data={customerGrowthData} options={chartOptions} height={200} />
              </div>
              <div className="bg-surface p-4 rounded-lg shadow h-72">
                <h3 className="text-lg leading-6 font-medium">Revenue Over Time</h3>
                <Line data={revenueData} options={chartOptions} height={200} />
              </div>
              <div className="bg-surface p-4 rounded-lg shadow h-72">
                <h3 className="text-lg leading-6 font-medium">Average Job Cost</h3>
                <Bar data={averageJobCostData} options={chartOptions} height={200} />
              </div>
              <div className="bg-surface p-4 rounded-lg shadow h-72">
                <h3 className="text-lg leading-6 font-medium">Job Status Distribution</h3>
                <Doughnut data={jobStatusData} options={chartOptions} height={200} />
              </div>
              <div className="bg-surface p-4 rounded-lg shadow h-72">
                <h3 className="text-lg leading-6 font-medium">Employee Distribution</h3>
                <Doughnut data={employeeDistributionData} options={chartOptions} height={200} />
              </div>
              <div className="bg-surface p-4 rounded-lg shadow h-72">
                <h3 className="text-lg leading-6 font-medium">Top Services by Revenue</h3>
                <Bar data={topServicesData} options={chartOptions} height={200} />
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
