// src/pages/Schedule.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Schedule = () => {
  const [schedules, setSchedules] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [newSchedule, setNewSchedule] = useState({
    customer: '',
    job: '',
    date: '',
    time: '',
    recurrence: 'none',
  });

  useEffect(() => {
    // Fetch customers and schedules from your API
    // This is a placeholder. Replace with actual API calls.
    setCustomers([
      { id: '1', name: 'Customer A' },
      { id: '2', name: 'Customer B' },
    ]);
    setSchedules([
      { id: '1', customer: 'Customer A', job: 'Lawn Mowing', date: '2024-03-15', time: '10:00', recurrence: 'weekly' },
      { id: '2', customer: 'Customer B', job: 'Hedge Trimming', date: '2024-03-16', time: '14:00', recurrence: 'biweekly' },
    ]);
  }, []);

  const handleInputChange = (e) => {
    setNewSchedule({ ...newSchedule, [e.target.name]: e.target.value });
  };

  const handleAddSchedule = (e) => {
    e.preventDefault();
    // Add new schedule to the list
    setSchedules([...schedules, { ...newSchedule, id: Date.now().toString() }]);
    setNewSchedule({ customer: '', job: '', date: '', time: '', recurrence: 'none' });
  };

  const handleDeleteSchedule = (id) => {
    setSchedules(schedules.filter(schedule => schedule.id !== id));
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(schedules);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);
    setSchedules(items);
  };

  const handleMoveAllSchedules = (days) => {
    const movedSchedules = schedules.map(schedule => ({
      ...schedule,
      date: new Date(new Date(schedule.date).getTime() + days * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
    }));
    setSchedules(movedSchedules);
  };

  return (
    <div className="min-h-screen bg-background text-text p-8">
      <h1 className="text-3xl font-bold mb-8">Scheduling</h1>

      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Add New Schedule</h2>
        <form onSubmit={handleAddSchedule} className="space-y-4">
          <select
            name="customer"
            value={newSchedule.customer}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select Customer</option>
            {customers.map(customer => (
              <option key={customer.id} value={customer.name}>{customer.name}</option>
            ))}
          </select>
          <input
            type="text"
            name="job"
            value={newSchedule.job}
            onChange={handleInputChange}
            placeholder="Job Description"
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="date"
            name="date"
            value={newSchedule.date}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="time"
            name="time"
            value={newSchedule.time}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
          <select
            name="recurrence"
            value={newSchedule.recurrence}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
          >
            <option value="none">No Recurrence</option>
            <option value="weekly">Weekly</option>
            <option value="biweekly">Every Two Weeks</option>
            <option value="monthly">Monthly</option>
          </select>
          <button type="submit" className="bg-primary text-white px-4 py-2 rounded">Add Schedule</button>
        </form>
      </div>

      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Manage Schedules</h2>
        <div className="space-x-4 mb-4">
          <button onClick={() => handleMoveAllSchedules(1)} className="bg-secondary text-white px-4 py-2 rounded">
            Move All Forward 1 Day
          </button>
          <button onClick={() => handleMoveAllSchedules(7)} className="bg-secondary text-white px-4 py-2 rounded">
            Move All Forward 1 Week
          </button>
        </div>
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="schedules">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef} className="space-y-4">
                {schedules.map((schedule, index) => (
                  <Draggable key={schedule.id} draggableId={schedule.id} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-surface p-4 rounded shadow flex justify-between items-center"
                      >
                        <div>
                          <p className="font-semibold">{schedule.customer}</p>
                          <p>{schedule.job}</p>
                          <p>{schedule.date} at {schedule.time}</p>
                          <p>Recurrence: {schedule.recurrence}</p>
                        </div>
                        <button
                          onClick={() => handleDeleteSchedule(schedule.id)}
                          className="bg-red-500 text-white px-2 py-1 rounded"
                        >
                          Delete
                        </button>
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      </div>

      <Link to="/dashboard" className="text-primary hover:text-primary-dark">
        Back to Dashboard
      </Link>
    </div>
  );
};

export default Schedule;
