// src/pages/Profile.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header'; // Header for authenticated pages
import ProfileTab from '../components/ProfileTabs/ProfileTab';
import SecurityTab from '../components/ProfileTabs/SecurityTab';
import BadgesTab from '../components/ProfileTabs/BadgesTab';
import ActivityTab from '../components/ProfileTabs/ActivityTab';
import SettingsTab from '../components/ProfileTabs/SettingsTab';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { LinearProgress } from '@mui/material'; // Using Material UI for the progress bar
import { FaUser, FaShieldAlt, FaMedal, FaChartLine, FaCog } from 'react-icons/fa'; // Icons for tabs

const Profile = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const [profileData, setProfileData] = useState(null);
  const { user, loading } = useAuth();
  const navigate = useNavigate();

  // Function to fetch profile data
  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/profile`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setProfileData(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
      // Handle unauthorized access
      if (error.response && error.response.status === 401) {
        // Redirect to login or show a message
        navigate('/signin');
      }
    }
  };

  useEffect(() => {
    if (!loading) {
      if (!user) {
        navigate('/signin');
      } else {
        fetchProfile();
      }
    }
  }, [loading, user, navigate]);

  const renderTabContent = () => {
    switch (activeTab) {
      case 'profile':
        return <ProfileTab profileData={profileData} refreshProfile={fetchProfile} />;
      case 'security':
        return <SecurityTab />;
      case 'badges':
        return <BadgesTab badges={profileData?.badges} />;
      case 'activity':
        return <ActivityTab />;
      case 'settings':
        return <SettingsTab />;
      default:
        return <ProfileTab profileData={profileData} refreshProfile={fetchProfile} />;
    }
  };

  // Calculate profile completion percentage
  const calculateCompletion = () => {
    if (!profileData) return 0;
    let fieldsCompleted = 0;
    const fieldsTotal = 5; // Adjust based on the number of profile fields
    if (profileData.name) fieldsCompleted += 1;
    if (profileData.email) fieldsCompleted += 1;
    if (profileData.avatar) fieldsCompleted += 1;
    if (profileData.bio) fieldsCompleted += 1;
    if (profileData.preferences) fieldsCompleted += 1;
    return (fieldsCompleted / fieldsTotal) * 100;
  };

  return (
    <div className="bg-background text-text min-h-screen">
      <Header />
      <main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* User Summary Section */}
        <div className="flex items-center space-x-6 mb-8">
          <img
            className="w-24 h-24 rounded-full object-cover"
            src={profileData?.avatar || '/default-avatar.png'}
            alt="User Avatar"
          />
          <div>
            <h1 className="text-3xl font-bold">{profileData?.name || 'Your Name'}</h1>
            <p className="text-gray-500">{profileData?.bio || 'Add a bio to your profile'}</p>
          </div>
        </div>

        {/* Profile Completion Progress Bar */}
        <div className="mb-6">
          <p className="text-sm text-gray-600 mb-1">
            Profile Completion: {Math.round(calculateCompletion())}%
          </p>
          <LinearProgress
            variant="determinate"
            value={calculateCompletion()}
            style={{ height: '10px', borderRadius: '5px' }}
          />
        </div>

        {/* Tabs */}
        <div className="bg-surface shadow rounded-lg">
          <div className="border-b border-gray-300">
            <nav className="flex">
              {[
                { name: 'Profile', icon: <FaUser />, key: 'profile' },
                { name: 'Security', icon: <FaShieldAlt />, key: 'security' },
                { name: 'Badges', icon: <FaMedal />, key: 'badges' },
                { name: 'Activity', icon: <FaChartLine />, key: 'activity' },
                { name: 'Settings', icon: <FaCog />, key: 'settings' },
              ].map((tab) => (
                <button
                  key={tab.key}
                  onClick={() => setActiveTab(tab.key)}
                  className={`flex-1 py-4 px-1 text-center border-b-2 font-medium text-sm flex items-center justify-center space-x-2 ${
                    activeTab === tab.key
                      ? 'border-primary text-primary'
                      : 'border-transparent text-gray-500 hover:text-primary hover:border-gray-300'
                  }`}
                >
                  <span>{tab.icon}</span>
                  <span>{tab.name}</span>
                </button>
              ))}
            </nav>
          </div>
          <div className="p-6">{renderTabContent()}</div>
        </div>
      </main>
    </div>
  );
};

export default Profile;
