import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust this URL as needed

const getHeaders = () => ({
  headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
});

export const fetchJobs = () => axios.get(`${API_URL}/jobs`, getHeaders()).then(res => res.data);
export const fetchCustomers = () => axios.get(`${API_URL}/customers`, getHeaders()).then(res => res.data);
export const fetchEmployees = () => axios.get(`${API_URL}/employees`, getHeaders()).then(res => res.data);
export const fetchAnalytics = () => axios.get(`${API_URL}/analytics`, getHeaders()).then(res => res.data);
