// src/components/CustomerMap.js
import React from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

const defaultCenter = {
  lat: 0,
  lng: 0
};

const CustomerMap = ({ customers }) => {
  const validCustomers = React.useMemo(() => {
    return customers.filter(c => 
      c.address && 
      typeof c.address.lat === 'number' && 
      typeof c.address.lng === 'number' &&
      !isNaN(c.address.lat) && 
      !isNaN(c.address.lng)
    );
  }, [customers]);

  const center = React.useMemo(() => {
    if (validCustomers.length === 0) return defaultCenter;
    
    const lats = validCustomers.map(c => c.address.lat);
    const lngs = validCustomers.map(c => c.address.lng);
    
    return {
      lat: (Math.min(...lats) + Math.max(...lats)) / 2,
      lng: (Math.min(...lngs) + Math.max(...lngs)) / 2
    };
  }, [validCustomers]);

  if (validCustomers.length === 0) {
    return <div>No valid customer locations available for map.</div>;
  }

  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
      >
        {validCustomers.map((customer) => (
          <Marker
            key={customer._id}
            position={{
              lat: customer.address.lat,
              lng: customer.address.lng
            }}
            title={customer.name}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default CustomerMap;
