// src/components/ProfileTabs/ActivityTab.js
import React, { useEffect, useState } from 'react';
import axiosInstance from '../../utils/axiosInstance'; // Use axiosInstance

const ActivityTab = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axiosInstance.get(
          `${process.env.REACT_APP_API_URL}/api/profile/stats`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setStats(response.data);
      } catch (err) {
        console.error('Error fetching stats:', err);
        setError('Failed to load stats.');
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  if (loading) {
    return <p className="text-gray-500">Loading...</p>;
  }

  if (error) {
    return <p className="text-red-500">{error}</p>;
  }

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-semibold mb-4">Your Activity & Stats</h2>
      <ul className="list-disc pl-5 space-y-2 text-gray-700">
        <li>Customers Added: {stats.customersAdded}</li>
        <li>Jobs Added: {stats.jobsAdded}</li>
        <li>Jobs Completed: {stats.jobsCompleted}</li>
        <li>Routes Created: {stats.routesMade}</li>
        <li>Total Revenue: ${stats.totalRevenue.toFixed(2)}</li>
        {/* Add more stats as needed */}
      </ul>
    </div>
  );
};

export default ActivityTab;
