import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Line, Bar } from 'react-chartjs-2';
import { fetchAnalytics } from '../api/dashboardApi';

const Dashboard = () => {
  const { user } = useAuth();
  const [timeRange, setTimeRange] = useState('30');
  const [analytics, setAnalytics] = useState({
    revenueOverTime: null,
    revenueByJobType: null,
    averageDurationAndRate: null,
    pastDueRecurring: null
  });

  useEffect(() => {
    fetchDashboardAnalytics();
  }, [timeRange]);

  const fetchDashboardAnalytics = async () => {
    try {
      const data = await fetchAnalytics(timeRange);
      setAnalytics(data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  return (
    <div>
      {/* Time Range Selector */}
      <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
        <option value="7">Past 7 days</option>
        <option value="30">Past 30 days</option>
        <option value="90">Past 90 days</option>
        <option value="365">Past Year</option>
      </select>

      {/* Render Analytics */}
      {analytics.revenueOverTime && (
        <Line data={{
          labels: analytics.revenueOverTime.labels,
          datasets: [{
            label: 'Revenue',
            data: analytics.revenueOverTime.data,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1
          }]
        }} />
      )}

      {analytics.revenueByJobType && (
        <Bar data={{
          labels: ['One-Time', 'Recurring'],
          datasets: [{
            label: 'Revenue',
            data: [analytics.revenueByJobType.oneTime, analytics.revenueByJobType.recurring],
            backgroundColor: ['#FF6384', '#36A2EB'],
          }]
        }} />
      )}
    </div>
  );
};

export default Dashboard;
