import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import { PlusIcon, UsersIcon, MapIcon, DocumentIcon, ArrowPathIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { fetchJobs, fetchCustomers, fetchEmployees, fetchAnalytics } from '../api/dashboardApi.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { user } = useAuth();
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [employeeCount, setEmployeeCount] = useState(0);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState('30'); // Default to 30 days

  const fetchDashboardData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [jobs, customers, employees, analytics] = await Promise.all([
        fetchJobs(),
        fetchCustomers(),
        fetchEmployees(),
        fetchAnalytics(timeRange)
      ]);
      setJobCount(jobs.length);
      setCustomerCount(customers.length);
      setEmployeeCount(employees.length);
      setAnalyticsData(analytics);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, [timeRange]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: '',
      },
    },
  };

const TimeRangeSelector = ({ value, onChange }) => (
  <select 
    value={value} 
    onChange={(e) => onChange(e.target.value)}
    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
  >
    <option value="7">Past 7 days</option>
    <option value="30">Past 30 days</option>
    <option value="90">Past 90 days</option>
    <option value="all">All time</option>
  </select>
);
  const getChartData = (dataKey, label, bgColor) => ({
    labels: analyticsData ? analyticsData[dataKey].labels : [],
    datasets: [
      {
        label: label,
        data: analyticsData ? analyticsData[dataKey].data : [],
        backgroundColor: bgColor,
        borderColor: bgColor,
      },
    ],
  });

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user?.name || 'User'}</h1>
          <div className="flex items-center">
            <TimeRangeSelector value={timeRange} onChange={setTimeRange} />
            <button 
              onClick={fetchDashboardData} 
              className="ml-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center"
            >
              <ArrowPathIcon className="h-5 w-5 mr-2" />
              Refresh Dashboard
            </button>
          </div>
        </div>
        {error && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6" role="alert">
            <p>{error}</p>
          </div>
        )}

        {/* Overview Cards */}
        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 mb-8">
          <OverviewCard title="Jobs" count={jobCount} link="/manage-jobs" />
          <OverviewCard title="Customers" count={customerCount} link="/manage-customers" />
          <OverviewCard title="Employees" count={employeeCount} link="/manage-employees" />
        </div>

        {/* Quick Actions */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" />
          </div>
        </div>

        {/* Dashboard Insights */}
        <div>
          <h2 className="text-2xl font-semibold mb-4">Dashboard Insights</h2>
          {loading ? (
            <div className="text-center py-12">Loading dashboard data...</div>
          ) : (
            <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
              <ChartContainer title="Customer Growth Over Time">
                <Line data={getChartData('customerGrowth', 'Customer Growth', '#00bcd4')} options={chartOptions} />
              </ChartContainer>
              <ChartContainer title="Revenue Over Time">
                <Line data={getChartData('revenue', 'Revenue', '#ab47bc')} options={chartOptions} />
              </ChartContainer>
              <ChartContainer title="Average Job Cost">
                <Bar data={getChartData('averageJobCost', 'Average Job Cost', ['#00bcd4', '#ab47bc', '#ff7043'])} options={chartOptions} />
              </ChartContainer>
              <ChartContainer title="Job Status Distribution">
                <Doughnut data={getChartData('jobStatus', 'Job Status', ['#00bcd4', '#ab47bc', '#ff7043', '#ffca28'])} options={chartOptions} />
              </ChartContainer>
              <ChartContainer title="Employee Distribution">
                <Doughnut data={getChartData('employeeDistribution', 'Employee Distribution', ['#00bcd4', '#ab47bc', '#ff7043'])} options={chartOptions} />
              </ChartContainer>
              <ChartContainer title="Top Services by Revenue">
                <Bar data={getChartData('topServices', 'Top Services', ['#00bcd4', '#ab47bc', '#ff7043'])} options={chartOptions} />
              </ChartContainer>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

const OverviewCard = ({ title, count, link }) => (
  <div className="bg-surface overflow-hidden shadow rounded-lg">
    <div className="px-4 py-5 sm:p-6">
      <h3 className="text-lg leading-6 font-medium">{title}</h3>
      <div className="mt-2 text-3xl font-semibold">{count}</div>
      <div className="mt-4">
        <Link to={link} className="text-primary hover:text-primary-dark">
          View all {title.toLowerCase()}
        </Link>
      </div>
    </div>
  </div>
);

const QuickActionButton = ({ to, icon: Icon, text }) => (
  <Link to={to} className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center">
    <Icon className="h-5 w-5 mr-2" />
    {text}
  </Link>
);

const ChartContainer = ({ title, children }) => (
  <div className="bg-surface p-4 rounded-lg shadow">
    <h3 className="text-lg leading-6 font-medium mb-4">{title}</h3>
    <div className="h-64">
      {children}
    </div>
  </div>
);

export default Dashboard;
