// src/pages/ManageJobs.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { PencilIcon, TrashIcon } from '@heroicons/react/24/solid';

const ManageJobs = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [editingJob, setEditingJob] = useState(null);
  const [filter, setFilter] = useState('All');

  useEffect(() => {
    fetchJobs();
  }, []);

  useEffect(() => {
    if (filter === 'All') {
      setFilteredJobs(jobs);
    } else {
      setFilteredJobs(jobs.filter(job => job.status === filter));
    }
  }, [filter, jobs]);

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(response.data);
      setFilteredJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to fetch jobs. Please try again.');
    }
    setLoading(false);
  };

  const handleDeleteJob = async (jobId) => {
    if (window.confirm('Are you sure you want to delete this job?')) {
      try {
        await axios.delete(`http://localhost:5000/api/jobs/${jobId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setJobs(jobs.filter(job => job._id !== jobId));
      } catch (error) {
        console.error('Error deleting job:', error);
        setError('Failed to delete job. Please try again.');
      }
    }
  };

  const handleEditJob = (job) => {
    setEditingJob({ ...job, customer: job.customer._id });
  };
  

  const handleUpdateJob = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/api/jobs/${editingJob._id}`, editingJob, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
  
      // Fetch the updated job with customer details
      const updatedJobResponse = await axios.get(`http://localhost:5000/api/jobs/${editingJob._id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
  
      setJobs(jobs.map(job => job._id === editingJob._id ? updatedJobResponse.data : job));
      setEditingJob(null);
    } catch (error) {
      console.error('Error updating job:', error);
      setError('Failed to update job. Please try again.');
    }
  };

  const handleInputChange = (e) => {
    if (e.target.name === 'customer') {
      setEditingJob({ ...editingJob, customer: e.target.value });
    } else {
      setEditingJob({ ...editingJob, [e.target.name]: e.target.value });
    }
  };
  

  const handleStatusChange = async (jobId, newStatus) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/jobs/${jobId}`, 
        { status: newStatus },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setJobs(jobs.map(job => job._id === jobId ? response.data : job));
    } catch (error) {
      console.error('Error updating job status:', error);
      setError('Failed to update job status. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-6 text-white">Manage Jobs</h1>
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          
          <div className="mb-4">
            <label htmlFor="filter" className="mr-2">Filter by status:</label>
            <select 
              id="filter" 
              value={filter} 
              onChange={(e) => setFilter(e.target.value)}
              className="border rounded p-2 bg-background text-text"
            >
              <option value="All">All</option>
              <option value="Scheduled">Scheduled</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
              <option value="Cancelled">Cancelled</option>
            </select>
          </div>

          {loading ? (
            <div className="text-center">
              <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
              <p className="mt-4">Loading jobs...</p>
            </div>
          ) : filteredJobs.length > 0 ? (
            <div className="bg-surface shadow overflow-hidden sm:rounded-lg">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-500">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Job</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Customer</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Price</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Recurrence</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-surface divide-y divide-gray-200">
                  {filteredJobs.map((job) => (
                    <tr key={job._id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-text">{job.title}</div>
                        <div className="text-sm text-gray-500">{job.description}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-text">{job.customer.name}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-text">{new Date(job.scheduledDate).toLocaleDateString()}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-text">${job.price}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <select
                          value={job.status}
                          onChange={(e) => handleStatusChange(job._id, e.target.value)}
                          className="text-sm text-white border rounded p-1 bg-primary"
                        >
                          <option value="Scheduled" className="bg-primary text-white">Scheduled</option>
                          <option value="In Progress" className="bg-primary text-white">In Progress</option>
                          <option value="Completed" className="bg-primary text-white">Completed</option>
                          <option value="Cancelled" className="bg-primary text-white">Cancelled</option>
                        </select>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-text">{job.recurrence}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button
                          onClick={() => handleEditJob(job)}
                          className="text-indigo-600 hover:text-indigo-900 mr-2"
                        >
                          <PencilIcon className="h-5 w-5 inline" />
                        </button>
                        <button
                          onClick={() => handleDeleteJob(job._id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          <TrashIcon className="h-5 w-5 inline" />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center text-gray-500">No jobs found.</p>
          )}
          <Link to="/dashboard" className="mt-6 inline-block bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90 transition duration-200">
            Back to Dashboard
          </Link>
        </div>
      </main>

      {/* Edit Job Modal */}
      {editingJob && (
        <div className="fixed z-10 inset-0 overflow-y-auto" aria-labelledby="modal-title" role="dialog" aria-modal="true">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true"></div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-surface rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-background px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <h3 className="text-lg leading-6 font-medium text-text" id="modal-title">
                  Edit Job
                </h3>
                <div className="mt-2">
                  <input
                    type="text"
                    name="title"
                    value={editingJob.title}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-primary focus:border-primary mb-2 text-black"
                    placeholder="Job Title"
                  />
                  <textarea
                    name="description"
                    value={editingJob.description}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-primary focus:border-primary mb-2 text-black"
                    placeholder="Job Description"
                  />
                  <input
                    type="date"
                    name="scheduledDate"
                    value={editingJob.scheduledDate.split('T')[0]}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-primary focus:border-primary mb-2 text-black"
                  />
                  <input
                    type="number"
                    name="price"
                    value={editingJob.price}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-primary focus:border-primary mb-2 text-black"
                    placeholder="Price"
                  />
                  <select
                    name="status"
                    value={editingJob.status}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-primary focus:border-primary mb-2 text-white bg-primary"
                  >
                    <option value="Scheduled" className="bg-primary text-white">Scheduled</option>
                    <option value="In Progress" className="bg-primary text-white">In Progress</option>
                    <option value="Completed" className="bg-primary text-white">Completed</option>
                    <option value="Cancelled" className="bg-primary text-white">Cancelled</option>
                  </select>
                  <select
                    name="recurrence"
                    value={editingJob.recurrence}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-primary focus:border-primary mb-2 text-black bg-white"
                  >
                    <option value="One-time">One-time</option>
                    <option value="Weekly">Weekly</option>
                    <option value="Bi-weekly">Bi-weekly</option>
                    <option value="Monthly">Monthly</option>
                  </select>
                </div>
              </div>
              <div className="bg-gray-800 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                <button
                  type="button"
                  className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-primary text-base font-medium text-white hover:bg-opacity-90 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary sm:ml-3 sm:w-auto sm:text-sm"
                  onClick={handleUpdateJob}
                >
                  Save
                </button>
                <button
                  type="button"
                  className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-surface text-base font-medium text-text hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                  onClick={() => setEditingJob(null)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageJobs;
