import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import Header from '../components/Header';

const EmployeePerformanceAnalytics = () => {
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEmployeePerformanceData();
  }, []);

  const fetchEmployeePerformanceData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/analytics/employee-performance', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setAnalyticsData(response.data);
    } catch (error) {
      console.error('Error fetching employee performance data:', error);
      setError('Failed to fetch employee performance data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Employee Performance',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Jobs Completed'
        }
      }
    }
  };

  const chartData = {
    labels: analyticsData?.map(employee => employee.name) || [],
    datasets: [
      {
        label: 'Jobs Completed',
        data: analyticsData?.map(employee => employee.jobsCompleted) || [],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: 'Average Job Rating',
        data: analyticsData?.map(employee => employee.averageJobRating) || [],
        backgroundColor: 'rgba(153, 102, 255, 0.6)',
      },
    ]
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6">Employee Performance Analytics</h1>
        {loading ? (
          <p>Loading data...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : analyticsData ? (
          <div className="bg-surface p-6 rounded-lg shadow-lg">
            <Bar data={chartData} options={chartOptions} />
          </div>
        ) : (
          <p>No data available.</p>
        )}
      </main>
    </div>
  );
};

export default EmployeePerformanceAnalytics;
