import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { PlusIcon, ArrowUpOnSquareIcon, InformationCircleIcon } from '@heroicons/react/24/outline';

const Customers = () => {
  const [customer, setCustomer] = useState({
    name: '',
    email: '',
    phone: '',
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: ''
    },
    yardSize: '',
    notes: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [csvFile, setCsvFile] = useState(null);
  const [showCsvFormat, setShowCsvFormat] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setCustomer(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setCustomer(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await axios.post('http://localhost:5000/api/customers', customer, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Customer added successfully!');
      setCustomer({
        name: '',
        email: '',
        phone: '',
        address: { street: '', city: '', state: '', zipCode: '' },
        yardSize: '',
        notes: ''
      });
    } catch (error) {
      console.error('Error creating customer:', error);
      setError('Failed to add customer. Please try again.');
    }
    setLoading(false);
  };

  const handleCsvUpload = async (e) => {
    e.preventDefault();
    if (!csvFile) {
      setError('Please select a CSV file to upload.');
      return;
    }
  
    const formData = new FormData();
    formData.append('file', csvFile);
  
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/customers/import', formData, {
        headers: { 
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}` 
        }
      });
      setSuccess('Customers imported successfully!');
      setCsvFile(null);
    } catch (error) {
      console.error('Error importing customers:', error);
      setError('Failed to import customers. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl font-semibold">Add Customers</h1>
            <Link to="/manage-customers" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90">
              Manage Customers
            </Link>
          </div>
          
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">{error}</div>}
          {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative mb-4" role="alert">{success}</div>}
          
          <div className="bg-surface overflow-hidden shadow rounded-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Add New Customer</h2>
            <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="name" className="block text-sm font-medium">Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={customer.name}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                  required
                />
              </div>
              <div>
                <label htmlFor="email" className="block text-sm font-medium">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={customer.email}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                  required
                />
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium">Phone</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={customer.phone}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div>
                <label htmlFor="yardSize" className="block text-sm font-medium">Yard Size (sq ft)</label>
                <input
                  type="number"
                  id="yardSize"
                  name="yardSize"
                  value={customer.yardSize}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div>
                <label htmlFor="address.street" className="block text-sm font-medium">Street</label>
                <input
                  type="text"
                  id="address.street"
                  name="address.street"
                  value={customer.address.street}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div>
                <label htmlFor="address.city" className="block text-sm font-medium">City</label>
                <input
                  type="text"
                  id="address.city"
                  name="address.city"
                  value={customer.address.city}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div>
                <label htmlFor="address.state" className="block text-sm font-medium">State</label>
                <input
                  type="text"
                  id="address.state"
                  name="address.state"
                  value={customer.address.state}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div>
                <label htmlFor="address.zipCode" className="block text-sm font-medium">Zip Code</label>
                <input
                  type="text"
                  id="address.zipCode"
                  name="address.zipCode"
                  value={customer.address.zipCode}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div className="md:col-span-2">
                <label htmlFor="notes" className="block text-sm font-medium">Notes</label>
                <textarea
                  id="notes"
                  name="notes"
                  value={customer.notes}
                  onChange={handleChange}
                  rows="3"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                ></textarea>
              </div>
              <div className="md:col-span-2">
                <button
                  type="submit"
                  className="w-full bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center"
                  disabled={loading}
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  {loading ? 'Adding...' : 'Add Customer'}
                </button>
              </div>
            </form>
          </div>

          <div className="bg-surface overflow-hidden shadow rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Import Customers from CSV</h2>
            <form onSubmit={handleCsvUpload} className="space-y-4">
              <div>
              <label htmlFor="csvFile" className="block text-sm font-medium">Select CSV File</label>
              <input
                type="file"
                id="csvFile"
                accept=".csv"
                onChange={(e) => setCsvFile(e.target.files[0])}
                className="mt-1 block w-full"
              />
              </div>
              <button
                type="submit"
                className="w-full bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center"
                disabled={loading || !csvFile}
              >
                <ArrowUpOnSquareIcon className="h-5 w-5 mr-2" />
                {loading ? 'Importing...' : 'Import Customers'}
              </button>
            </form>
            <div className="mt-4 flex justify-center items-center">
              <button 
                className="flex items-center text-primary hover:text-primary-dark"
                onClick={() => setShowCsvFormat(true)}
              >
                <InformationCircleIcon className="h-5 w-5 mr-2" />
                CSV Format Info
              </button>
            </div>
          </div>

          <Link to="/dashboard" className="mt-6 inline-block text-primary hover:text-primary-dark">
            Back to Dashboard
          </Link>

          {showCsvFormat && (
            <div className="fixed z-10 inset-0 overflow-y-auto" aria-labelledby="modal-title" role="dialog" aria-modal="true">
              <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true"></div>
                <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
                <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
                  <div className="bg-gray-600 px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                    <h3 className="text-lg leading-6 font-medium text-gray-900" id="modal-title">CSV Format Information</h3>
                    <div className="mt-2">
                      <p className="text-sm text-gray-50">Please ensure your CSV file is formatted as follows:</p>
                      <table className="mt-2 bg-gray-500 p-2 rounded-md w-full text-center">
                        <thead>
                          <tr>
                            <th className="border px-1 py-1 text-xs">name</th>
                            <th className="border px-1 py-1 text-xs">email</th>
                            <th className="border px-1 py-1 text-xs">phone</th>
                            <th className="border px-1 py-1 text-xs">street</th>
                            <th className="border px-1 py-1 text-xs">city</th>
                            <th className="border px-1 py-1 text-xs">state</th>
                            <th className="border px-1 py-1 text-xs">zipCode</th>
                            <th className="border px-1 py-1 text-xs">yardSize</th>
                            <th className="border px-1 py-1 text-xs">notes</th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr>
                            <td className="border px-1 py-1 text-xs">John Doe</td>
                            <td className="border px-1 py-1 text-xs">john@x.com</td>
                            <td className="border px-1 py-1 text-xs">###</td>
                            <td className="border px-1 py-1 text-xs">address</td>
                            <td className="border px-1 py-1 text-xs">Anytown</td>
                            <td className="border px-1 py-1 text-xs">CA</td>
                            <td className="border px-1 py-1 text-xs">12345</td>
                            <td className="border px-1 py-1 text-xs">5000</td>
                            <td className="border px-1 py-1 text-xs">Sample note</td>
                          </tr>
                          <tr>
                            <td className="border px-1 py-1 text-xs">Jane Smith</td>
                            <td className="border px-1 py-1 text-xs">jane@x.com</td>
                            <td className="border px-1 py-1 text-xs">###</td>
                            <td className="border px-1 py-1 text-xs">456 Elm St</td>
                            <td className="border px-1 py-1 text-xs">Othertown</td>
                            <td className="border px-1 py-1 text-xs">TX</td>
                            <td className="border px-1 py-1 text-xs">67890</td>
                            <td className="border px-1 py-1 text-xs">7500</td>
                            <td className="border px-1 py-1 text-xs">Another note</td>
                          </tr>
                        </tbody>
                      </table>
                      <p className="text-sm text-gray-50 mt-2">Only the 'name' and 'email' fields are required. Other fields are optional.</p>
                    </div>
                  </div>
                  <div className="bg-gray-600 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                    <button
                      type="button"
                      className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-primary text-base font-medium text-white hover:bg-opacity-90 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary sm:ml-3 sm:w-auto sm:text-sm"
                      onClick={() => setShowCsvFormat(false)}
                    >
                      Close
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Customers;
