// Scheduling.js
import React, { useState, useEffect } from 'react';
import { fetchJobs, saveSchedule } from './api';

const Scheduling = ({ routes, setSuccessMessage }) => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadJobs();
  }, []);

  const loadJobs = async () => {
    try {
      setLoading(true);
      const jobsData = await fetchJobs();
      setJobs(jobsData);
    } catch (error) {
      console.error('Error loading jobs:', error);
      setError('Failed to load jobs. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const handleScheduleSave = async (routeId, schedule) => {
    try {
      await saveSchedule(routeId, schedule);
      setSuccessMessage('Schedule saved successfully!');
    } catch (error) {
      console.error('Error saving schedule:', error);
      setSuccessMessage('Failed to save schedule. Please try again.');
    }
  };

  if (loading) return <div>Loading jobs...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Scheduling</h2>
      <div>
        <h3>Routes</h3>
        {routes.map(route => (
          <div key={route._id}>
            <h4>{route.name}</h4>
            <ul>
              {route.customers.map(customer => (
                <li key={customer._id}>{customer.name}</li>
              ))}
            </ul>
            <button onClick={() => handleScheduleSave(route._id, { /* schedule data */ })}>
              Save Schedule
            </button>
          </div>
        ))}
      </div>
      <div>
        <h3>Jobs</h3>
        <ul>
          {jobs.map(job => (
            <li key={job._id}>{job.title}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Scheduling;
