import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';
const getHeaders = () => ({ Authorization: `Bearer ${localStorage.getItem('token')}` });

export const fetchCustomers = async () => {
  const response = await axios.get(`${API_BASE_URL}/customers`, { headers: getHeaders() });
  return response.data;
};

export const fetchRoutes = async () => {
  const response = await axios.get(`${API_BASE_URL}/routes`, { headers: getHeaders() });
  return response.data;
};


export const deleteRoute = async (routeId) => {
  try {
    await axios.delete(`${API_BASE_URL}/routes/${routeId}`, { headers: getHeaders() });
  } catch (error) {
    console.error('Error deleting route:', error);
    throw error;
  }
};

export const optimizeRoute = async (routeId) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/routes/${routeId}/optimize`, {}, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error optimizing route:', error);
    throw error;
  }
};

export const fetchJobs = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/jobs`, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error fetching jobs:', error);
    throw error;
  }
};

export const fetchCalendarEvents = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/calendar-events`, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error fetching calendar events:', error);
    throw error;
  }
};

export const updateCalendarEvent = async (eventData) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/calendar-events/${eventData.id}`, eventData, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error updating calendar event:', error);
    throw error;
  }
};

export const saveSchedule = async (routeId, schedule) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/routes/${routeId}/schedule`, schedule, { headers: getHeaders() });
    return response.data;
  } catch (error) {
    console.error('Error saving schedule:', error);
    throw error;
  }
};

export const updateRoute = async (routeId, updatedData) => {
    try {
      const response = await axios.put(`${API_BASE_URL}/routes/${routeId}`, updatedData, { headers: getHeaders() });
      return response.data;
    } catch (error) {
      console.error('Error updating route:', error);
      throw error;
    }
