import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Header from '../components/Header';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/24/outline';

const API_BASE_URL = 'http://localhost:5000/api';

const BuildRoutes = () => {
  const [routes, setRoutes] = useState({
    Monday: [],
    Tuesday: [],
    Wednesday: [],
    Thursday: [],
    Friday: [],
    Saturday: [],
    Sunday: [],
  });
  const [jobPool, setJobPool] = useState([]);
  const [selectedDay, setSelectedDay] = useState('Monday');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchRouteAndJobs = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [routesResponse, jobsResponse] = await Promise.all([
        axios.get(`${API_BASE_URL}/routes`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get(`${API_BASE_URL}/jobs/available`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      const fetchedRoutes = routesResponse.data.reduce((acc, route) => {
        acc[route.day] = route.jobs;
        return acc;
      }, {});

      setRoutes(prevRoutes => ({
        ...prevRoutes,
        ...fetchedRoutes
      }));

      setJobPool(jobsResponse.data);

    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load routes and jobs. Please try again.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchRouteAndJobs();
  }, [fetchRouteAndJobs]);

  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceList = source.droppableId;
    const destList = destination.droppableId;

    let updatedRoutes = { ...routes };
    let updatedJobPool = [...jobPool];
    let movedJob;

    if (sourceList === 'jobPool') {
      [movedJob] = updatedJobPool.splice(source.index, 1);
    } else {
      [movedJob] = updatedRoutes[sourceList].splice(source.index, 1);
    }

    if (destList === 'jobPool') {
      updatedJobPool.splice(destination.index, 0, movedJob);
    } else {
      updatedRoutes[destList].splice(destination.index, 0, movedJob);
    }

    setRoutes(updatedRoutes);
    setJobPool(updatedJobPool);

    try {
      if (destList !== 'jobPool') {
        await axios.put(`${API_BASE_URL}/routes/${destList}`, { jobs: updatedRoutes[destList] }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }
      if (sourceList !== 'jobPool') {
        await axios.put(`${API_BASE_URL}/routes/${sourceList}`, { jobs: updatedRoutes[sourceList] }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }
    } catch (error) {
      console.error('Error updating route:', error);
      setError('Failed to update route. Please try again.');
      fetchRouteAndJobs(); // Revert changes if update fails
    }
  };

  const handleCompleteRoute = async () => {
    try {
      await axios.post(`${API_BASE_URL}/routes/${selectedDay}/complete`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchRouteAndJobs(); // Refresh the data after completing the route
    } catch (error) {
      console.error('Error completing route:', error);
      setError('Failed to complete route. Please try again.');
    }
  };

  const calculateRouteRevenue = (jobs) => {
    return jobs.reduce((total, job) => total + job.price, 0);
  };

  const renderJobList = (listId, jobs) => (
    <Droppable droppableId={listId}>
      {(provided) => (
        <div
          {...provided.droppableProps}
          ref={provided.innerRef}
          className="bg-gray-800 p-4 rounded-lg min-h-[300px] shadow-lg"
        >
          <h2 className="text-xl font-bold mb-4 text-white">
            {listId === 'jobPool' ? 'Available Jobs' : `${listId} Route`}
          </h2>
          {jobs.map((job, index) => (
            <Draggable key={job._id} draggableId={job._id} index={index}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  className="bg-gray-700 p-3 mb-2 rounded-md shadow-md hover:bg-gray-600 transition-colors duration-200"
                >
                  <p className="font-bold text-white">{job.title}</p>
                  <p className="text-sm text-gray-300">{job.customer?.name}</p>
                  <p className="text-xs text-gray-400">{job.location?.address}</p>
                  <p className="text-xs text-gray-400">Revenue: ${job.price}</p>
                  <p className="text-xs text-gray-400">Type: {job.isRecurring ? `Recurring (${job.recurrencePattern})` : 'One-time'}</p>
                  <p className="text-xs text-gray-400">Last Service: {job.lastServiceDate ? new Date(job.lastServiceDate).toLocaleDateString() : 'N/A'}</p>
                  <p className="text-xs text-gray-400">Qty Served: {job.completionCount}</p>
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );

  const handleDayChange = (direction) => {
    const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    const currentIndex = days.indexOf(selectedDay);
    const newIndex = (currentIndex + direction + 7) % 7;
    setSelectedDay(days[newIndex]);
  };

  if (loading) {
    return <div className="text-center mt-8 text-white">Loading...</div>;
  }

  if (error) {
    return (
      <div className="text-center mt-8 text-red-500">
        <p>{error}</p>
        <button
          onClick={fetchRouteAndJobs}
          className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4 hover:bg-blue-600 transition-colors duration-200"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6">Build Routes</h1>
        <div className="flex justify-between items-center mb-6">
          <button onClick={() => handleDayChange(-1)} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors duration-200">
            <ChevronLeftIcon className="h-5 w-5" />
          </button>
          <h2 className="text-2xl font-semibold">{selectedDay}</h2>
          <button onClick={() => handleDayChange(1)} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors duration-200">
            <ChevronRightIcon className="h-5 w-5" />
          </button>
        </div>
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {renderJobList(selectedDay, routes[selectedDay] || [])}
            {renderJobList('jobPool', jobPool)}
          </div>
        </DragDropContext>
        <div className="mt-6 flex justify-between items-center">
          <button
            onClick={handleCompleteRoute}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
          >
            Complete Route
          </button>
          <p className="text-xl font-bold">
            Total Route Revenue: ${calculateRouteRevenue(routes[selectedDay] || []).toFixed(2)}
          </p>
        </div>
      </main>
    </div>
  );
};

export default BuildRoutes;
