// src/pages/Schedule.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Schedule = () => {
  const [schedules, setSchedules] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [newSchedule, setNewSchedule] = useState({
    customer: '',
    job: '',
    date: '',
    time: '',
    recurrence: 'none',
  });

  useEffect(() => {
    fetchSchedules();
    fetchCustomers();
  }, []);

  const fetchSchedules = async () => {
    try {
      const response = await fetch('/api/schedules', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` // Assume token is stored in localStorage
        }
      });
      if (response.ok) {
        const data = await response.json();
        setSchedules(data);
      } else {
        console.error('Failed to fetch schedules');
      }
    } catch (error) {
      console.error('Error fetching schedules:', error);
    }
  };

  const fetchCustomers = async () => {
    try {
      const response = await fetch('/api/customers', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setCustomers(data);
      } else {
        console.error('Failed to fetch customers');
      }
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const handleInputChange = (e) => {
    setNewSchedule({ ...newSchedule, [e.target.name]: e.target.value });
  };

  const handleAddSchedule = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/schedules', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(newSchedule)
      });
      if (response.ok) {
        const data = await response.json();
        setSchedules([...schedules, data]);
        setNewSchedule({ customer: '', job: '', date: '', time: '', recurrence: 'none' });
      } else {
        console.error('Failed to add schedule');
      }
    } catch (error) {
      console.error('Error adding schedule:', error);
    }
  };

  const handleDeleteSchedule = async (id) => {
    try {
      const response = await fetch(`/api/schedules/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      if (response.ok) {
        setSchedules(schedules.filter(schedule => schedule._id !== id));
      } else {
        console.error('Failed to delete schedule');
      }
    } catch (error) {
      console.error('Error deleting schedule:', error);
    }
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(schedules);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);
    setSchedules(items);
    // Here you might want to update the order in the backend as well
  };

  const handleMoveAllSchedules = async (days) => {
    try {
      const response = await fetch('/api/schedules/move', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ days })
      });
      if (response.ok) {
        const updatedSchedules = await response.json();
        setSchedules(updatedSchedules);
      } else {
        console.error('Failed to move schedules');
      }
    } catch (error) {
      console.error('Error moving schedules:', error);
    }
  };

  return (
    <div className="min-h-screen bg-background text-text p-8">
      <h1 className="text-3xl font-bold mb-8">Scheduling</h1>

      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Add New Schedule</h2>
        <form onSubmit={handleAddSchedule} className="space-y-4">
          <select
            name="customer"
            value={newSchedule.customer}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select Customer</option>
            {customers.map(customer => (
              <option key={customer._id} value={customer._id}>{customer.name}</option>
            ))}
          </select>
          <input
            type="text"
            name="job"
            value={newSchedule.job}
            onChange={handleInputChange}
            placeholder="Job Description"
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="date"
            name="date"
            value={newSchedule.date}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="time"
            name="time"
            value={newSchedule.time}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
          <select
            name="recurrence"
            value={newSchedule.recurrence}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
          >
            <option value="none">No Recurrence</option>
            <option value="weekly">Weekly</option>
            <option value="biweekly">Every Two Weeks</option>
            <option value="monthly">Monthly</option>
          </select>
          <button type="submit" className="bg-primary text-white px-4 py-2 rounded">Add Schedule</button>
        </form>
      </div>

      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Manage Schedules</h2>
        <div className="space-x-4 mb-4">
          <button onClick={() => handleMoveAllSchedules(1)} className="bg-secondary text-white px-4 py-2 rounded">
            Move All Forward 1 Day
          </button>
          <button onClick={() => handleMoveAllSchedules(7)} className="bg-secondary text-white px-4 py-2 rounded">
            Move All Forward 1 Week
          </button>
        </div>
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="schedules">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef} className="space-y-4">
                {schedules.map((schedule, index) => (
                  <Draggable key={schedule._id} draggableId={schedule._id} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-surface p-4 rounded shadow flex justify-between items-center"
                      >
                        <div>
                          <p className="font-semibold">{schedule.customer.name}</p>
                          <p>{schedule.job}</p>
                          <p>{new Date(schedule.date).toLocaleDateString()} at {schedule.time}</p>
                          <p>Recurrence: {schedule.recurrence}</p>
                        </div>
                        <button
                          onClick={() => handleDeleteSchedule(schedule._id)}
                          className="bg-red-500 text-white px-2 py-1 rounded"
                        >
                          Delete
                        </button>
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      </div>

      <Link to="/dashboard" className="text-primary hover:text-primary-dark">
        Back to Dashboard
      </Link>
    </div>
  );
};

export default Schedule;
