import React, { useState, useEffect, useCallback, memo } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useNavigate } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin, { Draggable as FcDraggable } from '@fullcalendar/interaction';
import CustomerMap from '../../components/CustomerMap';
import { fetchJobs, fetchRoutes, saveRoute, deleteRoute, updateJob, updateRoute } from './api';
import Header from '../../components/Header';
import ErrorBoundary from '../../components/ErrorBoundary';

const MemoizedFullCalendar = memo(FullCalendar);

const BuildRoutes = () => {
  const [jobs, setJobs] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [events, setEvents] = useState([]);
  const [activeTab, setActiveTab] = useState('routes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [selectedEvent, setSelectedEvent] = useState(null);
  const navigate = useNavigate();

  const fetchData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      let jobsData = JSON.parse(localStorage.getItem('jobs')) || [];
      let routesData = JSON.parse(localStorage.getItem('routes')) || [];
  
      if (jobsData.length === 0 || routesData.length === 0) {
        [jobsData, routesData] = await Promise.all([fetchJobs(), fetchRoutes()]);
        localStorage.setItem('jobs', JSON.stringify(jobsData));
        localStorage.setItem('routes', JSON.stringify(routesData));
      }
  
      setJobs(jobsData);
      setRoutes(routesData);
      generateEvents(jobsData, routesData);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const generateEvents = (jobsData, routesData) => {
    const jobEvents = (jobsData || []).map(job => ({
      id: job?._id || 'unknown-id',
      title: job?.title || 'No Title',
      start: job?.scheduledDate ? new Date(job.scheduledDate) : null,
      end: job?.scheduledEndDate ? new Date(job.scheduledEndDate) : null,
      color: '#4CAF50',
      extendedProps: { type: 'job', jobId: job?._id || 'unknown-id' }
    })).filter(event => event.start && event.end);
  
    const routeEvents = (routesData || []).flatMap(route => 
      (route.jobs || []).map(jobItem => ({
        id: `${route?._id}-${jobItem.job?._id}`,
        title: `${route?.name || 'Unnamed Route'}: ${jobItem.job?.title || 'No Title'}`,
        start: jobItem?.scheduledDate ? new Date(jobItem.scheduledDate) : null,
        end: jobItem?.scheduledEndDate ? new Date(jobItem.scheduledEndDate) : null,
        color: '#2196F3',
        extendedProps: { type: 'route', routeId: route?._id || 'unknown-id', jobId: jobItem.job?._id || 'unknown-id' }
      }))
    ).filter(event => event.start && event.end);
  
    setEvents([...jobEvents, ...routeEvents]);
  };

  const initializeExternalEvents = useCallback(() => {
    const draggableElems = document.querySelectorAll('.fc-event');
    draggableElems.forEach(elem => {
      const eventData = elem.getAttribute('data-event');
      if (eventData) {
        new FcDraggable(elem, {
          eventData: function() {
            return JSON.parse(eventData);
          }
        });
      }
    });
  }, []);

  useEffect(() => {
    if (activeTab === 'calendar') {
      setTimeout(initializeExternalEvents, 0);
    }
  }, [activeTab, jobs, routes, initializeExternalEvents]);

  const handleEventDrop = async (info) => {
    const { event } = info;
    console.log('Event dropped:', event);
    
    const job = jobs.find(j => j?._id === event.id);
    if (job && job.route) {
      console.error('This job is assigned to a route and cannot be moved on the calendar');
      info.revert();
      setError('This job is assigned to a route. Please remove it from the route before rescheduling.');
      return;
    }
  
    try {
      await handleEventUpdate(event);
      setSuccessMessage('Event updated successfully!');
    } catch (error) {
      console.error('Error updating event:', error);
      setError('Failed to update event. Please try again.');
      info.revert();
    }
  };

  const handleEventReceive = async (info) => {
    const { event } = info;
    console.log('Event received:', event);
  
    const jobId = event.extendedProps.jobId;
    const job = jobs.find(j => j?._id === jobId);
  
    if (job && job.route) {
      console.error('This job is already assigned to a route');
      info.revert();
      setError('This job is assigned to a route. Please remove it from the route before scheduling on the calendar.');
      return;
    }
  
    const startDate = event.start || new Date();
    const endDate = event.end || new Date(startDate.getTime() + 60 * 60 * 1000);
    
    const eventData = {
      id: event.id,
      title: event.title,
      start: startDate,
      end: endDate,
      extendedProps: event.extendedProps || { type: 'job' }
    };
  
    if (eventData.extendedProps.type === 'job' || eventData.extendedProps.type === 'route') {
      try {
        await handleEventUpdate(eventData);
        setEvents(prevEvents => [...prevEvents, eventData]);
        setSuccessMessage('Event scheduled successfully!');
      } catch (error) {
        console.error('Error scheduling event:', error);
        setError('Failed to schedule event. Please try again.');
        info.revert();
      }
    } else {
      console.error('Unrecognized event type:', eventData);
      info.revert();
    }
  };
  
  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;
  
    const jobId = result.draggableId;
  
    if (source.droppableId === 'jobPool' && destination.droppableId.startsWith('route-')) {
      const routeId = destination.droppableId.split('-')[1];
      await moveJobToRoute(jobId, routeId);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'jobPool') {
      const routeId = source.droppableId.split('-')[1];
      await moveJobToPool(jobId, routeId);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
      const sourceRouteId = source.droppableId.split('-')[1];
      const destRouteId = destination.droppableId.split('-')[1];
      await moveJobBetweenRoutes(jobId, sourceRouteId, destRouteId);
    }
  };

  const updateLocalStorage = (jobs, routes) => {
    localStorage.setItem('jobs', JSON.stringify(jobs));
    localStorage.setItem('routes', JSON.stringify(routes));
  };

  const moveJobToRoute = async (jobId, routeId) => {
  try {
    console.log('Moving job', jobId, 'to route', routeId);
    
    const job = jobs.find(j => j?._id === jobId);
    
    const updatedRoute = await updateRoute(routeId, { 
      $addToSet: { jobs: { job: jobId, scheduledDate: job?.scheduledDate, scheduledEndDate: job?.scheduledEndDate } }
    });

    const updatedJob = await updateJob(jobId, { route: routeId });

    const updatedRoutes = routes.map(r => r._id === routeId ? updatedRoute : r);
    const updatedJobs = jobs.map(j => j._id === jobId ? { ...j, route: routeId } : j);

    setRoutes(updatedRoutes);
    setJobs(updatedJobs);
    updateLocalStorage(updatedJobs, updatedRoutes);
    
    setSuccessMessage('Job added to route successfully!');
  } catch (error) {
    console.error('Error moving job to route:', error);
    setError('Failed to move job to route. Please try again.');
  }
};

const moveJobToPool = async (jobId, routeId) => {
  try {
    const updatedRoute = await updateRoute(routeId, { $pull: { jobs: { job: jobId } } });
    const updatedJob = await updateJob(jobId, { route: null });

    const updatedRoutes = routes.map(r => r._id === routeId ? updatedRoute : r);
    const updatedJobs = jobs.map(j => j._id === jobId ? updatedJob : j);

    setRoutes(updatedRoutes);
    setJobs(updatedJobs);
    updateLocalStorage(updatedJobs, updatedRoutes);

    setSuccessMessage('Job removed from route successfully!');
  } catch (error) {
    console.error('Error moving job to pool:', error);
    setError('Failed to remove job from route. Please try again.');
  }
};

const moveJobBetweenRoutes = async (jobId, sourceRouteId, destRouteId) => {
  try {
    const job = jobs.find(j => j?._id === jobId);
    await updateRoute(sourceRouteId, { $pull: { jobs: { job: jobId } } });
    const updatedDestRoute = await updateRoute(destRouteId, { 
      $addToSet: { jobs: { job: jobId, scheduledDate: job?.scheduledDate, scheduledEndDate: job?.scheduledEndDate } } 
    });
    const updatedJob = await updateJob(jobId, { route: destRouteId });

    const updatedRoutes = routes.map(route => {
      if (route._id === sourceRouteId) return { ...route, jobs: route.jobs.filter(j => j.job !== jobId) };
      if (route._id === destRouteId) return updatedDestRoute;
      return route;
    });

    const updatedJobs = jobs.map(j => j._id === jobId ? updatedJob : j);

    setRoutes(updatedRoutes);
    setJobs(updatedJobs);
    updateLocalStorage(updatedJobs, updatedRoutes);

    setSuccessMessage('Job moved between routes successfully!');
  } catch (error) {
    console.error('Error moving job between routes:', error);
    setError('Failed to move job between routes. Please try again.');
  }
};

  const handleEditRouteName = async (routeId, newName) => {
    try {
      const updatedRoute = await updateRoute(routeId, { name: newName });
      setRoutes(prevRoutes => prevRoutes.map(r => r._id === routeId ? updatedRoute : r));
      setSuccessMessage('Route name updated successfully!');
    } catch (error) {
      console.error('Error updating route name:', error);
      setError('Failed to update route name. Please try again.');
    }
  };

  const handleMapView = (routeId) => {
    navigate(`/route-map/${routeId}`);
  };

  const handleAddRoute = async () => {
    try {
      const newRoute = await saveRoute({ name: `New Route ${routes.length + 1}`, customers: [] });
      setRoutes(prevRoutes => [...prevRoutes, newRoute]);
      setSuccessMessage('New route added successfully!');
    } catch (error) {
      console.error('Error adding new route:', error);
      setError('Failed to add new route. Please try again.');
    }
  };

  const handleDeleteRoute = async (routeId) => {
    try {
      await deleteRoute(routeId);
      setRoutes(prevRoutes => prevRoutes.filter(route => route._id !== routeId));
      setSuccessMessage('Route deleted successfully!');
    } catch (error) {
      console.error('Error deleting route:', error);
      setError('Failed to delete route. Please try again.');
    }
  };

  const handleEventClick = (info) => {
    const { event } = info;
    if (event.id && event.title && event.start && event.end && event.extendedProps.type) {
      setSelectedEvent({
        id: event.id,
        title: event.title,
        start: event.start,
        end: event.end,
        extendedProps: event.extendedProps
      });
    } else {
      console.error('Event is missing necessary properties:', event);
    }
  };

  const handleEventResize = async (info) => {
    const { event } = info;
    console.log('Event resized:', event);
    
    const job = jobs.find(j => j?._id === event.id);
    if (job && job.route) {
      console.error('This job is assigned to a route and cannot be resized on the calendar');
      info.revert();
      setError('This job is assigned to a route. Please remove it from the route before resizing.');
      return;
    }
  
    try {
      await handleEventUpdate(event);
      setSuccessMessage('Event resized successfully!');
    } catch (error) {
      console.error('Error resizing event:', error);
      setError('Failed to resize event. Please try again.');
      info.revert();
    }
  };

  const handleRemoveJobFromRoute = async (jobId, routeId) => {
    try {
      const updatedRoute = await updateRoute(routeId, { $pull: { jobs: { job: jobId } } });
      const updatedJob = await updateJob(jobId, { route: null });
  
      setRoutes(prevRoutes => prevRoutes.map(r => r._id === routeId ? updatedRoute : r));
      setJobs(prevJobs => prevJobs.map(j => j._id === jobId ? updatedJob : j));
  
      setSuccessMessage('Job removed from route successfully!');
    } catch (error) {
      console.error('Error removing job from route:', error);
      setError('Failed to remove job from route. Please try again.');
    }
  };

  const handleEventUpdate = async (event) => {
    const job = jobs.find(j => j?._id === event.id);
    if (job && job.route) {
      throw new Error('This job is assigned to a route and cannot be updated on the calendar');
    }
  
    if (event.extendedProps.type === 'job') {
      await updateJob(event.id, {
        title: event.title,
        scheduledDate: event.start,
        scheduledEndDate: event.end
      });
      setJobs(prevJobs => prevJobs.map(job =>
        job?._id === event.id ? { ...job, title: event.title, scheduledDate: event.start, scheduledEndDate: event.end } : job
      ));
    } else if (event.extendedProps.type === 'route') {
      await updateRoute(event.extendedProps.routeId, {
        name: event.title,
        scheduledDate: event.start,
        scheduledEndDate: event.end
      });
      setRoutes(prevRoutes => prevRoutes.map(route =>
        route?._id === event.extendedProps.routeId ? { ...route, name: event.title, scheduledDate: event.start, scheduledEndDate: event.end } : route
      ));
    } else {
      throw new Error('Unrecognized event type');
    }
    setEvents(prevEvents =>
      prevEvents.map(e => e.id === event.id ? { ...e, title: event.title, start: event.start, end: event.end } : e)
    );
  };

  const handleRemoveEvent = async (event) => {
    if (event.extendedProps.type === 'job') {
      try {
        await updateJob(event.id, { scheduledDate: null, scheduledEndDate: null });
        setJobs(prevJobs => 
          prevJobs.map(job => 
            job?._id === event.id ? { ...job, scheduledDate: null, scheduledEndDate: null } : job
          )
        );
        setEvents(prevEvents => prevEvents.filter(e => e.id !== event.id));
        setSuccessMessage('Job removed from calendar and moved back to pool successfully!');
      } catch (error) {
        console.error('Error removing job from calendar:', error);
        setError('Failed to remove job. Please try again.');
      }
    } else if (event.extendedProps.type === 'route') {
      try {
        const routeId = event.extendedProps.routeId;
        const updatedRoute = await updateRoute(routeId, { scheduledDate: null, scheduledEndDate: null });
        setRoutes(prevRoutes => prevRoutes.map(route => 
          route?._id === routeId ? { ...route, scheduledDate: null, scheduledEndDate: null } : route
        ));
        setEvents(prevEvents => prevEvents.filter(e => e.id !== event.id));
        setSuccessMessage('Route removed from calendar and moved back to pool successfully!');
      } catch (error) {
        console.error('Error removing route from calendar:', error);
        setError('Failed to remove route. Please try again.');
      }
    }
  };
  
  const EventModal = ({ event, onClose, onSave, onRemove }) => {
    const [title, setTitle] = useState(event.title);
    const [start, setStart] = useState(event.start);
    const [end, setEnd] = useState(event.end);

    const handleSave = () => {
      onSave({ ...event, title, start, end });
      onClose();
    };

    const handleRemove = () => {
      onRemove(event);
      onClose();
    };

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white p-4 rounded-lg">
          <h2 className="text-xl font-bold mb-4">Edit Event</h2>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full mb-2 p-2 border rounded"
          />
          <input
            type="datetime-local"
            value={start ? start.toISOString().slice(0, 16) : ''}
            onChange={(e) => setStart(new Date(e.target.value))}
            className="w-full mb-2 p-2 border rounded"
          />
          <input
            type="datetime-local"
            value={end ? end.toISOString().slice(0, 16) : ''}
            onChange={(e) => setEnd(new Date(e.target.value))}
            className="w-full mb-2 p-2 border rounded"
          />
          <div className="flex justify-end">
            <button onClick={onClose} className="mr-2 px-4 py-2 bg-gray-200 rounded">Cancel</button>
            <button onClick={handleSave} className="mr-2 px-4 py-2 bg-blue-500 text-white rounded">Save</button>
            <button onClick={handleRemove} className="px-4 py-2 bg-red-500 text-white rounded">Remove</button>
          </div>
        </div>
      </div>
    );
  };

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-background text-gray-800">
        <Header />
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <div className="px-4 py-6 sm:px-0">
            <h1 className="text-3xl font-bold mb-6">Route Management</h1>
            
            {successMessage && (
              <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4" role="alert">
                <p>{successMessage}</p>
              </div>
            )}
    
            <div className="mb-6">
              <div className="hidden sm:block">
                <nav className="flex space-x-4" aria-label="Tabs">
                  {['routes', 'calendar', 'map'].map((tab) => (
                    <button
                      key={tab}
                      onClick={() => setActiveTab(tab)}
                      className={`${
                        activeTab === tab
                          ? 'bg-primary text-white'
                          : 'text-text hover:text-white hover:bg-primary'
                      } px-3 py-2 font-medium text-sm rounded-md`}
                    >
                      {tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                  ))}
                </nav>
              </div>
            </div>
    
            <div className="bg-surface p-6 rounded-lg shadow-lg">
              {activeTab === 'routes' && (
                <DragDropContext onDragEnd={onDragEnd}>
                  <div className="flex justify-between items-center mb-4">
                    <h2 className="text-2xl font-semibold">Routes</h2>
                    <button
                      onClick={handleAddRoute}
                      className="bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90"
                    >
                      Add New Route
                    </button>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {routes.map(route => (
                      <Droppable key={route?._id} droppableId={`route-${route?._id}`}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            className="bg-gray-100 p-4 rounded-lg text-black"
                          >
                            <div className="flex justify-between items-center mb-2">
                              <h3 className="font-semibold">{route?.name}</h3>
                              <div>
                                <button
                                  onClick={() => {
                                    const newName = prompt('Enter new route name:', route?.name);
                                    if (newName) handleEditRouteName(route?._id, newName);
                                  }}
                                  className="text-primary hover:text-primary-dark mr-2"
                                >
                                  Edit Name
                                </button>
                                <button
                                  onClick={() => handleMapView(route?._id)}
                                  className="text-blue-600 hover:text-blue-800 mr-2"
                                >
                                  View Map
                                </button>
                                <button
                                  onClick={() => handleDeleteRoute(route?._id)}
                                  className="text-red-600 hover:text-red-800"
                                >
                                  Delete
                                </button>
                              </div>
                            </div>
                            {route.jobs && route.jobs.map((jobItem, index) => {
                              const job = jobs.find(j => j?._id === jobItem.job);
                              if (!job) return null;
                              return (
                                <Draggable key={job?._id} draggableId={job?._id} index={index}>
                                  {(provided) => (
                                    <div
                                      ref={provided.innerRef}
                                      {...provided.draggableProps}
                                      {...provided.dragHandleProps}
                                      className="bg-white p-2 mb-2 rounded shadow"
                                    >
                                      <h4 className="font-semibold">{job?.title}</h4>
                                      <p>{job?.description}</p>
                                      <p>Status: {job?.status}</p>
                                      <p>Customer: {job.customer?.name}</p>
                                      <p>Scheduled: {jobItem?.scheduledDate ? new Date(jobItem.scheduledDate).toLocaleString() : 'Not scheduled'}</p>
                                      <button
                                        onClick={() => handleRemoveJobFromRoute(job?._id, route?._id)}
                                        className="mt-2 bg-red-500 text-white px-2 py-1 rounded text-sm"
                                      >
                                        Remove from Route
                                      </button>
                                    </div>
                                  )}
                                </Draggable>
                              );
                            })}
                            {provided.placeholder}
                          </div>
                        )}
                      </Droppable>
                    ))}
                  </div>
                  <Droppable droppableId="jobPool">
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                        className="mt-6 bg-gray-200 p-4 rounded-lg text-black"
                      >
                        <h3 className="font-semibold mb-2">Unassigned Jobs</h3>
                        {jobs.filter(job => !job.route).map((job, index) => (
                          <Draggable key={job?._id} draggableId={job?._id} index={index}>
                            {(provided) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                className="bg-white p-2 mb-2 rounded shadow fc-event cursor-move"
                                data-event={JSON.stringify({
                                  id: job?._id,
                                  title: job?.title,
                                  duration: '01:00',
                                  extendedProps: { type: 'job', jobId: job?._id }
                                })}
                              >
                                <h4 className="font-semibold">{job?.title}</h4>
                                <p>{job?.description}</p>
                                <p>Status: {job?.status}</p>
                                <p>Customer: {job.customer?.name}</p>
                                <p>Scheduled: {job?.scheduledDate ? new Date(job.scheduledDate).toLocaleString() : 'Not scheduled'}</p>
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    )}
                  </Droppable>
                </DragDropContext>
              )}
    
              {activeTab === 'calendar' && (
                <div className="calendar-container bg-white rounded-lg shadow-lg p-6">
                  <h2 className="text-2xl font-semibold mb-6 text-gray-800">Schedule</h2>
                  <div className="flex flex-col md:flex-row gap-6">
                    <div className="md:w-1/4">
                      <div id="external-events" className="bg-gray-100 rounded-lg p-4">
                        <h4 className="font-semibold mb-3 text-lg text-gray-700">Draggable Jobs</h4>
                        <div className="space-y-2">
                          {jobs.filter(job => !job.route).map(job => (
                            <div
                              key={job?._id}
                              className="fc-event bg-white p-3 rounded shadow-sm cursor-move border-l-4 border-blue-500 hover:bg-gray-50 transition duration-150"
                              data-event={JSON.stringify({
                                id: job?._id,
                                title: job?.title,
                                duration: '01:00',
                                extendedProps: { type: 'job', jobId: job?._id }
                              })}
                            >
                              <p className="font-medium text-gray-800">{job?.title}</p>
                            </div>
                          ))}
                        </div>
                        <h4 className="font-semibold mb-3 mt-6 text-lg text-gray-700">Draggable Routes</h4>
                        <div className="space-y-2">
                          {routes.filter(route => !route.scheduledDate).map(route => (
                            <div
                              key={route?._id}
                              className="fc-event bg-white p-3 rounded shadow-sm cursor-move border-l-4 border-green-500 hover:bg-gray-50 transition duration-150"
                              data-event={JSON.stringify({
                                id: route?._id,
                                title: route?.name,
                                duration: '01:00',
                                extendedProps: { type: 'route', routeId: route?._id }
                              })}
                            >
                              <p className="font-medium text-gray-800">{route?.name}</p>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                    <div className="md:w-3/4">
                      <div className="bg-white rounded-lg shadow-md overflow-hidden">
                        <MemoizedFullCalendar
                          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                          initialView="dayGridMonth"
                          headerToolbar={{
                            left: 'prev,next today',
                            center: 'title',
                            right: 'dayGridMonth,timeGridWeek,timeGridDay'
                          }}
                          editable={true}
                          droppable={true}
                          events={events}
                          eventDrop={handleEventDrop}
                          eventResize={handleEventResize}
                          eventReceive={handleEventReceive}
                          eventClick={handleEventClick}
                          height="auto"
                          aspectRatio={1.8}
                          dayMaxEvents={3}
                          eventTimeFormat={{
                            hour: 'numeric',
                            minute: '2-digit',
                            meridiem: 'short'
                          }}
                          eventClassNames="rounded-md shadow-sm"
                          slotLabelFormat={{
                            hour: 'numeric',
                            minute: '2-digit',
                            hour12: true
                          }}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              )}
    
              {activeTab === 'map' && (
                <div className="map-container">
                  <h2 className="text-2xl font-semibold mb-4">Route Maps</h2>
                  {routes.map(route => (
                    <div key={route?._id} className="mb-8">
                      <h3 className="text-xl font-semibold mb-2">{route?.name}</h3>
                      <CustomerMap 
                        jobs={route.jobs.map(jobItem => jobs.find(j => j?._id === jobItem.job)).filter(Boolean)}
                        route={route}
                      />
                    </div>
                  ))}
                </div>
              )}

            </div>
          </div>
        </main>
        {selectedEvent && (
          <EventModal
            event={selectedEvent}
            onClose={() => setSelectedEvent(null)}
            onSave={handleEventUpdate}
            onRemove={handleRemoveEvent}
          />
        )}
      </div>
    </ErrorBoundary>
  );
};

export default BuildRoutes;
