// src/pages/LoginSuccess.js

import React, { useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const LoginSuccess = () => {
  const { setUser } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUpdatedUser = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/auth/get-current-user`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Include token if required
          },
        });

        const data = await response.json();

        if (data.user) {
          setUser(data.user);
          navigate('/dashboard');
        } else {
          console.error('Failed to fetch updated user data');
          navigate('/dashboard'); // Redirect even if failed
        }
      } catch (error) {
        console.error('Error fetching updated user:', error);
        navigate('/dashboard'); // Redirect even if failed
      }
    };

    fetchUpdatedUser();
  }, [setUser, navigate]);

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gray-900 text-white">
      <h1 className="text-4xl font-bold mb-4">Thank You for Upgrading!</h1>
      <p className="text-lg">Redirecting to your dashboard...</p>
    </div>
  );
};

export default LoginSuccess;
