import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import {
  PlusIcon, UsersIcon, MapIcon, DocumentIcon,
  ArrowPathIcon, ChevronLeftIcon, ChevronRightIcon, EyeIcon, EyeSlashIcon
} from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { Line, Bar } from 'react-chartjs-2';
import { fetchAnalytics } from '../api/dashboardApi';
import TrialTimer from '../components/TrialTimer';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import { enUS } from 'date-fns/locale';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler
);

ChartJS.defaults.locale = enUS;

const Dashboard = () => {
  const { user } = useAuth();
  const [timeRange, setTimeRange] = useState('30');
  const [analytics, setAnalytics] = useState({
    revenueOverTime: null,
    revenueByJobType: null,
    averageDurationAndRate: null,
    pastDueRecurring: null
  });
  const [loading, setLoading] = useState(true);
  const [showInsights, setShowInsights] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardAnalytics();
  }, [timeRange]);

  useEffect(() => {
    console.log("Fetching dashboard analytics for timeRange:", timeRange); // Debug the timeRange
    fetchDashboardAnalytics();
  }, [timeRange]);
  
  const fetchDashboardAnalytics = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetchAnalytics(timeRange);
      console.log("API response:", response);
  
      // Fallbacks in case API response is missing keys or has unexpected null/undefined values
      setAnalytics({
        revenueOverTime: {
          labels: response?.revenueOverTime?.labels || [],
          data: response?.revenueOverTime?.data || [],
        },
        revenueByJobType: {
          oneTime: response?.revenueByJobType?.oneTime || 0,
          recurring: response?.revenueByJobType?.recurring || 0,
        },
        averageDurationAndRate: {
          averageDuration: response?.averageDurationAndRate?.averageDuration || 0,
          hourlyRate: response?.averageDurationAndRate?.hourlyRate || 0,
        },
        pastDueRecurring: {
          count: response?.pastDueRecurring?.count || 0,
        },
      });
    } catch (error) {
      console.error('Error fetching analytics:', error);
      setError('Failed to load analytics data. Please try again later.');
    }
    setLoading(false);
  };
  
  
  
  const sanitizeData = (data) => {
    if (!Array.isArray(data)) return [];
    return data.filter(value => typeof value === 'number');
  };

  const renderRevenueOverTime = () => {
    const revenueData = analytics?.revenueOverTime?.data ?? [];
    const revenueLabels = analytics?.revenueOverTime?.labels ?? [];
  
    // Ensure revenueData is an array and contains only valid numbers
    const cleanedData = Array.isArray(revenueData) ? revenueData.filter(value => typeof value === 'number') : [];
  
    // Ensure revenueLabels is an array and matches cleanedData length
    const cleanedLabels = Array.isArray(revenueLabels) && revenueLabels.length === cleanedData.length
      ? revenueLabels
      : cleanedData.map((_, i) => `Label ${i + 1}`);
  
    if (cleanedData.length === 0) {
      return <p>No revenue data available</p>;
    }
  
    return (
      <Line
        data={{
          labels: cleanedLabels,
          datasets: [{
            label: 'Revenue',
            data: cleanedData,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1
          }],
        }}
        options={{
          responsive: true,
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day',
                displayFormats: {
                  day: 'MMM d',
                },
              },
            },
            y: {
              beginAtZero: true,
              ticks: {
                callback: (value) => value.toLocaleString(), // Ensure proper number formatting
              },
            },
          },
        }}
      />
    );
  };
  
  
  
  
  const renderRevenueByJobType = () => {
    console.log("Rendering Revenue By Job Type", analytics?.revenueByJobType);
  
    // Ensure the values are numbers and fallback to 0 if they're not valid
    const oneTimeRevenue = Number(analytics?.revenueByJobType?.oneTime) || 0;
    const recurringRevenue = Number(analytics?.revenueByJobType?.recurring) || 0;
  
    if (oneTimeRevenue === 0 && recurringRevenue === 0) {
      return <p>No job type revenue data available</p>;
    }
  
    return (
      <Bar
        data={{
          labels: ['One-Time', 'Recurring'],
          datasets: [{
            label: 'Revenue',
            data: [oneTimeRevenue, recurringRevenue],
            backgroundColor: ['#FF6384', '#36A2EB'],
          }],
        }}
        options={{
          responsive: true,
          scales: {
            y: {
              beginAtZero: true,
              ticks: {
                // Add safe formatting to avoid recursion errors
                callback: (value) => {
                  return typeof value === 'number' ? value.toLocaleString() : '0';
                },
              },
            },
          },
        }}
      />
    );
  };
  
  
  
  
  const renderAnalytics = () => {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Revenue Over Time */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg font-medium text-white">Revenue Over Time</h3>
          {renderRevenueOverTime()}
        </div>
  
        {/* One-Time vs Recurring Revenue */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg font-medium text-white">One-Time vs Recurring Revenue</h3>
          {renderRevenueByJobType()}
        </div>
  
        {/* Average Duration & Hourly Rate */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg font-medium text-white">Avg. Duration & Hourly Rate</h3>
          <div className="mt-2">
            <p className="text-xl text-white">Duration: {analytics?.averageDurationAndRate?.averageDuration?.toFixed(2) || 'N/A'} mins</p>
            <p className="text-xl text-white">Hourly Rate: ${analytics?.averageDurationAndRate?.hourlyRate?.toFixed(2) || 'N/A'}</p>
          </div>
        </div>
  
        {/* Past Due Recurring Jobs */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg font-medium text-white">Past Due Recurring Jobs</h3>
          <p className="text-white">{analytics?.pastDueRecurring?.count || 0} jobs are past due</p>
        </div>
      </div>
    );
  };
  

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {user?.subscriptionTier === 'Free' && (
          <div className="flex flex-col sm:flex-row items-center justify-between mb-6">
            <TrialTimer trialEndDate={user.trialEndDate} />
            <Link
              to="/pricing"
              className="mt-4 sm:mt-0 bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600"
            >
              Upgrade Now
            </Link>
          </div>
        )}

        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user?.name || 'User'}</h1>
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            className="bg-gray-800 text-white px-4 py-2 rounded-md"
          >
            <option value="7">Past 7 days</option>
            <option value="30">Past 30 days</option>
            <option value="90">Past 90 days</option>
            <option value="365">Past Year</option>
          </select>
        </div>

        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" />
          </div>
        </div>

        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">Dashboard Insights</h2>
          <button onClick={() => setShowInsights(!showInsights)} className="text-white hover:text-gray-400">
            {showInsights ? <EyeSlashIcon className="h-6 w-6" /> : <EyeIcon className="h-6 w-6" />}
          </button>
        </div>

        {showInsights && (
          <>
            {loading ? (
              <div className="text-center py-12">Loading dashboard data...</div>
            ) : error ? (
              <div className="text-center py-12 text-red-500">{error}</div>
            ) : (
              renderAnalytics()
            )}
          </>
        )}
      </main>
    </div>
  );
};

const QuickActionButton = ({ to, icon: Icon, text }) => (
  <Link
    to={to}
    className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600 flex items-center justify-center"
  >
    <Icon className="h-5 w-5 mr-2" />
    {text}
  </Link>
);

export default Dashboard;
