import React, { useState, useEffect } from 'react';
import { fetchWithAuth } from '../components/utils';
import Header from '../components/Header';

const RouteAssignments = () => {
  const [routes, setRoutes] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRoutes = async () => {
      try {
        const response = await fetchWithAuth('/routes');
        setRoutes(response.routes);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching routes:', err);
        setError('Failed to fetch routes. Please try again.');
        setLoading(false);
      }
    };

    fetchRoutes();
  }, []);

  if (loading) {
    return <div className="text-center mt-8">Loading...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6">Route Assignments</h1>
        {Object.entries(routes).map(([day, dayRoutes]) => (
          <div key={day} className="mb-8">
            <h2 className="text-2xl font-semibold mb-4">{day}</h2>
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Route</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Assigned To</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Jobs</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {dayRoutes.map((route, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap">{`Route ${index + 1}`}</td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {route.assignedTo ? `Employee: ${route.assignedTo.name}` : 
                       route.crew ? `Crew: ${route.crew.name}` : 'Unassigned'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">{route.jobs.length} jobs</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </main>
    </div>
  );
};

export default RouteAssignments;
