import React, { useState } from 'react';
import axios from 'axios';
import { QuoteDownloadLink } from './QuotePDF';
import Header from './Header';

const difficultyLevels = ['Easy', 'Moderate', 'Difficult'];
const skillLevels = ['Basic', 'Intermediate', 'Expert'];

const QuoteTool = () => {
  const [type, setType] = useState('lawncare');
  const [area, setArea] = useState('');
  const [options, setOptions] = useState({
    lawncare: {
      frequency: 'bi-weekly',
      terrain: 'flat',
      grassHeight: 'normal',
      obstacles: 'few',
      edging: false,
      fertilizing: false,
      debrisRemoval: false,
      seasonalCleanup: false,
    },
    hedge_trimming: {
      hedgeHeight: 'under6ft',
      hedgeDensity: 'normal',
      shapeComplexity: 'simple',
      accessDifficulty: 'easy',
      debrisVolume: 'low',
    },
    landscaping: {
      designComplexity: 'simple',
      soilQuality: 'good',
      plantDiversity: 'low',
      irrigationNeeds: 'basic',
      hardscapingAmount: 'none',
      siteAccessibility: 'easy',
    },
  });
  const [timeEstimate, setTimeEstimate] = useState(0);
  const [difficulty, setDifficulty] = useState('Easy');
  const [skillRequired, setSkillRequired] = useState('Basic');
  const [quote, setQuote] = useState(null);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    address: '',
    phone: '',
    email: '',
  });

  const handleOptionChange = (serviceType, option, value) => {
    setOptions(prevOptions => ({
      ...prevOptions,
      [serviceType]: {
        ...prevOptions[serviceType],
        [option]: value,
      },
    }));
  };

  const handleCalculateQuote = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/quotes/calculate', {
        type,
        area: parseFloat(area),
        options: options[type],
        timeEstimate,
        difficulty,
        skillRequired,
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setQuote(response.data);
    } catch (error) {
      console.error('Error calculating quote:', error);
    }
  };

  const renderOptions = () => {
    const renderSelect = (optionName, optionValue, optionChoices) => (
      <label key={optionName} className="block mb-2">
        <span className="text-white">{optionName.split(/(?=[A-Z])/).join(" ")}:</span>
        <select
          value={optionValue}
          onChange={e => handleOptionChange(type, optionName, e.target.value)}
          className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
        >
          {optionChoices.map(choice => (
            <option key={choice} value={choice}>{choice}</option>
          ))}
        </select>
      </label>
    );

    const renderCheckbox = (optionName, optionValue) => (
      <label key={optionName} className="flex items-center mb-2">
        <input
          type="checkbox"
          checked={optionValue}
          onChange={e => handleOptionChange(type, optionName, e.target.checked)}
          className="form-checkbox h-5 w-5 text-blue-600"
        />
        <span className="ml-2 text-white">{optionName.split(/(?=[A-Z])/).join(" ")}</span>
      </label>
    );

    switch (type) {
      case 'lawncare':
        return (
          <>
            {renderSelect('frequency', options.lawncare.frequency, ['weekly', 'bi-weekly'])}
            {renderSelect('terrain', options.lawncare.terrain, ['flat', 'sloped', 'hilly'])}
            {renderSelect('grassHeight', options.lawncare.grassHeight, ['normal', 'overgrown', 'very overgrown'])}
            {renderSelect('obstacles', options.lawncare.obstacles, ['few', 'some', 'many'])}
            {renderCheckbox('edging', options.lawncare.edging)}
            {renderCheckbox('fertilizing', options.lawncare.fertilizing)}
            {renderCheckbox('debrisRemoval', options.lawncare.debrisRemoval)}
            {renderCheckbox('seasonalCleanup', options.lawncare.seasonalCleanup)}
          </>
        );
      case 'hedge_trimming':
        return (
          <>
            {renderSelect('hedgeHeight', options.hedge_trimming.hedgeHeight, ['under6ft', '6-10ft', 'over10ft'])}
            {renderSelect('hedgeDensity', options.hedge_trimming.hedgeDensity, ['sparse', 'normal', 'dense'])}
            {renderSelect('shapeComplexity', options.hedge_trimming.shapeComplexity, ['simple', 'moderate', 'complex'])}
            {renderSelect('accessDifficulty', options.hedge_trimming.accessDifficulty, ['easy', 'moderate', 'difficult'])}
            {renderSelect('debrisVolume', options.hedge_trimming.debrisVolume, ['low', 'medium', 'high'])}
          </>
        );
      case 'landscaping':
        return (
          <>
            {renderSelect('designComplexity', options.landscaping.designComplexity, ['simple', 'moderate', 'complex'])}
            {renderSelect('soilQuality', options.landscaping.soilQuality, ['poor', 'average', 'good'])}
            {renderSelect('plantDiversity', options.landscaping.plantDiversity, ['low', 'medium', 'high'])}
            {renderSelect('irrigationNeeds', options.landscaping.irrigationNeeds, ['basic', 'moderate', 'advanced'])}
            {renderSelect('hardscapingAmount', options.landscaping.hardscapingAmount, ['none', 'some', 'extensive'])}
            {renderSelect('siteAccessibility', options.landscaping.siteAccessibility, ['easy', 'moderate', 'difficult'])}
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <Header />
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-gray-800 rounded-lg shadow-xl p-6">
            <h2 className="text-2xl font-semibold text-white mb-4">Quoting Tool</h2>
            <div className="mb-4 space-x-2">
              {['lawncare', 'hedge_trimming', 'landscaping'].map(t => (
                <button
                  key={t}
                  onClick={() => setType(t)}
                  className={`px-4 py-2 rounded ${type === t ? 'bg-indigo-600 text-white' : 'bg-gray-700 text-gray-300'}`}
                >
                  {t.replace('_', ' ')}
                </button>
              ))}
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block mb-2 text-white">
                  Area (sq ft):
                  <input
                    type="number"
                    value={area}
                    onChange={e => setArea(e.target.value)}
                    className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                  />
                </label>
                <label className="block mb-2 text-white">
                  Estimated Time (minutes):
                  <input
                    type="number"
                    value={timeEstimate}
                    onChange={e => setTimeEstimate(e.target.value)}
                    className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                  />
                </label>
                <label className="block mb-2">
                  <span className="text-white">Difficulty:</span>
                  <select
                    value={difficulty}
                    onChange={e => setDifficulty(e.target.value)}
                    className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                  >
                    {difficultyLevels.map(level => (
                      <option key={level} value={level}>{level}</option>
                    ))}
                  </select>
                </label>
                <label className="block mb-2">
                  <span className="text-white">Skill Required:</span>
                  <select
                    value={skillRequired}
                    onChange={e => setSkillRequired(e.target.value)}
                    className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                  >
                    {skillLevels.map(level => (
                      <option key={level} value={level}>{level}</option>
                    ))}
                  </select>
                </label>
              </div>
              <div>
                {renderOptions()}
              </div>
            </div>
            <button
              onClick={handleCalculateQuote}
              className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-500"
            >
              Calculate Quote
            </button>
            {quote && (
              <div className="mt-6 bg-gray-700 p-4 rounded-lg">
                <h3 className="text-xl font-semibold text-white mb-4">Quote Result</h3>
                <div className="grid grid-cols-2 gap-2">
                  <p className="text-white">Base Price: ${quote.basePrice}</p>
                  <p className="text-white">Weighted Price: ${quote.weightedPrice}</p>
                  <p className="text-white">Additional Price: ${quote.additionalPrice}</p>
                  <p className="text-white">Total Price: ${quote.totalPrice}</p>
                  <p className="text-white">Area: {quote.area} sq ft</p>
                  <p className="text-white">Time Estimate: {quote.timeEstimate} minutes</p>
                  <p className="text-white">Difficulty: {quote.difficulty}</p>
                  <p className="text-white">Skill Required: {quote.skillRequired}</p>
                </div>
                <h4 className="text-lg font-semibold text-white mt-4 mb-2">Customer Information</h4>
                <div className="grid grid-cols-2 gap-2">
                  {Object.entries(customerInfo).map(([key, value]) => (
                    <label key={key} className="block mb-2">
                      <span className="text-white">{key.charAt(0).toUpperCase() + key.slice(1)}:</span>
                      <input
                        type="text"
                        value={value}
                        onChange={e => setCustomerInfo({ ...customerInfo, [key]: e.target.value })}
                        className="mt-1 block w-full rounded-md bg-gray-600 border-transparent focus:border-gray-500 focus:bg-gray-500 focus:ring-0 text-white"
                      />
                    </label>
                  ))}
                </div>
                <QuoteDownloadLink quote={quote} customerInfo={customerInfo} type={type} options={options[type]} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default QuoteTool;
