// src/pages/ManageEmployees.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { PlusIcon, TrashIcon } from '@heroicons/react/24/solid';
import Header from '../components/Header'; // Import the Header component

const ManageEmployees = () => {
  const [employees, setEmployees] = useState([]);
  const [crews, setCrews] = useState([]);
  const [newCrewName, setNewCrewName] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchEmployees();
    fetchCrews();
  }, []);

  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/employees', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again.');
    }
    setLoading(false);
  };

  const fetchCrews = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/crews', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCrews(response.data);
    } catch (error) {
      console.error('Error fetching crews:', error);
      setError('Failed to fetch crews. Please try again.');
    }
  };

  const handleAddCrew = async () => {
    if (!newCrewName.trim()) {
      setError('Crew name cannot be empty.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/api/crews', { name: newCrewName }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCrews([...crews, response.data]);
      setNewCrewName('');
    } catch (error) {
      console.error('Error adding crew:', error);
      setError('Failed to add crew. Please try again.');
    }
  };

  const handleDeleteCrew = async (crewId) => {
    if (window.confirm('Are you sure you want to delete this crew?')) {
      try {
        await axios.delete(`http://localhost:5000/api/crews/${crewId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setCrews(crews.filter(crew => crew._id !== crewId));
      } catch (error) {
        console.error('Error deleting crew:', error);
        setError('Failed to delete crew. Please try again.');
      }
    }
  };

  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceCrewId = source.droppableId;
    const destinationCrewId = destination.droppableId;
    const employeeId = result.draggableId;

    if (sourceCrewId === destinationCrewId) return;

    try {
      await axios.put(`http://localhost:5000/api/crews/move`, {
        employeeId,
        sourceCrewId,
        destinationCrewId
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      fetchEmployees();
      fetchCrews();
    } catch (error) {
      console.error('Error moving employee:', error);
      setError('Failed to move employee. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header /> {/* Add Header component */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-6 text-white">Manage Employees and Crews</h1>
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          
          <div className="mb-4">
            <input
              type="text"
              value={newCrewName}
              onChange={(e) => setNewCrewName(e.target.value)}
              className="border rounded p-2 bg-background text-text"
              placeholder="New Crew Name"
            />
            <button
              onClick={handleAddCrew}
              className="ml-2 bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90 transition duration-200"
            >
              Add Crew <PlusIcon className="h-5 w-5 inline" />
            </button>
          </div>

          <DragDropContext onDragEnd={onDragEnd}>
            <div className="flex space-x-4">
              {crews.map((crew) => (
                <Droppable key={crew._id} droppableId={crew._id}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className="bg-surface shadow overflow-hidden sm:rounded-lg p-4 w-1/3"
                    >
                      <h2 className="text-xl font-bold mb-4 text-white">{crew.name}</h2>
                      <ul className="space-y-2">
                        {crew.members.map((employee, index) => (
                          <Draggable key={employee._id} draggableId={employee._id} index={index}>
                            {(provided) => (
                              <li
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                className="bg-gray-800 p-2 rounded"
                              >
                                <div className="text-white">{employee.name}</div>
                                <div className="text-gray-400 text-sm">{employee.email}</div>
                              </li>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </ul>
                      <button
                        onClick={() => handleDeleteCrew(crew._id)}
                        className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-800 transition duration-200"
                      >
                        Delete Crew <TrashIcon className="h-5 w-5 inline" />
                      </button>
                    </div>
                  )}
                </Droppable>
              ))}
              <Droppable droppableId="unassigned">
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="bg-surface shadow overflow-hidden sm:rounded-lg p-4 w-1/3"
                  >
                    <h2 className="text-xl font-bold mb-4 text-white">Unassigned Employees</h2>
                    <ul className="space-y-2">
                      {employees
                        .filter(employee => !crews.some(crew => crew.members.find(member => member._id === employee._id)))
                        .map((employee, index) => (
                          <Draggable key={employee._id} draggableId={employee._id} index={index}>
                            {(provided) => (
                              <li
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                className="bg-gray-800 p-2 rounded"
                              >
                                <div className="text-white">{employee.name}</div>
                                <div className="text-gray-400 text-sm">{employee.email}</div>
                              </li>
                            )}
                          </Draggable>
                        ))}
                      {provided.placeholder}
                    </ul>
                  </div>
                )}
              </Droppable>
            </div>
          </DragDropContext>
        </div>
      </main>
    </div>
  );
};

export default ManageEmployees;
