import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useNavigate } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin, { Draggable as FcDraggable } from '@fullcalendar/interaction';
import CustomerMap from '../../components/CustomerMap';
import { fetchJobs, fetchRoutes, saveRoute, deleteRoute, updateCalendarEvent, updateJob, fetchCalendarEvents } from './api';

const BuildRoutes = () => {
  const [jobs, setJobs] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [events, setEvents] = useState([]);
  const [activeTab, setActiveTab] = useState('routes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const [jobsData, routesData] = await Promise.all([fetchJobs(), fetchRoutes()]);
        console.log('Fetched jobs:', jobsData);
        console.log('Fetched routes:', routesData);
        setJobs(jobsData || []);
        setRoutes(routesData || []);
        await loadEvents(routesData);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load data. Please try refreshing the page.');
      } finally {
        setLoading(false);
      }
    };
  
    fetchData();
  }, []);
  
  useEffect(() => {
    initializeExternalEvents();
  }, [jobs, routes]);


  const loadEvents = async (routesData) => {
    try {
      const calendarEvents = await fetchCalendarEvents();
      const routeEvents = routesData.flatMap(route => 
        route.customers.map(customer => ({
          id: customer._id,
          title: `${route.name}: ${customer.name}`,
          start: customer.scheduledDate || new Date(),
          end: customer.scheduledEndDate || new Date(new Date().getTime() + 60*60*1000),
          color: '#4CAF50',
          extendedProps: { type: 'route', routeId: route._id, customerId: customer._id }
        }))
      );
      setEvents([...calendarEvents, ...routeEvents]);
    } catch (error) {
      console.error('Error loading events:', error);
      setError('Failed to load calendar events. Please try refreshing the page.');
    }
  };

  const initializeExternalEvents = () => {
  const draggableElems = document.querySelectorAll('.fc-event');
  if (draggableElems.length === 0) {
    console.warn('No draggable elements found');
    return;
  }
  
  draggableElems.forEach(elem => {
    new FcDraggable(elem, {
      eventData: function() {
        return {
          id: elem.getAttribute('data-id'),
          title: elem.innerText,
          duration: '01:00'
        };
      }
    });
  });
  console.log('Draggable elements initialized:', draggableElems.length);
};

// Call initializeExternalEvents in useEffect
useEffect(() => {
  initializeExternalEvents();
}, [jobs, routes]);

  const onDragEnd = async (result) => {
    const { source, destination } = result;

    if (!destination) return;

    if (source.droppableId === 'jobPool' && destination.droppableId.startsWith('route-')) {
      const jobId = result.draggableId;
      const routeId = destination.droppableId.split('-')[1];
      await moveJobToRoute(jobId, routeId);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'jobPool') {
      const jobId = result.draggableId;
      const routeId = source.droppableId.split('-')[1];
      await moveJobToPool(jobId, routeId);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
      const jobId = result.draggableId;
      const sourceRouteId = source.droppableId.split('-')[1];
      const destRouteId = destination.droppableId.split('-')[1];
      await moveJobBetweenRoutes(jobId, sourceRouteId, destRouteId);
    }
  };

  const moveJobToRoute = async (jobId, routeId) => {
    const job = jobs.find(j => j._id === jobId);
    setJobs(prevJobs => prevJobs.filter(j => j._id !== jobId));
    setRoutes(prevRoutes => prevRoutes.map(route => 
      route._id === routeId 
        ? { ...route, customers: [...route.customers, job] }
        : route
    ));
    await updateJobRoute(jobId, routeId);
  };

  const moveJobToPool = async (jobId, routeId) => {
    const route = routes.find(r => r._id === routeId);
    const job = route.customers.find(j => j._id === jobId);
    setRoutes(prevRoutes => prevRoutes.map(r => 
      r._id === routeId 
        ? { ...r, customers: r.customers.filter(j => j._id !== jobId) }
        : r
    ));
    setJobs(prevJobs => [...prevJobs, job]);
    await updateJobRoute(jobId, null);
  };

  const moveJobBetweenRoutes = async (jobId, sourceRouteId, destRouteId) => {
    const sourceRoute = routes.find(r => r._id === sourceRouteId);
    const job = sourceRoute.customers.find(j => j._id === jobId);
    setRoutes(prevRoutes => prevRoutes.map(route => {
      if (route._id === sourceRouteId) {
        return { ...route, customers: route.customers.filter(j => j._id !== jobId) };
      }
      if (route._id === destRouteId) {
        return { ...route, customers: [...route.customers, job] };
      }
      return route;
    }));
    await updateJobRoute(jobId, destRouteId);
  };

  const updateJobRoute = async (jobId, routeId) => {
    try {
      await updateJob(jobId, { route: routeId });
      setSuccessMessage('Job updated successfully!');
    } catch (error) {
      console.error('Error updating job route:', error);
      setError('Failed to update job route. Please try again.');
    }
  };

  const handleEditRouteName = async (routeId, newName) => {
    try {
      const updatedRoute = await saveRoute({ ...routes.find(r => r._id === routeId), name: newName });
      setRoutes(prevRoutes => prevRoutes.map(r => r._id === routeId ? updatedRoute : r));
      setSuccessMessage('Route name updated successfully!');
    } catch (error) {
      console.error('Error updating route name:', error);
      setError('Failed to update route name. Please try again.');
    }
  };

  const handleMapView = (routeId) => {
    navigate(`/route-map/${routeId}`);
  };

  const handleEventDrop = async (info) => {
    const { event } = info;
    console.log('Event dropped:', event);
    try {
      const updatedEvent = await updateCalendarEvent({
        id: event.id,
        start: event.start,
        end: event.end,
      });
      setEvents(prevEvents => 
        prevEvents.map(e => e.id === updatedEvent.id ? updatedEvent : e)
      );
      const { routeId, customerId } = event.extendedProps;
      const updatedRoutes = routes.map(route => {
        if (route._id === routeId) {
          return {
            ...route,
            customers: route.customers.map(customer => 
              customer._id === customerId 
                ? { ...customer, scheduledDate: event.start, scheduledEndDate: event.end }
                : customer
            )
          };
        }
        return route;
      });
      setRoutes(updatedRoutes);
      await saveRoute(updatedRoutes.find(r => r._id === routeId));
    } catch (error) {
      console.error('Error updating event:', error);
      setError('Failed to update event. Please try again.');
      info.revert();
    }
  };

  const handleEventReceive = async (info) => {
    const { event } = info;
    console.log('Event received:', event);
    const jobId = event.id;
    const startDate = event.start;
  
    if (event.extendedProps.type === 'route') {
      const routeId = jobId;
      const route = routes.find(r => r._id === routeId);
      if (!route) {
        console.error('Route not found:', routeId);
        setError('Failed to find route. Please try again.');
        info.revert();
        return;
      }
  
      // Decompose the route into jobs
      const jobsInRoute = route.customers;
  
      try {
        await Promise.all(jobsInRoute.map(async (job) => {
          await updateJob(job._id, { scheduledDate: startDate });
          setJobs(prevJobs => prevJobs.map(j => 
            j._id === job._id ? { ...j, scheduledDate: startDate } : j
          ));
        }));
  
        setEvents(prevEvents => [
          ...prevEvents,
          ...jobsInRoute.map(job => ({
            id: job._id,
            title: job.title,
            start: startDate,
            end: new Date(startDate.getTime() + 60 * 60 * 1000),
            color: '#4CAF50',
            extendedProps: { type: 'job', jobId: job._id }
          }))
        ]);
  
        setSuccessMessage('Route scheduled successfully!');
      } catch (error) {
        console.error('Error scheduling route:', error);
        setError('Failed to schedule route. Please try again.');
        info.revert();
      }
    } else {
      // Existing job scheduling logic
      try {
        const job = jobs.find(job => job._id === jobId);
        if (!job) {
          throw new Error('Job not found');
        }
        await updateJob(jobId, { scheduledDate: startDate });
        setJobs(prevJobs => prevJobs.map(job => 
          job._id === jobId ? { ...job, scheduledDate: startDate } : job
        ));
        setEvents(prevEvents => [...prevEvents, {
          id: jobId,
          title: event.title,
          start: startDate,
          end: new Date(startDate.getTime() + 60 * 60 * 1000),
          color: '#4CAF50',
          extendedProps: { type: 'job', jobId }
        }]);
        setSuccessMessage('Job scheduled successfully!');
      } catch (error) {
        console.error('Error scheduling job:', error);
        setError('Failed to schedule job. Please try again.');
        info.revert();
      }
    }
  };
  const handleEventClick = (info) => {
    const { event } = info;
    alert(`Event: ${event.title}\nStart: ${event.start}\nEnd: ${event.end}`);
  };

  const handleAddRoute = async () => {
    try {
      const newRoute = await saveRoute({ name: `New Route ${routes.length + 1}`, customers: [] });
      setRoutes([...routes, newRoute]);
      setSuccessMessage('New route added successfully!');
    } catch (error) {
      console.error('Error adding new route:', error);
      setError('Failed to add new route. Please try again.');
    }
  };

  const handleDeleteRoute = async (routeId) => {
    try {
      await deleteRoute(routeId);
      setRoutes(routes.filter(route => route._id !== routeId));
      setSuccessMessage('Route deleted successfully!');
    } catch (error) {
      console.error('Error deleting route:', error);
      setError('Failed to delete route. Please try again.');
    }
  };

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <div className="build-routes min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-6">Route Management</h1>
          
          {successMessage && (
            <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4" role="alert">
              <p>{successMessage}</p>
            </div>
          )}

          <div className="mb-6">
            <div className="sm:hidden">
              <select
                onChange={(e) => setActiveTab(e.target.value)}
                className="block w-full bg-background text-text border-gray-300 focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 rounded-md shadow-sm"
              >
                <option value="routes">Routes</option>
                <option value="jobs">Jobs</option>
                <option value="calendar">Calendar</option>
                <option value="map">Map</option>
              </select>
            </div>
            <div className="hidden sm:block">
              <nav className="flex space-x-4" aria-label="Tabs">
                {['routes', 'jobs', 'calendar', 'map'].map((tab) => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`${
                      activeTab === tab
                        ? 'bg-primary text-white'
                        : 'text-text hover:text-white hover:bg-primary'
                    } px-3 py-2 font-medium text-sm rounded-md`}
                  >
                    {tab.charAt(0).toUpperCase() + tab.slice(1)}
                  </button>
                ))}
              </nav>
            </div>
          </div>

          <div className="bg-surface p-6 rounded-lg shadow-lg">
            {activeTab === 'routes' && (
              <DragDropContext onDragEnd={onDragEnd}>
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-2xl font-semibold">Routes</h2>
                  <button
                    onClick={handleAddRoute}
                    className="bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90"
                  >
                    Add New Route
                  </button>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {routes.map(route => (
                    <Droppable key={route._id} droppableId={`route-${route._id}`}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.droppableProps}
                          className="bg-gray-100 p-4 rounded-lg text-black"
                        >
                          <div className="flex justify-between items-center mb-2">
                            <h3 className="font-semibold">{route.name}</h3>
                            <div>
                              <button
                                onClick={() => {
                                  const newName = prompt('Enter new route name:', route.name);
                                  if (newName) handleEditRouteName(route._id, newName);
                                }}
                                className="text-primary hover:text-primary-dark mr-2"
                              >
                                Edit Name
                              </button>
                              <button
                                onClick={() => handleMapView(route._id)}
                                className="text-blue-600 hover:text-blue-800 mr-2"
                              >
                                View Map
                              </button>
                              <button
                                onClick={() => handleDeleteRoute(route._id)}
                                className="text-red-600 hover:text-red-800"
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                          {route.customers && route.customers.map((customer, index) => (
                            <Draggable key={customer._id} draggableId={customer._id} index={index}>
                              {(provided) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                  className="bg-white p-2 mb-2 rounded shadow"
                                >
                                  {customer.name}
                                </div>
                              )}
                            </Draggable>
                          ))}
                          {provided.placeholder}
                        </div>
                      )}
                    </Droppable>
                  ))}
                </div>
                <Droppable droppableId="jobPool">
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className="mt-6 bg-gray-200 p-4 rounded-lg text-black"
                    >
                      <h3 className="font-semibold mb-2">Unassigned Jobs</h3>
                      {jobs.map((job, index) => (
                        <Draggable key={job._id} draggableId={job._id} index={index}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className="bg-white p-2 mb-2 rounded shadow fc-event cursor-move"
                              data-id={job._id}
                            >
                              <h4 className="font-semibold">{job.title}</h4>
                              <p>{job.description}</p>
                              <p>Status: {job.status}</p>
                              <p>Customer: {job.customer?.name}</p>
                              <p>Scheduled: {job.scheduledDate ? new Date(job.scheduledDate).toLocaleString() : 'Not scheduled'}</p>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </DragDropContext>
            )}

            {activeTab === 'jobs' && (
              <div className="jobs-container">
                <h2 className="text-2xl font-semibold mb-4">Jobs</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {jobs.map(job => (
                    <div key={job._id} className="bg-gray-100 p-4 rounded-lg text-black">
                      <h3 className="font-semibold">{job.title}</h3>
                      <p>{job.description}</p>
                      <p>Status: {job.status}</p>
                      <p>Customer: {job.customer?.name}</p>
                      <p>Scheduled: {job.scheduledDate ? new Date(job.scheduledDate).toLocaleString() : 'Not scheduled'}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'calendar' && (
              <div className="calendar-container">
                <h2 className="text-2xl font-semibold mb-4">Schedule</h2>
                <div id="external-events" className="mb-4">
                  <h4 className="font-semibold mb-2">Draggable Jobs</h4>
                  {jobs.map(job => (
                    <div
                      key={job._id}
                      className="fc-event bg-white p-2 mb-2 rounded shadow cursor-move"
                      data-id={job._id}
                    >
                      {job.title}
                    </div>
                  ))}
                  <h4 className="font-semibold mb-2">Draggable Routes</h4>
                  {routes.map(route => (
                    <div
                      key={route._id}
                      className="fc-event bg-white p-2 mb-2 rounded shadow cursor-move"
                      data-id={route._id}
                    >
                      {route.name}
                    </div>
                  ))}
                </div>
                <FullCalendar
                  plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                  initialView="dayGridMonth"
                  headerToolbar={{
                    left: 'prev,next today',
                    center: 'title',
                    right: 'dayGridMonth,timeGridWeek,timeGridDay'
                  }}
                  editable={true}
                  droppable={true}
                  events={events}
                  eventDrop={handleEventDrop}
                  eventReceive={handleEventReceive}
                  eventClick={handleEventClick}
                />
              </div>
            )}

            {activeTab === 'map' && (
              <div className="map-container">
                <h2 className="text-2xl font-semibold mb-4">Route Maps</h2>
                {routes.map(route => (
                  <div key={route._id} className="mb-8">
                    <h3 className="text-xl font-semibold mb-2">{route.name}</h3>
                    <CustomerMap 
                      customers={route.customers}
                      route={route}
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default BuildRoutes;
