import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import CustomerMap from '../components/CustomerMap';
import { useAuth } from '../contexts/AuthContext';
import Header from '../components/Header';

const BuildRoutes = () => {
  const { user } = useAuth();
  const [customers, setCustomers] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('routes');
  const [filteredCustomers, setFilteredCustomers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [showAddressWarning, setShowAddressWarning] = useState(false);
  const [successMessage, setSuccessMessage] = useState(null);
  const customersPerPage = 10;
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [recurringOptions, setRecurringOptions] = useState({
    frequency: 'weekly',
    interval: 1,
    daysOfWeek: [],
  });
  const [jobs, setJobs] = useState([]);
  const [calendarEvents, setCalendarEvents] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const filtered = customers.filter(customer => 
      customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (customer.address && `${customer.address.street} ${customer.address.city} ${customer.address.state} ${customer.address.zipCode}`
        .toLowerCase().includes(searchTerm.toLowerCase()))
    );
    setFilteredCustomers(filtered);
    setCurrentPage(1);

    const incompleteAddresses = customers.some(customer => 
      !customer.address || !customer.address.street || !customer.address.city || 
      !customer.address.state || !customer.address.zipCode
    );
    setShowAddressWarning(incompleteAddresses);
  }, [customers, searchTerm]);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [customersResponse, routesResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/customers', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('http://localhost:5000/api/routes', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);
      
      const geocodedCustomers = await Promise.all(customersResponse.data.map(geocodeCustomer));
      setCustomers(geocodedCustomers);
      setRoutes(routesResponse.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const geocodeCustomer = async (customer) => {
    if (customer.address && !customer.address.lat && !customer.address.lng) {
      const geocoded = await geocodeAddress(customer.address);
      return { ...customer, address: { ...customer.address, ...geocoded } };
    }
    return customer;
  };

  const geocodeAddress = async (address) => {
    if (!address.street || !address.city || !address.state || !address.zipCode) {
      console.warn('Incomplete address for geocoding:', address);
      return null;
    }
    const fullAddress = `${address.street}, ${address.city}, ${address.state} ${address.zipCode}`;
    try {
      const response = await axios.get(`https://maps.googleapis.com/maps/api/geocode/json`, {
        params: {
          address: fullAddress,
          key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
        }
      });
      if (response.data.results.length > 0) {
        const { lat, lng } = response.data.results[0].geometry.location;
        console.log(`Geocoded ${fullAddress} to:`, { lat, lng });
        return { lat, lng };
      }
      console.warn(`No results found for address: ${fullAddress}`);
      return null;
    } catch (error) {
      console.error('Geocoding error:', error);
      return null;
    }
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if (!destination) return;

    try {
      if (source.droppableId === 'customersList' && destination.droppableId.startsWith('route-')) {
        moveCustomerToRoute(source.index, parseInt(destination.droppableId.split('-')[1]), destination.index);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'customersList') {
        moveCustomerToPool(parseInt(source.droppableId.split('-')[1]), source.index);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
        moveCustomerBetweenRoutes(
          parseInt(source.droppableId.split('-')[1]),
          source.index,
          parseInt(destination.droppableId.split('-')[1]),
          destination.index
        );
      }
    } catch (err) {
      console.error('Error during drag and drop:', err);
      setError('Failed to update routes. Please try again.');
    }
  };

  const moveCustomerToRoute = (customerIndex, routeIndex, destinationIndex) => {
    const customer = filteredCustomers[customerIndex];
    setCustomers(prevCustomers => prevCustomers.filter(c => c._id !== customer._id));
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      newRoutes[routeIndex].customers.splice(destinationIndex, 0, customer);
      return newRoutes;
    });
  };

  const moveCustomerToPool = (routeIndex, customerIndex) => {
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      const [customer] = newRoutes[routeIndex].customers.splice(customerIndex, 1);
      setCustomers(prevCustomers => [...prevCustomers, customer]);
      return newRoutes;
    });
  };

  const moveCustomerBetweenRoutes = (sourceRouteIndex, sourceIndex, destRouteIndex, destIndex) => {
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      const [customer] = newRoutes[sourceRouteIndex].customers.splice(sourceIndex, 1);
      newRoutes[destRouteIndex].customers.splice(destIndex, 0, customer);
      return newRoutes;
    });
  };

  const addNewRoute = () => {
    const newRouteName = `Route ${routes.length + 1}`;
    if (!routes.some(route => route.name === newRouteName)) {
      setRoutes(prevRoutes => [...prevRoutes, { _id: Date.now().toString(), name: newRouteName, customers: [] }]);
    } else {
      setError('A route with this name already exists.');
    }
  };

  const deleteRoute = async (routeIndex) => {
    try {
      const routeToDelete = routes[routeIndex];
      console.log('Deleting route with ID:', routeToDelete._id);
      await axios.delete(`http://localhost:5000/api/routes/${routeToDelete._id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      
      setRoutes(prevRoutes => {
        const newRoutes = prevRoutes.filter((_, index) => index !== routeIndex);
        return newRoutes.map((route, index) => ({
          ...route,
          name: `Route ${index + 1}`
        }));
      });
    } catch (error) {
        console.error('Error deleting route:', error.response?.data || error.message);
        setError('Failed to delete route. Please try again.');
      }
  };

  const saveRoute = async (route) => {
    try {
      const response = await axios.post('http://localhost:5000/api/routes', route, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      console.log('Route saved:', response.data);
      
      setRoutes(prevRoutes => prevRoutes.map(r => 
        r._id === route._id ? { ...r, _id: response.data._id, isSaved: true } : r
      ));
  
      setError(null);
      setSuccessMessage('Route saved successfully!');
  
      setTimeout(() => setSuccessMessage(null), 3000);
    } catch (error) {
      console.error('Error saving route:', error);
      setError('Failed to save route. Please try again.');
    }
  };  

  const optimizeRoute = async (routeId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/routes/${routeId}/optimize`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      console.log('Route optimized:', response.data);
      setRoutes(prevRoutes => prevRoutes.map(route => 
        route._id === routeId ? response.data : route
      ));
    } catch (error) {
      console.error('Error optimizing route:', error);
      setError('Failed to optimize route. Please try again.');
    }
  };

  const indexOfLastCustomer = currentPage * customersPerPage;
  const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
  const currentCustomers = filteredCustomers.slice(indexOfFirstCustomer, indexOfLastCustomer);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const renderRouteBuilder = () => (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="grid grid-cols-3 gap-4">
        <div className="routes-list col-span-2 text-gray-800">
          <h2 className="text-xl text-white font-semibold mb-2">Routes</h2>
          {routes.map((route, routeIndex) => (
            <Droppable key={route._id} droppableId={`route-${routeIndex}`}>
              {(provided) => (
                <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-4 rounded mb-4 min-h-[150px]">
                  <div className="flex justify-between items-center mb-2">
                    <h3 className="font-semibold">{route.name}</h3>
                    <div>
                      <button 
                        onClick={() => saveRoute(route)} 
                        className={`px-2 py-1 rounded mr-2 ${
                          route.isSaved 
                            ? 'bg-green-500 text-white' 
                            : 'bg-blue-500 text-white hover:bg-blue-600'
                        }`}
                      >
                        {route.isSaved ? 'Saved' : 'Save'}
                      </button>
                      <button onClick={() => deleteRoute(routeIndex)} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600">
                        Delete
                      </button>
                    </div>
                  </div>
                  {route.customers.map((customer, index) => (
                    <Draggable key={`${route._id}-${customer._id}`} draggableId={`${route._id}-${customer._id}`} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white p-2 mb-2 rounded shadow text-gray-800"
                        >
                          {customer.name} - {customer.address?.street}, {customer.address?.city}, {customer.address?.state} {customer.address?.zipCode}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
        <div className="customers-list text-gray-800">
          <h2 className="text-xl text-white font-semibold mb-2">Customers</h2>
          <input
            type="text"
            placeholder="Search customers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full p-2 mb-4 rounded"
          />
          <Droppable droppableId="customersList">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-4 rounded min-h-[300px] max-h-[500px] overflow-auto">
                {currentCustomers.map((customer, index) => (
                  <Draggable key={customer._id} draggableId={customer._id} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-white p-2 mb-2 rounded shadow"
                      >
                        {customer.name} - {customer.address?.street}, {customer.address?.city}, {customer.address?.state} {customer.address?.zipCode}
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
          <div className="flex justify-center mt-4">
            {Array.from({ length: Math.ceil(filteredCustomers.length / customersPerPage) }, (_, i) => (
              <button
                key={i}
                onClick={() => paginate(i + 1)}
                className={`mx-1 px-3 py-1 rounded ${currentPage === i + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                {i + 1}
              </button>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
  
  const renderMap = () => (
    <div className="map-container h-[600px] w-full">
      <CustomerMap customers={customers} routes={routes} user={user} onOptimizeRoute={optimizeRoute} />
    </div>
  );

  useEffect(() => {
    // Fetch jobs and existing calendar events
    fetchJobs();
    fetchCalendarEvents();
  }, []);

  const fetchJobs = async () => {
    // Fetch jobs
    // Example code to fetch jobs
    try {
      const response = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const fetchCalendarEvents = async () => {
    // Fetch calendar events
    // Example code to fetch calendar events
    try {
      const response = await axios.get('http://localhost:5000/api/calendar-events', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCalendarEvents(response.data);
    } catch (error) {
      console.error('Error fetching calendar events:', error);
    }
  };

  const onEventDrop = (info) => {
    // Handle event drop (update start and end times)
    console.log('Event dropped:', info);
  };

  const renderCalendar = () => (
    <div className="calendar-container grid grid-cols-4 gap-4">
      <div className="col-span-3">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          editable={true}
          droppable={true}
          events={calendarEvents}
          eventDrop={onEventDrop}
        />
      </div>
      <div className="job-pool">
        <h3 className="text-lg font-semibold mb-2">Job Pool</h3>
        <Droppable droppableId="jobPool">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef}>
              {jobs.map((job, index) => (
                <Draggable key={job.id} draggableId={job.id} index={index}>
                  {(provided) => (
                    <li
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="p-2 bg-white shadow rounded mb-2"
                    >
                      {job.title}
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </div>
    </div>
  );

  const handleRouteSelect = (routeId) => {
    // Fetch route details and set as selected route
    const route = routes.find(route => route._id === routeId);
    setSelectedRoute(route);
  };

  const handleRecurringOptionChange = (option, value) => {
    setRecurringOptions(prev => ({ ...prev, [option]: value }));
  };

  const saveSchedule = () => {
    // Save the recurring schedule for the selected route
  };

  const renderScheduling = () => (
    <div className="scheduling-container">
      <h2 className="text-xl font-semibold mb-4">Scheduling</h2>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <h3 className="text-lg font-semibold mb-2">Routes</h3>
          <ul>
            {routes.map(route => (
              <li 
                key={route._id} 
                onClick={() => handleRouteSelect(route._id)}
                className={`cursor-pointer p-2 ${selectedRoute?._id === route._id ? 'bg-blue-100' : ''}`}
              >
                {route.name}
              </li>
            ))}
          </ul>
        </div>
        <div>
          <h3 className="text-lg font-semibold mb-2">Recurring Schedule</h3>
          {selectedRoute && (
            <div>
              <select 
                value={recurringOptions.frequency} 
                onChange={(e) => handleRecurringOptionChange('frequency', e.target.value)}
              >
                <option value="weekly">Weekly</option>
                <option value="biweekly">Bi-weekly</option>
                <option value="monthly">Monthly</option>
              </select>
              {/* Add more options for recurring schedule */}
              <button onClick={saveSchedule} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
                Save Schedule
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;
  
  return (
    <div className="min-h-screen bg-background text-gray-800">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-bold mb-4">Route Management</h1>
          
          {successMessage && (
            <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4" role="alert">
              <p>{successMessage}</p>
            </div>
          )}
  
          {showAddressWarning && (
            <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-4" role="alert">
              <p className="font-bold">Warning</p>
              <p>Some customer addresses are incomplete. This may affect the accuracy of the map view and route optimization.</p>
            </div>
          )}
  
          <div className="mb-4">
            <button onClick={addNewRoute} className="bg-blue-500 text-white px-4 py-2 rounded mr-2 hover:bg-blue-600">
              Add New Route
            </button>
          </div>
          <div className="mb-4">
            <button
              onClick={() => setActiveTab('routes')}
              className={`px-4 py-2 rounded-tl-lg rounded-tr-lg mr-1 ${activeTab === 'routes' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
            >
              Route Builder
            </button>
            <button
              onClick={() => setActiveTab('map')}
              className={`px-4 py-2 rounded-tl-lg rounded-tr-lg mr-1 ${activeTab === 'map' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
            >
              Map View
            </button>
            <button
              onClick={() => setActiveTab('calendar')}
              className={`px-4 py-2 rounded-tl-lg rounded-tr-lg mr-1 ${activeTab === 'calendar' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
            >
              Calendar
            </button>
            <button
              onClick={() => setActiveTab('scheduling')}
              className={`px-4 py-2 rounded-tl-lg rounded-tr-lg ${activeTab === 'scheduling' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
            >
              Scheduling
            </button>
          </div>
          <div className="bg-surface p-4 rounded-b-lg rounded-tr-lg">
            {activeTab === 'routes' && renderRouteBuilder()}
            {activeTab === 'map' && renderMap()}
            {activeTab === 'calendar' && renderCalendar()}
            {activeTab === 'scheduling' && renderScheduling()}
          </div>
          
          <div className="mt-6 bg-blue-100 p-4 rounded-lg">
            <h3 className="text-lg font-semibold mb-2">Your Subscription: {user.subscriptionTier}</h3>
            <p>
              {user.subscriptionTier === 'Free' && 'Upgrade to access more features like route optimization and advanced analytics.'}
              {user.subscriptionTier === 'Basic' && 'Enjoy basic features. Upgrade to Pro for advanced route optimization.'}
              {user.subscriptionTier === 'Pro' && 'You have access to advanced features including route optimization.'}
              {user.subscriptionTier === 'Enterprise' && 'You have full access to all features including custom solutions.'}
            </p>
            {user.subscriptionTier !== 'Enterprise' && (
              <Link to="/pricing" className="mt-2 inline-block bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                Upgrade Subscription
              </Link>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default BuildRoutes;
