// src/components/CustomerMap.js
import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker, Polyline } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '600px' // Ensure this is large enough to be visible
};

const defaultCenter = {
  lat: 40.7128, // New York City coordinates as fallback
  lng: -74.0060
};

const CustomerMap = ({ customers, routes }) => {
  const [center, setCenter] = useState(defaultCenter);

  useEffect(() => {
    if (customers.length > 0) {
      const validCustomers = customers.filter(c => c.address?.lat && c.address?.lng);
      if (validCustomers.length > 0) {
        const lats = validCustomers.map(c => c.address.lat);
        const lngs = validCustomers.map(c => c.address.lng);
        setCenter({
          lat: (Math.min(...lats) + Math.max(...lats)) / 2,
          lng: (Math.min(...lngs) + Math.max(...lngs)) / 2
        });
      }
    }
  }, [customers]);

  const getRandomColor = () => `#${Math.floor(Math.random()*16777215).toString(16)}`;

  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
      >
        {customers.map((customer) => (
          customer.address?.lat && customer.address?.lng && (
            <Marker
              key={customer._id}
              position={{
                lat: customer.address.lat,
                lng: customer.address.lng
              }}
              title={customer.name}
            />
          )
        ))}
        {routes.map((route) => (
          <Polyline
            key={route._id}
            path={route.customers
              .filter(c => c.address?.lat && c.address?.lng)
              .map(c => ({ lat: c.address.lat, lng: c.address.lng }))}
            options={{
              strokeColor: getRandomColor(),
              strokeOpacity: 0.8,
              strokeWeight: 2,
            }}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default CustomerMap;
