// src/components/ProfileTabs/ProfileTab.js
import React, { useState } from 'react';
import axios from 'axios';
import { Autocomplete } from '@react-google-maps/api';

const ProfileTab = ({ profileData, refreshProfile, isLoaded }) => {
  const [formData, setFormData] = useState(profileData || {});
  const [autocomplete, setAutocomplete] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const handlePlaceChanged = () => {
    if (autocomplete !== null) {
      const place = autocomplete.getPlace();

      // Extract address components
      const addressComponents = place.address_components;
      const streetNumber =
        addressComponents.find((component) => component.types.includes('street_number'))
          ?.long_name || '';
      const route =
        addressComponents.find((component) => component.types.includes('route'))?.long_name || '';
      const city =
        addressComponents.find((component) => component.types.includes('locality'))?.long_name || '';
      const state =
        addressComponents.find((component) =>
          component.types.includes('administrative_area_level_1')
        )?.short_name || '';
      const zipCode =
        addressComponents.find((component) => component.types.includes('postal_code'))?.long_name ||
        '';

      setFormData((prev) => ({
        ...prev,
        address: {
          ...prev.address,
          street: `${streetNumber} ${route}`.trim(),
          city,
          state,
          zipCode,
        },
      }));
    } else {
      console.log('Autocomplete is not loaded yet!');
    }
  };

  const onLoadAutocomplete = (autocompleteInstance) => {
    setAutocomplete(autocompleteInstance);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/profile`,
        formData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      refreshProfile();
      setMessage({ type: 'success', text: 'Profile updated successfully!' });
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage({ type: 'error', text: 'Failed to update profile. Please try again.' });
    }
    setLoading(false);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-semibold mb-4">Edit Profile</h2>
      {message.text && (
        <div
          className={`px-4 py-2 rounded ${
            message.type === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
          }`}
        >
          {message.text}
        </div>
      )}
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name */}
        <div>
          <label htmlFor="name" className="block text-sm font-medium">
            Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name || ''}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
            required
          />
        </div>
        {/* Email */}
        <div>
          <label htmlFor="email" className="block text-sm font-medium">
            Email
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email || ''}
            onChange={handleChange}
            className="mt-1 block w-full bg-gray-200 rounded-md border-gray-300 shadow-sm text-black"
            disabled
          />
        </div>
        {/* Bio */}
        <div>
          <label htmlFor="bio" className="block text-sm font-medium">
            Bio
          </label>
          <textarea
            id="bio"
            name="bio"
            value={formData.bio || ''}
            onChange={handleChange}
            rows="3"
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
          ></textarea>
        </div>
        {/* Address */}
        <div>
          <label htmlFor="address.street" className="block text-sm font-medium">
            Address
          </label>
          {isLoaded && (
            <Autocomplete onLoad={onLoadAutocomplete} onPlaceChanged={handlePlaceChanged}>
              <input
                type="text"
                id="address.street"
                name="address.street"
                value={formData.address?.street || ''}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
              />
            </Autocomplete>
          )}
          {!isLoaded && (
            <input
              type="text"
              id="address.street"
              name="address.street"
              value={formData.address?.street || ''}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
            />
          )}
        </div>
        {/* City */}
        <div>
          <label htmlFor="address.city" className="block text-sm font-medium">
            City
          </label>
          <input
            type="text"
            id="address.city"
            name="address.city"
            value={formData.address?.city || ''}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
          />
        </div>
        {/* State */}
        <div>
          <label htmlFor="address.state" className="block text-sm font-medium">
            State
          </label>
          <input
            type="text"
            id="address.state"
            name="address.state"
            value={formData.address?.state || ''}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
          />
        </div>
        {/* Zip Code */}
        <div>
          <label htmlFor="address.zipCode" className="block text-sm font-medium">
            Zip Code
          </label>
          <input
            type="text"
            id="address.zipCode"
            name="address.zipCode"
            value={formData.address?.zipCode || ''}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
          />
        </div>
        {/* Submit Button */}
        <div>
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
          >
            {loading ? 'Updating...' : 'Update Profile'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProfileTab;
