import React, { useState, useEffect } from 'react';
import { fetchWithAuth } from '../components/utils';
import Header from '../components/Header';
import { ChevronDownIcon, ChevronUpIcon } from '@heroicons/react/24/outline';

const RouteAssignments = () => {
  const [routes, setRoutes] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedRoutes, setExpandedRoutes] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [routesResponse, jobsResponse, employeesResponse, crewsResponse] = await Promise.all([
          fetchWithAuth('/routes'),
          fetchWithAuth('/jobs'),
          fetchWithAuth('/employees'),
          fetchWithAuth('/crews'),
        ]);

        const fetchedRoutes = routesResponse.routes || {};
        const fetchedJobs = jobsResponse || [];
        const fetchedEmployees = employeesResponse || [];
        const fetchedCrews = crewsResponse || [];

        // Process the data to include job details and assignments
        processRoutesData(fetchedRoutes, fetchedJobs, fetchedEmployees, fetchedCrews);

        setLoading(false);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to fetch data. Please try again.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const processRoutesData = (fetchedRoutes, fetchedJobs, fetchedEmployees, fetchedCrews) => {
    // Create lookup maps
    const jobMap = {};
    fetchedJobs.forEach(job => {
      jobMap[job._id] = job;
    });

    const employeeMap = {};
    fetchedEmployees.forEach(employee => {
      employeeMap[employee._id] = employee;
    });

    const crewMap = {};
    fetchedCrews.forEach(crew => {
      crewMap[crew._id] = crew;
    });

    // Process routes
    const processedRoutes = {};

    for (const [day, dayRoutes] of Object.entries(fetchedRoutes)) {
      processedRoutes[day] = dayRoutes.map(route => {
        // Flatten jobs if they are grouped
        let jobIds = route.jobs;
        if (Array.isArray(route.jobs)) {
          // Jobs are already an array of IDs
        } else if (typeof route.jobs === 'object') {
          // Jobs are grouped by pattern; flatten them
          jobIds = Object.values(route.jobs).flat();
        } else {
          jobIds = [];
        }

        // Replace job IDs with full job objects
        const fullJobs = jobIds.map(jobId => jobMap[jobId]).filter(job => job);

        // Get assigned employee or crew
        const assignedEmployee = route.employee ? employeeMap[route.employee] : null;
        const assignedCrew = route.crew ? crewMap[route.crew] : null;

        return {
          ...route,
          jobs: fullJobs,
          employee: assignedEmployee,
          crew: assignedCrew,
        };
      });
    }

    setRoutes(processedRoutes);
  };

  const toggleRouteExpansion = (day, index) => {
    setExpandedRoutes(prev => ({
      ...prev,
      [day]: {
        ...prev[day],
        [index]: !prev[day]?.[index]
      }
    }));
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-white text-2xl">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-red-500 text-2xl">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-gray-200">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6 text-white">Route Assignments</h1>
        {Object.entries(routes).map(([day, dayRoutes]) => (
          <div key={day} className="mb-8">
            <h2 className="text-2xl font-semibold mb-4 text-white">{day}</h2>
            <div className="bg-gray-800 shadow overflow-hidden sm:rounded-md">
              <ul className="divide-y divide-gray-700">
                {dayRoutes.map((route, index) => (
                  <li key={index}>
                    <div className="px-4 py-4 sm:px-6">
                      <div className="flex items-center justify-between">
                        <div className="text-lg font-medium text-blue-400">{route.name || `Route ${index + 1}`}</div>
                        <div className="ml-2 flex-shrink-0 flex">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${route.employee || route.crew ? 'bg-green-200 text-green-800' : 'bg-red-200 text-red-800'}`}>
                            {route.employee ? 'Employee Assigned' : route.crew ? 'Crew Assigned' : 'Unassigned'}
                          </span>
                        </div>
                      </div>
                      <div className="mt-2 sm:flex sm:justify-between">
                        <div className="sm:flex">
                          <p className="flex items-center text-sm text-gray-300">
                            {route.employee ? `Assigned to: ${route.employee.name}` : 
                             route.crew ? `Assigned to crew: ${route.crew.name}` : 'Not assigned'}
                          </p>
                        </div>
                        <div className="mt-2 flex items-center text-sm text-gray-300 sm:mt-0">
                          <p>{route.jobs.length} jobs</p>
                        </div>
                      </div>
                      <button
                        onClick={() => toggleRouteExpansion(day, index)}
                        className="mt-3 w-full flex items-center justify-center px-4 py-2 border border-gray-600 rounded-md shadow-sm text-sm font-medium text-white bg-gray-700 hover:bg-gray-600"
                      >
                        {expandedRoutes[day]?.[index] ? (
                          <>
                            Hide Details <ChevronUpIcon className="ml-2 h-5 w-5" />
                          </>
                        ) : (
                          <>
                            Show Details <ChevronDownIcon className="ml-2 h-5 w-5" />
                          </>
                        )}
                      </button>
                      {expandedRoutes[day]?.[index] && (
                        <div className="mt-4">
                          <h4 className="text-lg font-medium text-white mb-2">Jobs:</h4>
                          <ul className="divide-y divide-gray-700">
                            {route.jobs.map((job, jobIndex) => (
                              <li key={jobIndex} className="py-3">
                                <div className="flex items-center justify-between">
                                  <p className="text-sm font-medium text-blue-300">{job.title || 'Untitled Job'}</p>
                                  <p className="text-sm text-gray-400">{job.customer?.name || 'No Customer'}</p>
                                </div>
                                <p className="mt-1 text-xs text-gray-400">{job.location?.address || 'No Address'}</p>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default RouteAssignments;
