// src/components/ProfileTabs/BadgesTab.js
import React from 'react';

const BadgesTab = ({ badges }) => {
  if (!badges) {
    return <p>No badges earned yet. Start completing tasks to earn badges!</p>;
  }

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Your Badges</h2>
      <div className="grid grid-cols-2 gap-4">
        {badges.map((badge, index) => (
          <div
            key={index}
            className="bg-indigo-600 text-white p-4 rounded flex items-center"
          >
            <div className="mr-4">
              {/* You can add an icon or image here */}
              <div className="h-12 w-12 bg-white rounded-full"></div>
            </div>
            <div>
              <h3 className="font-semibold">{badge.name}</h3>
              <p className="text-sm">{badge.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BadgesTab;
