// src/components/QuotePDF.js
import React from 'react';
import { Document, Page, Text, View, StyleSheet, Image, PDFDownloadLink } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: { 
    padding: 30, 
    backgroundColor: '#ffffff' 
  },
  section: { 
    margin: 10, 
    padding: 10 
  },
  header: { 
    flexDirection: 'row', 
    justifyContent: 'space-between', 
    marginBottom: 20 
  },
  logo: { 
    width: 100, 
    height: 100 
  },
  businessInfo: { 
    fontSize: 10, 
    alignItems: 'flex-end' 
  },
  title: { 
    fontSize: 24, 
    marginBottom: 20, 
    color: '#333333', 
    fontWeight: 'bold' 
  },
  subheader: { 
    fontSize: 18, 
    marginBottom: 10, 
    color: '#333333', 
    fontWeight: 'bold' 
  },
  text: { 
    fontSize: 12, 
    marginBottom: 5, 
    color: '#333333' 
  },
  bold: { 
    fontWeight: 'bold' 
  },
  row: { 
    flexDirection: 'row', 
    justifyContent: 'space-between', 
    marginBottom: 5 
  },
  table: { 
    display: 'table', 
    width: 'auto', 
    marginTop: 10 
  },
  tableRow: { 
    flexDirection: 'row' 
  },
  tableCol: { 
    width: '25%', 
    borderStyle: 'solid', 
    borderWidth: 1, 
    borderColor: '#bfbfbf' 
  },
  tableCell: { 
    margin: 'auto', 
    marginTop: 5, 
    marginBottom: 5, 
    fontSize: 10 
  },
});

const QuotePDF = ({ quote, customerInfo, type, options, businessInfo }) => {
  const renderCustomerInfo = () => {
    return Object.entries(customerInfo).map(([key, value]) => {
      if (key === 'address') {
        return Object.entries(value).map(([addressKey, addressValue]) => (
          <Text key={addressKey} style={styles.text}>
            <Text style={styles.bold}>{addressKey.charAt(0).toUpperCase() + addressKey.slice(1)}:</Text> {addressValue}
          </Text>
        ));
      }
      if (key !== '_id') {
        return (
          <Text key={key} style={styles.text}>
            <Text style={styles.bold}>{key.charAt(0).toUpperCase() + key.slice(1)}:</Text> {value}
          </Text>
        );
      }
      return null;
    });
  };

  const renderOptions = () => {
    return Object.entries(options).map(([key, value]) => (
      <Text key={key} style={styles.text}>
        {key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1')}: {value.toString()}
      </Text>
    ));
  };

  return (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.header}>
          <Image style={styles.logo} src={businessInfo.logo} />
          <View style={styles.businessInfo}>
            <Text>{businessInfo.name}</Text>
            <Text>{businessInfo.address}</Text>
            <Text>{businessInfo.phone}</Text>
            <Text>{businessInfo.email}</Text>
            <Text>{businessInfo.website}</Text>
          </View>
        </View>
        <Text style={styles.title}>Quote #{quote._id}</Text>
        <View style={styles.section}>
          <Text style={styles.subheader}>Customer Information</Text>
          {renderCustomerInfo()}
        </View>
        <View style={styles.section}>
          <Text style={styles.subheader}>Service Details</Text>
          <Text style={styles.text}>
            <Text style={styles.bold}>Service Type:</Text> {type.replace('_', ' ')}
          </Text>
          <Text style={styles.text}>
            <Text style={styles.bold}>Area:</Text> {quote.area} sq ft
          </Text>
          <Text style={styles.subheader}>Options</Text>
          {renderOptions()}
        </View>
        <View style={styles.section}>
          <Text style={styles.subheader}>Pricing</Text>
          <View style={styles.table}>
            <View style={styles.tableRow}>
              <View style={styles.tableCol}><Text style={styles.tableCell}>Description</Text></View>
              <View style={styles.tableCol}><Text style={styles.tableCell}>Qty</Text></View>
              <View style={styles.tableCol}><Text style={styles.tableCell}>Unit Price</Text></View>
              <View style={styles.tableCol}><Text style={styles.tableCell}>Total</Text></View>
            </View>
            <View style={styles.tableRow}>
              <View style={styles.tableCol}><Text style={styles.tableCell}>{type.replace('_', ' ')} Service</Text></View>
              <View style={styles.tableCol}><Text style={styles.tableCell}>1</Text></View>
              <View style={styles.tableCol}><Text style={styles.tableCell}>${quote.basePrice}</Text></View>
              <View style={styles.tableCol}><Text style={styles.tableCell}>${quote.basePrice}</Text></View>
            </View>
            {quote.additionalPrice > 0 && (
              <View style={styles.tableRow}>
                <View style={styles.tableCol}><Text style={styles.tableCell}>Additional Services</Text></View>
                <View style={styles.tableCol}><Text style={styles.tableCell}>1</Text></View>
                <View style={styles.tableCol}><Text style={styles.tableCell}>${quote.additionalPrice}</Text></View>
                <View style={styles.tableCol}><Text style={styles.tableCell}>${quote.additionalPrice}</Text></View>
              </View>
            )}
          </View>
          <View style={[styles.row, { marginTop: 10 }]}>
            <Text style={styles.bold}>Total Price:</Text>
            <Text style={styles.bold}>${quote.totalPrice}</Text>
          </View>
        </View>
        <Text style={[styles.text, { marginTop: 20 }]}>
          This quote is valid for 30 days from the date of issue.
        </Text>
      </Page>
    </Document>
  );
};

export const QuoteDownloadLink = ({ quote, customerInfo, type, options, businessInfo }) => {
  console.log('QuoteDownloadLink props:', { quote, customerInfo, type, options, businessInfo });

  if (!quote || !customerInfo || !type || !options || !businessInfo) {
    console.error('Missing required props for QuoteDownloadLink');
    return null;
  }

  return (
    <PDFDownloadLink
      document={<QuotePDF quote={quote} customerInfo={customerInfo} type={type} options={options} businessInfo={businessInfo} />}
      fileName="quote.pdf"
      className="bg-indigo-600 text-white px-4 py-2 rounded mt-4 inline-block hover:bg-indigo-500"
    >
      {({ blob, url, loading, error }) => {
        if (loading) return 'Generating document...';
        if (error) {
          console.error('Error generating PDF:', error);
          return 'Error generating PDF';
        }
        return 'Download PDF';
      }}
    </PDFDownloadLink>
  );
};

export default QuotePDF;
