import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import { fetchJobs, fetchCalendarEvents, updateCalendarEvent } from './api';

const CalendarView = ({ routes }) => {
  const [jobs, setJobs] = useState([]);
  const [calendarEvents, setCalendarEvents] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [jobsData, eventsData] = await Promise.all([fetchJobs(), fetchCalendarEvents()]);
      setJobs(jobsData);
      setCalendarEvents(eventsData);
    } catch (error) {
      console.error('Error loading calendar data:', error);
    }
  };

  const onEventDrop = async (info) => {
    const { event } = info;
    try {
      const updatedEvent = await updateCalendarEvent({
        id: event.id,
        start: event.start,
        end: event.end,
      });
      setCalendarEvents(prevEvents => 
        prevEvents.map(e => e.id === updatedEvent.id ? updatedEvent : e)
      );
    } catch (error) {
      console.error('Error updating event:', error);
      info.revert();
    }
  };

  return (
    <div className="calendar-container grid grid-cols-4 gap-4">
      <div className="col-span-3">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          editable={true}
          droppable={true}
          events={calendarEvents}
          eventDrop={onEventDrop}
        />
      </div>
      <div className="job-pool">
        <h3 className="text-lg font-semibold mb-2">Job Pool</h3>
        <Droppable droppableId="jobPool">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef}>
              {jobs.map((job, index) => (
                <Draggable key={job.id} draggableId={job.id} index={index}>
                  {(provided) => (
                    <li
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="p-2 bg-white shadow rounded mb-2"
                    >
                      {job.title}
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </div>
    </div>
  );
};

export default CalendarView;
