// frontend/src/pages/Dashboard.js

import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext'; // Custom Auth Context
import { Link, useNavigate } from 'react-router-dom';
import {
  PlusIcon,
  UsersIcon,
  MapIcon,
  DocumentIcon,
  ArrowPathIcon,
  EyeIcon,
  EyeSlashIcon,
} from '@heroicons/react/24/outline';
import Header from '../components/Header'; // Custom Header Component
import TrialTimer from '../components/TrialTimer'; // Custom Trial Timer Component
import OverviewCard from '../components/OverviewCard'; // Custom Overview Card Component
import QuickActionButton from '../components/QuickActionButton'; // Custom Quick Action Button Component
import {
  fetchJobs,
  fetchCustomers,
  fetchEmployees,
  fetchAnalytics,
  fetchAnalyticsCounts,
} from '../api/dashboardApi'; // API Functions

// Import Chart Components
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import GaugeChart from '../components/GaugeChart'; // Custom Gauge Chart Component

// Import Chart.js Modules
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler,
} from 'chart.js';
import 'chartjs-adapter-date-fns';

// Register Chart.js Modules
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler
);

const Dashboard = () => {
  const { user } = useAuth(); // Access user from Auth Context
  const navigate = useNavigate(); // For navigation

  // State Variables
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [employeeCount, setEmployeeCount] = useState(0);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [globalTimeRange, setGlobalTimeRange] = useState('30'); // Default to past 30 days
  const [customDateRange, setCustomDateRange] = useState({ start: '', end: '' });
  const [showInsights, setShowInsights] = useState(true);

  // Fetch Dashboard Data on Component Mount and When Time Range Changes
  useEffect(() => {
    fetchDashboardData(globalTimeRange);
    const interval = setInterval(() => {
      fetchDashboardData(globalTimeRange);
    }, 600000); // Refresh every 10 minutes
    return () => clearInterval(interval);
  }, [globalTimeRange]);

  // Function to Fetch Dashboard Data
  const fetchDashboardData = async (timeRange, customRange = null) => {
    setLoading(true);
    setError(null);
    try {
      // Option 1: Use Separate Endpoints for Counts
      const [jobs, customers, employees, analytics] = await Promise.all([
        fetchJobs().catch((error) => {
          console.error('Error fetching jobs:', error);
          return [];
        }),
        fetchCustomers().catch((error) => {
          console.error('Error fetching customers:', error);
          return [];
        }),
        fetchEmployees().catch((error) => {
          console.error('Error fetching employees:', error);
          return [];
        }),
        fetchAnalytics(timeRange, customRange).catch((error) => {
          console.error('Error fetching analytics:', error);
          return {};
        }),
      ]);

      // Set Counts
      setJobCount(jobs?.length || 0);
      setCustomerCount(customers?.length || 0);
      setEmployeeCount(employees?.length || 0);

      // Set Analytics Data
      setAnalyticsData({
        ...analytics,
        revenue: analytics.revenue || { labels: [], data: [] },
        customerGrowth: analytics.customerGrowth || { labels: [], data: [] },
        jobStatusDistribution: analytics.jobStatusDistribution || { labels: [], data: [] },
        topPerformingEmployees: analytics.topPerformingEmployees || { labels: [], data: [] },
        customerRetention: analytics.customerRetention || { labels: [], data: [] },
        jobCancellationRate: analytics.jobCancellationRate || { labels: [], data: [] },
        jobFrequencyByCustomer: analytics.jobFrequencyByCustomer || { labels: [], data: [] },
        customerSatisfaction: analytics.customerSatisfaction || { labels: [], data: [], averageRating: 'N/A' },
        topServices: analytics.topServices || { labels: [], data: [] },
        employeeDistribution: analytics.employeeDistribution || { labels: [], data: [] },
        customerLifetimeValue: analytics.customerLifetimeValue || { labels: [], data: [] },
        averageLifetimeValue: analytics.averageLifetimeValue || { labels: [], data: [] },
        jobCompletionRate: analytics.jobCompletionRate || { labels: [], data: [] },
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error.response?.data || error.message);
      if (error.response && error.response.status === 403) {
        // Redirect to Trial Ended page if Subscription is Expired
        navigate('/trial-ended');
      } else {
        setError('Failed to load dashboard data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle Custom Date Range Input Changes
  const handleCustomDateChange = (e) => {
    setCustomDateRange({ ...customDateRange, [e.target.name]: e.target.value });
  };

  // Apply Custom Date Range
  const applyCustomDateRange = () => {
    if (customDateRange.start && customDateRange.end) {
      fetchDashboardData('custom', customDateRange);
    }
  };

  // Render Dashboard Content (Charts, Insights)
  const renderDashboardContent = () => {
    return (
      <>
        {/* Revenue Over Time Chart */}
        <ChartContainer title="Revenue Over Time">
          <Line
            data={{
              labels: analyticsData.revenue.map((item) => item._id),
              datasets: [
                {
                  label: 'Total Revenue ($)',
                  data: analyticsData.revenue.map((item) => item.totalRevenue),
                  fill: true,
                  backgroundColor: 'rgba(75, 192, 192, 0.2)',
                  borderColor: 'rgba(75, 192, 192, 1)',
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { position: 'top' },
                title: { display: false },
              },
              scales: {
                x: { title: { display: true, text: 'Date' } },
                y: { title: { display: true, text: 'Revenue ($)' }, beginAtZero: true },
              },
            }}
          />
        </ChartContainer>

        {/* Customer Growth Chart */}
        <ChartContainer title="Customer Growth">
          <Bar
            data={{
              labels: analyticsData.customerGrowth.map((item) => item._id),
              datasets: [
                {
                  label: 'New Customers',
                  data: analyticsData.customerGrowth.map((item) => item.newCustomers),
                  backgroundColor: 'rgba(153, 102, 255, 0.6)',
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { display: false },
                title: { display: false },
              },
              scales: {
                x: { title: { display: true, text: 'Date' } },
                y: { title: { display: true, text: 'Number of Customers' }, beginAtZero: true },
              },
            }}
          />
        </ChartContainer>

        {/* Job Status Distribution Chart */}
        <ChartContainer title="Job Status Distribution">
          <Doughnut
            data={{
              labels: analyticsData.jobStatusDistribution.map((item) => item._id),
              datasets: [
                {
                  label: 'Job Status',
                  data: analyticsData.jobStatusDistribution.map((item) => item.count),
                  backgroundColor: [
                    'rgba(54, 162, 235, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(255, 99, 132, 0.6)',
                  ],
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { position: 'right' },
                title: { display: false },
              },
            }}
          />
        </ChartContainer>

        {/* Top Performing Employees Chart */}
        <ChartContainer title="Top Performing Employees">
          <Bar
            data={{
              labels: analyticsData.topPerformingEmployees.map((item) => item._id),
              datasets: [
                {
                  label: 'Jobs Completed',
                  data: analyticsData.topPerformingEmployees.map((item) => item.count),
                  backgroundColor: 'rgba(255, 159, 64, 0.6)',
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { display: false },
                title: { display: false },
              },
              scales: {
                x: { title: { display: true, text: 'Employee' } },
                y: { title: { display: true, text: 'Jobs Completed' }, beginAtZero: true },
              },
            }}
          />
        </ChartContainer>

        {/* Additional Charts and Insights can be added similarly */}
      </>
    );
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header Component */}
      <Header />

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {/* Trial Timer and Upgrade Button Section */}
        {user?.subscriptionTier === 'Free' && (
          <div className="flex flex-col sm:flex-row items-center justify-between mb-6">
            <TrialTimer trialEndDate={user.trialEndDate} />
            <Link
              to="/pricing"
              className="mt-4 sm:mt-0 bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600"
            >
              Upgrade Now
            </Link>
          </div>
        )}

        {/* Welcome Message and Time Range Selector */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user?.name || 'User'}</h1>
          <div className="flex items-center">
            <select
              value={globalTimeRange}
              onChange={(e) => setGlobalTimeRange(e.target.value)}
              className="mr-2 p-2 border rounded text-black"
            >
              <option value="7">Past 7 days</option>
              <option value="30">Past 30 days</option>
              <option value="90">Past 90 days</option>
              <option value="all">All time</option>
              <option value="custom">Custom range</option>
            </select>
            {globalTimeRange === 'custom' && (
              <>
                <input
                  type="date"
                  name="start"
                  value={customDateRange.start}
                  onChange={handleCustomDateChange}
                  className="mr-2 p-2 border rounded"
                />
                <input
                  type="date"
                  name="end"
                  value={customDateRange.end}
                  onChange={handleCustomDateChange}
                  className="mr-2 p-2 border rounded"
                />
                <button
                  onClick={applyCustomDateRange}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600"
                >
                  Apply
                </button>
              </>
            )}
            <button
              onClick={() => fetchDashboardData(globalTimeRange, globalTimeRange === 'custom' ? customDateRange : null)}
              className="ml-4 bg-green-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-green-600 flex items-center"
            >
              <ArrowPathIcon className="h-5 w-5 mr-2" />
              Refresh Dashboard
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6" role="alert">
            <p>{error}</p>
          </div>
        )}

        {/* Overview Cards */}
        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 mb-8">
          <OverviewCard title="Jobs" count={jobCount} link="/manage-jobs" />
          <OverviewCard title="Customers" count={customerCount} link="/manage-customers" />
          <OverviewCard title="Employees" count={employeeCount} link="/manage-employees" />
        </div>

        {/* Quick Actions */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" />
          </div>
        </div>

        {/* Dashboard Insights Toggle */}
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">Dashboard Insights</h2>
          <button onClick={() => setShowInsights(!showInsights)} className="text-white hover:text-gray-400">
            {showInsights ? <EyeSlashIcon className="h-6 w-6" /> : <EyeIcon className="h-6 w-6" />}
          </button>
        </div>

        {/* Dashboard Insights Content */}
        {showInsights && (
          <>
            {loading ? (
              <div className="text-center py-12">Loading dashboard data...</div>
            ) : analyticsData ? (
              renderDashboardContent()
            ) : (
              <div className="text-center py-12">
                No data available. Please try refreshing the dashboard.
                {user?.subscriptionTier === 'Free' && (
                  <div className="mt-4">
                    <p>Upgrade to access full analytics</p>
                    <Link
                      to="/pricing"
                      className="bg-yellow-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-yellow-600"
                    >
                      Upgrade Now
                    </Link>
                  </div>
                )}
              </div>
            )}
          </>
        )}
      </main>
    </div>
  );
};

// ChartContainer Component for Consistent Styling
const ChartContainer = ({ title, children }) => (
  <div className="bg-gray-800 p-4 rounded-lg shadow mb-8">
    <h3 className="text-lg leading-6 font-medium mb-4 text-white">{title}</h3>
    <div className="h-64">{children}</div>
  </div>
);

export default Dashboard;
